/*Table structure for table `mp_article` */

DROP TABLE IF EXISTS `mp_article`;

CREATE TABLE `mp_article` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `title` varchar(512) DEFAULT NULL COMMENT '标题',
  `thumb` varchar(1024) DEFAULT NULL COMMENT '缩略图',
  `category_id` int(11) DEFAULT NULL COMMENT '关联图文信息分类表',
  `content` text COMMENT '图文信息内容',
  `create_time` int(11) DEFAULT NULL,
  `read_count` int(11) DEFAULT NULL COMMENT '阅读量',
  `like_count` int(11) DEFAULT NULL COMMENT '点赞数量',
  `come_from` varchar(255) DEFAULT NULL COMMENT '来源',
  `author` varchar(255) DEFAULT NULL COMMENT '作者',
  `user_id` bigint(20) DEFAULT NULL COMMENT '关联系统用户表',
  `status` int(11) DEFAULT '1' COMMENT '0、未审核 1、正常 2、审核不通过',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=152 DEFAULT CHARSET=utf8 COMMENT='图文信息表';

/*Data for the table `mp_article` */

insert  into `mp_article`(`id`,`title`,`thumb`,`category_id`,`content`,`create_time`,`read_count`,`like_count`,`come_from`,`author`,`user_id`,`status`) values 
(114,'交通路线','',83,'<p><span style=\"line-height: 17.1428px;\"><b>公交：</b></span></p><p><span style=\"line-height: 17.1428px;\">17,5,210,223,256,88,89，乘车到终点站下车，步行300米即到，乘车以下车，步行200米即到。</span></p><hr><p><span style=\"line-height: 17.1428px;\"><b>地铁一号线：</b></span></p><p><span style=\"line-height: 17.1428px;\">地铁一号线运营时间：8：00-22:00</span></p><hr><p><span style=\"line-height: 17.1428px;\"><b>地铁二号线：</b></span></p><p><span style=\"line-height: 17.1428px;\">地铁二号线运行时间：6:00-21:00</span></p><hr><p><span style=\"line-height: 17.1428px;\"><b>自驾车：</b></span></p><p><span style=\"line-height: 17.1428px;\">1.广深高速 &nbsp;--北环大道 --香山路 --南京中环街 --侨城西路 --深圳华侨城</span></p><p><span style=\"line-height: 17.1428px;\">2.和有收费站 --水官高速 --南坪快速 --香山路 --深圳华侨城</span></p>',1495768487,122,14,'深圳东部华侨城','深圳东部华侨城',1,1),
(120,'关于东部华侨城','',125,'<section style=\"color: rgb(51, 51, 51); font-family: arial, \'pingfang sc\', stheiti, simsun, sans-serif; font-size: 12px; line-height: 18px;\"><div class=\"section_title\" style=\"margin-top: 29px; margin-bottom: 15px; height: 38px; clear: both; background: url(&quot;http://cache.soso.com/baike/i/lemma/sgbk_lemma_shadow_sprite.png&quot;) 0px -21px no-repeat;\"><h2 style=\"margin-top: 0px; margin-right: 10px; margin-bottom: 0px; font-size: 22px; font-family: arial, \'pingfang sc\', stheiti, \'microsoft yahei\', sans-serif; float: left; line-height: 36px; color: rgb(116, 134, 198); white-space: nowrap; zoom: 1;\"><span style=\"font-size: 16px; float: left; display: block; width: 24px; height: 24px; margin-top: 6px; margin-right: 10px; line-height: 24px; color: rgb(255, 255, 255); text-align: center; background-color: rgb(130, 142, 201);\">1</span><a name=\"1\" id=\"1\" style=\"color: rgb(116, 134, 198);\"></a><a name=\"para1\" id=\"para1\" style=\"color: rgb(116, 134, 198);\">景点介绍</a></h2></div><div class=\"section_content\" style=\"line-height: 25px; font-size: 14px; clear: both;\"><div class=\"rich_text_area\" style=\"margin-bottom: 10px; font-size: 14px; word-wrap: break-word; zoom: 1;\"><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城，坐落于中国广东省深圳市大梅沙，占地近9平方公里，由华侨城集团斥资35亿元精心打造，是国内首个集休闲度假、观光旅游、户外运动、科普教育、生态探险等主题于一体的大型综合性国家生态旅游示范区，主要包括大侠谷生态公园、茶溪谷休闲公园、云海谷体育公园、大华兴寺、主题酒店群落、天麓大宅等六大板块，体现了人与自然的和谐共处。华侨城以\"规划科学合理，功能配套齐全，城区环境优美，风尚高尚文明，管理规范先进\"为规划，以“让都市人回归自然”为宗旨，定位于建设成为集生态旅游、娱乐休闲、郊野度假、户外运动等多个主题于一体的综合性都市型山地主题休闲度假区。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><span class=\"text_img ed_imgfloat_right\" style=\"border: 1px solid rgb(226, 226, 226); color: rgb(102, 102, 102); display: inline; font-size: 12px; padding: 2px; text-align: center; text-indent: 0px; box-shadow: rgba(150, 150, 150, 0.2) 0px 0px 2px; position: relative; float: right; margin-left: 10px; clear: right; margin-bottom: 10px; width: 300px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><a class=\"ed_image_link\" title=\"点击查看大图\" href=\"http://baike.sogou.com/PicBooklet.v?relateImageGroupIds=&amp;lemmaId=25078&amp;now=http%3A%2F%2Fpic.baike.soso.com%2Fp%2F20130626%2F20130626094139-1333521457.jpg&amp;type=1\" target=\"_blank\" style=\"position: relative; display: block; color: rgb(51, 102, 204);\"><img title=\"深圳东部华侨城\" alt=\"\" src=\"http://pic.baike.soso.com/p/20130626/bki-20130626094139-1333521457.jpg\" width=\"300\" height=\"199\" style=\"display: inline-block; vertical-align: top;\"></a><span style=\"display: block; line-height: 20px; padding: 6px 10px;\">深圳东部华侨城</span></span>从2004年12月30日动工建设至2007年7月28日项目一期开放，在短短938天的时间里，东部华侨城在山海间创新打造了包含生态景区、主题酒店、健康水疗、郊野球场、大型演艺等内容的一系列生态旅游精品，并已成为粤港澳地区一张流光溢彩的城市名片。 　2004 年12 月30 日东部华侨城动工建设，2007 年7 月28 日项目一期开放，截至2008 年底，项目一期接待游客达300 万人次，实现税收5.08 亿元，各项指标均创全国旅游景区之先河，已成为粤港澳地区一张流光溢彩的城市名片。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城在山海间巧妙规划了大侠谷、茶溪谷、云海谷三大主题区域，集生态动感、休闲度假、户外运动等多项文化旅游功能于一体，体现了人与自然的和谐共处。东部华侨城一期已于2007年7月28日隆重试业，体现中西文化交融并兼有“茶、禅、花、竹”等主要元素的茶溪谷、以奥林匹克军体运动和高尔夫为主的云海谷以及茵特拉根酒店、矿泉SPA、天禅晚会、首届国际山地歌会等同时精彩亮相。</p></div></div></section><section style=\"color: rgb(51, 51, 51); font-family: arial, \'pingfang sc\', stheiti, simsun, sans-serif; font-size: 12px; line-height: 18px;\"><div class=\"section_title\" style=\"margin-top: 29px; margin-bottom: 15px; height: 38px; clear: both; background: url(&quot;http://cache.soso.com/baike/i/lemma/sgbk_lemma_shadow_sprite.png&quot;) 0px -21px no-repeat;\"><h2 style=\"margin-top: 0px; margin-right: 10px; margin-bottom: 0px; font-size: 22px; font-family: arial, \'pingfang sc\', stheiti, \'microsoft yahei\', sans-serif; float: left; line-height: 36px; color: rgb(116, 134, 198); white-space: nowrap; zoom: 1;\"><span style=\"font-size: 16px; float: left; display: block; width: 24px; height: 24px; margin-top: 6px; margin-right: 10px; line-height: 24px; color: rgb(255, 255, 255); text-align: center; background-color: rgb(130, 142, 201);\">2</span><a name=\"2\" id=\"2\" style=\"color: rgb(116, 134, 198);\"></a><a name=\"para2\" id=\"para2\" style=\"color: rgb(116, 134, 198);\">特色景观</a></h2></div><div class=\"section_content\" style=\"line-height: 25px; font-size: 14px; clear: both;\"><div class=\"rich_text_area\" style=\"margin-bottom: 10px; font-size: 14px; word-wrap: break-word; zoom: 1;\"><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城位于中国深圳大梅沙，占地近9平方公里，是由华侨城集团斥资35亿元精心打造的世界级度假旅游目的地，是由国家环境保护部和国家旅游局联合授予的首个 “国家生态旅游示范区”。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><span class=\"text_img ed_imgfloat_left\" style=\"border: 1px solid rgb(226, 226, 226); color: rgb(102, 102, 102); display: inline; font-size: 12px; padding: 2px; text-align: center; text-indent: 0px; box-shadow: rgba(150, 150, 150, 0.2) 0px 0px 2px; float: left; margin-right: 10px; clear: left; margin-bottom: 10px; width: 299px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><a class=\"ed_image_link\" title=\"点击查看大图\" href=\"http://baike.sogou.com/PicBooklet.v?relateImageGroupIds=&amp;lemmaId=25078&amp;now=http%3A%2F%2Fpic.baike.soso.com%2Fp%2F20130813%2F20130813093059-1920246958.jpg&amp;type=1\" target=\"_blank\" style=\"position: relative; display: block; color: rgb(51, 102, 204);\"><img title=\"深圳东部华侨城\" alt=\"\" src=\"http://pic.baike.soso.com/p/20130813/20130813093059-1920246958.jpg\" width=\"299\" height=\"300\" style=\"display: inline-block; vertical-align: top;\"></a><span style=\"display: block; line-height: 20px; padding: 6px 10px;\">深圳东部华侨城</span></span>东部华侨城集两个主题公园、三座旅游小镇、四家度假酒店、两座36洞山地球场、大华兴寺和天麓地产等项目于一体。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">主要包括：大侠谷生态乐园、茶溪谷度假公园、茵特拉根小镇、海菲德小镇、茶翁古镇、大华兴寺、茵特拉根酒店、东部华侨城瀑布酒店、东部华侨城房车酒店、大华兴寺菩提宾舍、火车营地、咖酷旅馆、云海谷高尔夫会员球场和公众球场、天麓地产等，体现人与自然的和谐共处。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城还精心打造了《天禅》、《天机》、《天音》、《天籁》等多台文化演艺项目，每年的春夏秋冬，还会分别推出“山地采茶节”、“山海放歌节”、“国际茶艺节”、“山地祈福节”等主题活动，深圳田维为广大游客带来无限精彩。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城认真履行中央企业社会责任，规划营造了一个全方位自然体验的生态之旅，同时以寓教于乐的方式向游客推广环保知识，启发游客热爱、保护大自然的意识，实现了生态、经济、社会效益的和谐统一。东部华侨城自2007年7月28日项目一期试业以来，先后赢得由社会各界有影响力的组织授予的20多项荣誉，截至08年底，项目一期已接待游客达300万人次，实现税收5.0亿元，各项指标均创全国旅游景区之先河，提供了3000多个就业岗位。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">两个主题公园</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><span class=\"text_img ed_imgfloat_right\" style=\"border: 1px solid rgb(226, 226, 226); color: rgb(102, 102, 102); display: inline; font-size: 12px; padding: 2px; text-align: center; text-indent: 0px; box-shadow: rgba(150, 150, 150, 0.2) 0px 0px 2px; position: relative; float: right; margin-left: 10px; clear: right; margin-bottom: 10px; width: 220px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><a class=\"ed_image_link\" title=\"点击查看大图\" href=\"http://baike.sogou.com/PicBooklet.v?relateImageGroupIds=&amp;lemmaId=25078&amp;now=http%3A%2F%2Fpic.baike.soso.com%2Fp%2F20130902%2F20130902095406-1626519647.jpg&amp;type=1\" target=\"_blank\" style=\"position: relative; display: block; color: rgb(51, 102, 204);\"><img title=\"大侠谷\" alt=\"\" src=\"http://pic.baike.soso.com/ugc/baikepic/12299/cut-20130902095448-846883784.jpg/0\" width=\"220\" height=\"165\" style=\"display: inline-block; vertical-align: top;\"></a><span style=\"display: block; line-height: 20px; padding: 6px 10px;\">大侠谷</span></span>大侠谷生态乐园以“人与自然”为主题，集山地郊野公园和都市主题公园于一体，实现了自然景观、生态理念与娱乐体验、科普教育的创新结合，主要包括水公园、峡湾森林、海菲德小镇、生态峡谷和云海高地等五大主题区，自然奇幻的主题乐园为游客带来不一样的欢乐体验。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">茶溪谷度假公园呈现了一个绿的世界、花的世界、中西文化交融的世界和休闲度假的世界，体现休闲度假旅游文化，主要包括：茵特拉根、湿地花园、三洲茶园、茶翁古镇以及水上高尔夫练习场、屋顶可开合式网球馆、东部华侨城大剧院等。近期，茶溪谷将建造一个滑雪场，为游客打造一个南国浪漫雪世界。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">三座旅游小镇</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">茵特拉根小镇撷取欧洲瑞士阿尔卑斯山麓茵特拉根的建筑、</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">赛马特的花卉、谢菲尔德的彩绘等多种题材和元素，实现了中欧山地建筑风格与优美自然景观的完美结合，温馨的主题街区、古老的森林小火车、典雅的度假酒店、茵特拉根温泉、东部华侨城大剧院、水上高尔夫、网球馆等，在山谷间创造出一个美得像童话世界的山地小镇。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">海菲德小镇以葡萄酒文化为主题的美洲风情小镇，原木与砖石相结合的建筑温馨质朴，系列铜雕展示了从葡萄采摘到红酒酿造的全过程，演绎了19世纪美国加州纳帕山谷的红酒小镇风情。主要包括天幕、海布伦宫、红酒体验馆、自酿啤酒屋、湖畔美食廊、小镇客栈等特色项目，为游客精心打造了一处与红酒约会的陶醉之乡。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">茶翁古镇是茶文化的鉴赏区和中心服务区，也是环境幽雅的游客休憩区。游客可以品茶餐、尝茶点、吃土菜、观茶戏、饮茶酒，深入了解茶禅文化；还可以在茶艺坊、茶酒坊、陶艺坊亲身体验采茶、制茶、做陶的乐趣。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">四家主题酒店</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城酒店群由四大主题酒店组成，是国内首个主题酒店群，拥有 600余间客房。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">茵特拉根酒店将中欧山地建筑风格与南中国壮美的山海景观完美结合，出落成一个美得不食人间烟火的童话。包括 308间豪华客房、池畔别墅、连排别墅、商业街公寓以及总统堡，在优雅，尊贵中为你一一呈现山海的浪漫。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">大华兴寺菩提宾舍将“禅宗”理念纳入酒店的设计构思，创造空灵、简朴的禅意空间。22间禅意客房：包括 4间禅意套房，16间静谧大床房，2间典雅双床房。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城房车酒店位于 2.5万平方公里郊野体育公园，收尽东部华侨城独有的湖光山色、云海奇观。她将概念客房与豪华房车，静与动完美结合，最好的诠释了“自由”或“肆意”。 拥有159间写意客房及4辆豪华房车。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城瀑布酒店隐藏在大瀑布和巨岩之间。 94间客房、啤酒工厂、主题餐厅、宴会演绎厅……设计师以水为元素进行创作，艺术与奇思妙想渗入每一个角落。客人可将壮观的大侠谷瀑布收入眼帘，也可沉醉于红酒小镇，集太空，海洋于一体的主题公园为这里增添了无尽的梦幻色彩。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">两座36洞山地球场</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">云海谷体育公园包括18洞的云海谷公众球场和18洞的云海谷会员球场，以休闲健身、生态探险、时尚运动、休闲娱乐、奥运军体运动为主线，体现户外运动旅游文化。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">主题地产</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><span class=\"text_img ed_imgfloat_right\" style=\"border: 1px solid rgb(226, 226, 226); color: rgb(102, 102, 102); display: inline; font-size: 12px; padding: 2px; text-align: center; text-indent: 0px; box-shadow: rgba(150, 150, 150, 0.2) 0px 0px 2px; position: relative; float: right; margin-left: 10px; clear: right; margin-bottom: 10px; width: 300px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><a class=\"ed_image_link\" title=\"点击查看大图\" href=\"http://baike.sogou.com/PicBooklet.v?relateImageGroupIds=&amp;lemmaId=25078&amp;now=http%3A%2F%2Fpic.baike.soso.com%2Fp%2F20130626%2F20130626094152-454976772.jpg&amp;type=1\" target=\"_blank\" style=\"position: relative; display: block; color: rgb(51, 102, 204);\"><img title=\"深圳东部华侨城\" alt=\"\" src=\"http://pic.baike.soso.com/p/20130626/bki-20130626094152-454976772.jpg\" width=\"300\" height=\"225\" style=\"display: inline-block; vertical-align: top;\"></a><span style=\"display: block; line-height: 20px; padding: 6px 10px;\">深圳东部华侨城</span></span>天麓大宅依托于东部华侨城9平方公里壮阔的山海，在景区里建住宅，在住宅里造景点，实现了优质的人居生活环境。2008年，荣膺联合国\"全球人居环境最佳社区\"。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">茵特拉根·天麓街区：7月中旬，天麓大宅新品——茵特拉根·天麓街区将隆重开盘，她拥有独特的湖山自然景观，延续茵特拉根的北欧山地建筑风格，充分发挥临湖而居的特色和优势，融建筑于自然山体之中，体现了人与自然的和谐相处。日前，茵特拉根·天麓街区VIP尊享卡正在火热申请中。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">主题佛教园区</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">大华兴寺坐落在东部华侨城的观音座莲山，体现佛教旅游文化，拥有目前世界上唯一一座集四尊不同观音像为一体的户外贴金佛像；国内首家佛文化主题酒店——大华兴寺菩提宾舍；国内首台以佛文化为主题的多媒体编钟诗乐《天音》等精品。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">四台主题演艺</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">《天禅》是一部融合了多种艺术手段，以禅茶文化为主题的大型多媒体交响音画晚会，其多维表演空间组合成令人震撼的视听效果和精美绝伦的艺术画面。晚会长达 60分钟，高潮迭起，让人仿佛走进天人合一、梦幻神秘的天禅之境。每天在东部华侨城大剧院上演</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">《天机》是一部以生命起源、人与自然相互依存为故事主线的海陆空跨界多媒体奇幻水秀。水孕育了生命与人类，人类不断地发展进入了工业革命，随着对大自然无限制的索取，地球的原生态环境也随之遭到破坏和污染，人类要继续发展，就必须保护好大自然。该晚会唤起大家生态环保、珍爱生命的意识，将于2009年暑期隆重推出。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">《天音》使用精仿战国时代出土文物曾侯乙编钟作为主演奏乐器，以《观音赞》作为主音乐线索，在26分钟的时间里为广大游客演绎了一场规模宏大、气势恢弘的佛教历史文化视听盛宴。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">《天籁》描绘出一幅神秘而灵秀的原生态风景画卷，原生态歌手质朴的歌声，满山遍野茶叶带来的盎然绿意，明丽动人的采茶女，以交相辉映的形式勾勒出勃勃生机的大自然。</p></div></div></section><section style=\"color: rgb(51, 51, 51); font-family: arial, \'pingfang sc\', stheiti, simsun, sans-serif; font-size: 12px; line-height: 18px;\"><div class=\"section_title\" style=\"margin-top: 29px; margin-bottom: 15px; height: 38px; clear: both; background: url(&quot;http://cache.soso.com/baike/i/lemma/sgbk_lemma_shadow_sprite.png&quot;) 0px -21px no-repeat;\"><h2 style=\"margin-top: 0px; margin-right: 10px; margin-bottom: 0px; font-size: 22px; font-family: arial, \'pingfang sc\', stheiti, \'microsoft yahei\', sans-serif; float: left; line-height: 36px; color: rgb(116, 134, 198); white-space: nowrap; zoom: 1;\"><span style=\"font-size: 16px; float: left; display: block; width: 24px; height: 24px; margin-top: 6px; margin-right: 10px; line-height: 24px; color: rgb(255, 255, 255); text-align: center; background-color: rgb(130, 142, 201);\">3</span><a name=\"3\" id=\"3\" style=\"color: rgb(116, 134, 198);\"></a><a name=\"para3\" id=\"para3\" style=\"color: rgb(116, 134, 198);\">游览指引</a></h2></div><div class=\"section_content\" style=\"line-height: 25px; font-size: 14px; clear: both;\"><div class=\"rich_text_area\" style=\"margin-bottom: 10px; font-size: 14px; word-wrap: break-word; zoom: 1;\"></div></div></section><section style=\"color: rgb(51, 51, 51); font-family: arial, \'pingfang sc\', stheiti, simsun, sans-serif; font-size: 12px; line-height: 18px;\"><div class=\"section_content\" style=\"line-height: 25px; font-size: 14px; clear: both;\"><div class=\"rich_text_area\" style=\"margin-bottom: 10px; font-size: 14px; word-wrap: break-word; zoom: 1;\"><a name=\"para4\" id=\"para4\"></a><h3 style=\"margin-top: 25px; margin-bottom: 15px; font-size: 20px; font-family: arial, \'pingfang sc\', stheiti, \'microsoft yahei\', sans-serif; line-height: 24px;\">交通指南</h3><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">内部交通</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城将传统与现代结合，东方与西方结合、旅游和文化演艺结合，创新性打造了一系列精彩纷呈、独特魅力的景观。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">丛林缆车：世界首创的双坡山地开放式观光车，全长约2公里，高差400米的丛 　林缆车将带来壮观的观景视角，往返于大侠谷与云中部落之间。<sup style=\"margin-right: 2px;\"><a href=\"http://baike.sogou.com/v25078.htm?fromTitle=%E4%B8%9C%E9%83%A8%E5%8D%8E%E4%BE%A8%E5%9F%8E#quote1\" style=\"color: rgb(51, 102, 204);\">[1]</a><a name=\"ref_1\" id=\"ref_1\"></a></sup></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><span class=\"text_img ed_imgfloat_right\" style=\"border: 1px solid rgb(226, 226, 226); color: rgb(102, 102, 102); display: inline; font-size: 12px; padding: 2px; text-align: center; text-indent: 0px; box-shadow: rgba(150, 150, 150, 0.2) 0px 0px 2px; position: relative; float: right; margin-left: 10px; clear: right; margin-bottom: 10px; width: 220px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><a class=\"ed_image_link\" title=\"点击查看大图\" href=\"http://baike.sogou.com/PicBooklet.v?relateImageGroupIds=&amp;lemmaId=25078&amp;now=http%3A%2F%2Fpic.baike.soso.com%2Fp%2F20130902%2F20130902095956-771973215.jpg&amp;type=1\" target=\"_blank\" style=\"position: relative; display: block; color: rgb(51, 102, 204);\"><img title=\"云海索道\" alt=\"\" src=\"http://pic.baike.soso.com/ugc/baikepic/12293/cut-20130902100016-1890390921.jpg/0\" width=\"220\" height=\"165\" style=\"display: inline-block; vertical-align: top;\"></a><span style=\"display: block; line-height: 20px; padding: 6px 10px;\">云海索道</span></span>云海索道 ：深圳第一条高空索道，全长1089米，乘坐云海索道，俯瞰梅沙湾畔的帆星点点，别有一番天地，往返于大侠谷与云中部落之间。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">森林小火车：融观光性，参与性和交通运输功能三位一体，轨道全长约6公里，先 　后经过峡谷、山林、湖泊、天堑，沿途山花烂漫，彩蝶纷飞，别有 　一番天地，往返于大拐弯火车站与茵特拉根火车站之间。<sup style=\"margin-right: 2px;\"><a href=\"http://baike.sogou.com/v25078.htm?fromTitle=%E4%B8%9C%E9%83%A8%E5%8D%8E%E4%BE%A8%E5%9F%8E#quote2\" style=\"color: rgb(51, 102, 204);\">[2]</a><a name=\"ref_2\" id=\"ref_2\"></a></sup></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">外部交通</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">深圳市内交通 ：游客可在市区搭乘J1、53、103、239、308、380或机场6号线前往东部华侨城站，或搭乘103B、360、364、387路公交车至大梅沙站，再步行前往大侠谷。到达大侠谷后，在停车场乘坐东部 　华侨城909专线直接前往茶溪谷。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">自驾车</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">1、深盐第二通道已经全面通车，游客从市中心区经深盐第二通道转，盐坝高速到“大梅沙”出口，前往东部华侨城大侠谷。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">2、自驾车游客也可从深盐第二通道“北山道”出口转盐梅路至“三洲田路口”，沿盐三公路享受15分钟山地自驾乐趣后到达茶溪谷。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">3、市内公交：</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">可以搭乘E12,E13,J1,53,观光线1或机场6线，前往东部华侨城大侠谷站。节假日搭乘东线假日快线1路、东部假日快线2路前来东部华侨城更是快速、便捷。<sup style=\"margin-right: 2px;\"><a href=\"http://baike.sogou.com/v25078.htm?fromTitle=%E4%B8%9C%E9%83%A8%E5%8D%8E%E4%BE%A8%E5%9F%8E#quote3\" style=\"color: rgb(51, 102, 204);\">[3]</a><a name=\"ref_3\" id=\"ref_3\"></a></sup></p></div></div></section><section style=\"color: rgb(51, 51, 51); font-family: arial, \'pingfang sc\', stheiti, simsun, sans-serif; font-size: 12px; line-height: 18px;\"><div class=\"section_content\" style=\"line-height: 25px; font-size: 14px; clear: both;\"><div class=\"rich_text_area\" style=\"margin-bottom: 10px; font-size: 14px; word-wrap: break-word; zoom: 1;\"><a name=\"para5\" id=\"para5\"></a><h3 style=\"margin-top: 25px; margin-bottom: 15px; font-size: 20px; font-family: arial, \'pingfang sc\', stheiti, \'microsoft yahei\', sans-serif; line-height: 24px;\">景区门票</h3><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">茶溪谷门票160元；大侠谷门票180元，两谷一日游260元，两谷两日游300元。另有温泉门票及联票。观看《天禅》演出需要另外购买门票。普通席位价格平日为￥50元起，节假日为￥80元起。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">售票处主要分设三处：大侠谷售票处主要出售门票缆车组合票； 茶溪谷售票处主要出售茶溪谷门票、门票缆车组合票；东部华侨城大剧院售票处主要出售《天禅》演出票。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">电子门票优惠请登陆旅游名店城查看.</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">东部华侨城门票说明：</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城景区将实行一票制，大侠谷、茶溪谷景区将不再单独售票，两园合并为一个园区，合并后的票价由原来的248元（大侠谷+茶溪谷景区联票）调整为特惠220元。<sup style=\"margin-right: 2px;\"><a href=\"http://baike.sogou.com/v25078.htm?fromTitle=%E4%B8%9C%E9%83%A8%E5%8D%8E%E4%BE%A8%E5%9F%8E#quote4\" style=\"color: rgb(51, 102, 204);\">[4]</a><a name=\"ref_4\" id=\"ref_4\"></a></sup></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">现在游客游玩只需“一票在手”即可玩转大侠谷探险乐园、茶溪谷度假公园及免费游玩大华兴寺三大园区，游玩变得更加方便快捷。</p></div></div></section><section style=\"color: rgb(51, 51, 51); font-family: arial, \'pingfang sc\', stheiti, simsun, sans-serif; font-size: 12px; line-height: 18px;\"><div class=\"section_content\" style=\"line-height: 25px; font-size: 14px; clear: both;\"><div class=\"rich_text_area\" style=\"margin-bottom: 10px; font-size: 14px; word-wrap: break-word; zoom: 1;\"><a name=\"para6\" id=\"para6\"></a><h3 style=\"margin-top: 25px; margin-bottom: 15px; font-size: 20px; font-family: arial, \'pingfang sc\', stheiti, \'microsoft yahei\', sans-serif; line-height: 24px;\">购票须知</h3><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><span class=\"text_img ed_imgfloat_right\" style=\"border: 1px solid rgb(226, 226, 226); color: rgb(102, 102, 102); display: inline; font-size: 12px; padding: 2px; text-align: center; text-indent: 0px; box-shadow: rgba(150, 150, 150, 0.2) 0px 0px 2px; position: relative; float: right; margin-left: 10px; clear: right; margin-bottom: 10px; width: 300px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><a class=\"ed_image_link\" title=\"点击查看大图\" href=\"http://baike.sogou.com/PicBooklet.v?relateImageGroupIds=&amp;lemmaId=25078&amp;now=http%3A%2F%2Fpic.baike.soso.com%2Fp%2F20130813%2F20130813093153-2084868746.jpg&amp;type=1\" target=\"_blank\" style=\"position: relative; display: block; color: rgb(51, 102, 204);\"><img title=\"深圳东部华侨城\" alt=\"\" src=\"http://pic.baike.soso.com/p/20130813/20130813093153-2084868746.jpg\" width=\"300\" height=\"283\" style=\"display: inline-block; vertical-align: top;\"></a><span style=\"display: block; line-height: 20px; padding: 6px 10px;\">深圳东部华侨城</span></span>购买200元的联票可前往华兴寺游览。购买150元的茶溪谷门票需在茶溪谷售票处补票50元才可以前往华兴寺游览和乘丛林缆车或者云海索道前往大侠谷。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">半票优惠：</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">1、身高1.2米（含1.2米）至1.5米的儿童；</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">2、65到69周岁（含65周岁）的老年人（凭本人有效身份证或护照）；</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">3、持旧版三等伤残证明的人士，持新版七至十级伤残证明的人士。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">免票优惠：</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">1、身高1.2米以下的儿童；</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">2、70周岁以上（含70周岁）的老年人（凭本人身份证、护照等有效证件）；</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">3、持旧版一、二等伤残证明的人士(其中一等证可免陪同一位，二等证陪同可半票入园一位)；持新版一至六级伤残证明的人士；（其中一至四级可免陪同一位，五至六级陪同可半票入园一位）；</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">4、持《深圳市敬老优待证》的老年人。</p></div></div></section><section style=\"color: rgb(51, 51, 51); font-family: arial, \'pingfang sc\', stheiti, simsun, sans-serif; font-size: 12px; line-height: 18px;\"><div class=\"section_content\" style=\"line-height: 25px; font-size: 14px; clear: both;\"><div class=\"rich_text_area\" style=\"margin-bottom: 10px; font-size: 14px; word-wrap: break-word; zoom: 1;\"><a name=\"para7\" id=\"para7\"></a><h3 style=\"margin-top: 25px; margin-bottom: 15px; font-size: 20px; font-family: arial, \'pingfang sc\', stheiti, \'microsoft yahei\', sans-serif; line-height: 24px;\">温馨提示</h3><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">安全是享受快乐旅程的保证。出发深圳东部华侨城前最好购买旅游意外保险，如果发生意外能得到及时的救助。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">深圳东部华侨城旅途中尽量少带现金，不要将钱放在行李中，要贴身保管。最好到正规商店购物，买了东西要发票证明。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">重要证件身份证、信用卡、机船车票要随身携带、妥善保管。出发深圳东部华侨城前最好各复印一件放在手提包中，原件放在贴身的内衣口袋中。遇到有人查证件时也不要轻易答应，应报告领队处理。如领队不在场，可要求对方出示身份证或工作证件，否则应予拒绝。若对方是警察，也应记下其证件号、胸牌号和车号。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">在深圳东部华侨城旅游过程中，游客应当保存好一切可能用得着的证明材料，如旅游合同、旅游发票、景点门票、医疗单据等，不要仅凭口头承诺。必要时，消费者可将与旅行社进行商谈交涉的过程以录音的形式记录下来，以备用。遇到侵权更要及时向旅行社、消费者协会、旅游质量质监所等机构反映。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">女性独自深圳东部华侨城旅游</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">对现代女性而言，独自旅行早已不是难事。但是如果未曾考量旅游安全问题，那就可能会在旅游时遇骗遭劫，轻则伤财，重则危及人身安全。女性旅游时应该注意哪些安全问题呢？</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">1、女性深圳东部华侨城旅行，少许化妆不但令你的旅程增添姿采，拍照留念的效果也特好，可以留下更美好的回忆。出外旅游最好以化淡妆为主。带一条或多条围巾，可以配衣服，令你“形象百变”，而且又轻又不占空间，可用来当头巾面纱；还可以包扎行李，一物多用。穿着简单自然，出外旅游可不是选美，切忌打扮花枝招展。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">2、已成家的女性在外旅游宜戴一只戒指，向陌生男子表示你已经结婚。此外，切勿饮用陌生人提供的饮料，或应承陌生人的邀约（即使对方都是女孩子）。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">3、深圳东部华侨城旅游留宿时，一定告知家人旅馆名称、电话、预定留宿时间。如果是团队游，要记下队友的房间电话。</p></div></div></section><section style=\"color: rgb(51, 51, 51); font-family: arial, \'pingfang sc\', stheiti, simsun, sans-serif; font-size: 12px; line-height: 18px;\"><div class=\"section_title\" style=\"margin-top: 29px; margin-bottom: 15px; height: 38px; clear: both; background: url(&quot;http://cache.soso.com/baike/i/lemma/sgbk_lemma_shadow_sprite.png&quot;) 0px -21px no-repeat;\"><h2 style=\"margin-top: 0px; margin-right: 10px; margin-bottom: 0px; font-size: 22px; font-family: arial, \'pingfang sc\', stheiti, \'microsoft yahei\', sans-serif; float: left; line-height: 36px; color: rgb(116, 134, 198); white-space: nowrap; zoom: 1;\"><span style=\"font-size: 16px; float: left; display: block; width: 24px; height: 24px; margin-top: 6px; margin-right: 10px; line-height: 24px; color: rgb(255, 255, 255); text-align: center; background-color: rgb(130, 142, 201);\">4</span><a name=\"8\" id=\"8\" style=\"color: rgb(116, 134, 198);\"></a><a name=\"para8\" id=\"para8\" style=\"color: rgb(116, 134, 198);\">景区板块</a></h2></div><div class=\"section_content\" style=\"line-height: 25px; font-size: 14px; clear: both;\"><div class=\"rich_text_area\" style=\"margin-bottom: 10px; font-size: 14px; word-wrap: break-word; zoom: 1;\"><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">板块一：大峡谷生态公园</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">大峡谷俯瞰深圳东部黄金海岸线，集山地郊野公园和都市主题公园于一体，体现生态动感旅游文化。包括：海洋之心、大峡谷天街、黎明营地儿童乐园、峡湾森林、发现之旅、云中部落等六大主题区。大侠谷将于2009年春节试业，五一全面开业。 中国海滨第一瀑，宽300米，地形高差70米，水流落差42米高，蔚然壮观。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><span class=\"text_img ed_imgfloat_right\" style=\"border: 1px solid rgb(226, 226, 226); color: rgb(102, 102, 102); display: inline; font-size: 12px; padding: 2px; text-align: center; text-indent: 0px; box-shadow: rgba(150, 150, 150, 0.2) 0px 0px 2px; position: relative; float: right; margin-left: 10px; clear: right; margin-bottom: 10px; width: 300px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><a class=\"ed_image_link\" title=\"点击查看大图\" href=\"http://baike.sogou.com/PicBooklet.v?relateImageGroupIds=&amp;lemmaId=25078&amp;now=http%3A%2F%2Fpic.baike.soso.com%2Fp%2F20130626%2F20130626094203-1434630772.jpg&amp;type=1\" target=\"_blank\" style=\"position: relative; display: block; color: rgb(51, 102, 204);\"><img title=\"深圳东部华侨城\" alt=\"\" src=\"http://pic.baike.soso.com/p/20130626/bki-20130626094203-1434630772.jpg\" width=\"300\" height=\"225\" style=\"display: inline-block; vertical-align: top;\"></a><span style=\"display: block; line-height: 20px; padding: 6px 10px;\">深圳东部华侨城</span></span>云中部落：海拔480米的云中部落是总揽全局的最佳观景台。包括云中风车，云中漫步观景带、“登峰造极”观景平台、自然之眼观光塔、云中餐厅等项目。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">海洋之心：海洋之心以海洋文化为主题，茂密的椰树林、水清沙幼的海滩、风姿绰约的海岛风情……将海洋文化延伸到极致。包括海洋广场、室内水公园、图腾广场等项目和一流的国际主题水上表演，堪称国内最具特色的全景式海洋主题奇幻水秀。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">天街：大侠谷??天街主要向游客展现了浓郁而独具特色的世界红酒文化，主要包括红酒商业街、地下酒窖体验区、海布伦宫、湖畔美食长廊、海菲德湖、天幕等特色项目。天街上空是长120米、曹vbcvb 宽12米的LED屏幕，环绕立体声和高清视频以及变幻莫测的虚幻场景为广大游客营造出步移景换的精彩旅程。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">发现之旅：发现之旅以未来地球生态为主题，虚拟生态环境已严重破坏，各地发生各类毁灭性灾难。包括太空之旅、地心之旅、世纪大海啸、极端气候体验馆、飓风救险水战船、山洪暴发等项目，让游客在惊险刺激的体验中加深对自然灾害的了解，学到自救互救的宝贵知识。黎明营地儿童乐园是专门为儿童精心设计的游乐区。桑巴塔、魔术自行车、救火队、疯狂巴士、高空轨道自行车等游乐项目让孩子们乐不思蜀。其中亮点项目880米的丛林穿梭轨道让喜欢探险的孩子惊呼过瘾。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">板块二：茶溪谷休闲公园</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">茶溪谷体现中西文化交融的休闲度假旅游文化，并兼有“茶、禅、花、竹”等主要元素，包括茵特拉根、湿地花园、茶翁古镇和三洲茶园四个游览区，融合了西方山地小镇的风情、湿地花海的浪漫、茶禅文化的融合和岭南茶田的幽雅。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">茶翁古镇：茶翁古镇是茶文化的鉴赏区和中心服务区，也是环境幽雅的游客休憩区。游客可以品茶餐、尝茶点、吃土菜、观茶戏、饮茶酒，深入了解茶禅文化；还可以在茶艺坊、茶酒坊、陶艺坊亲身体验采茶、制茶、做陶的乐趣。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><span class=\"text_img ed_imgfloat_right\" style=\"border: 1px solid rgb(226, 226, 226); color: rgb(102, 102, 102); display: inline; font-size: 12px; padding: 2px; text-align: center; text-indent: 0px; box-shadow: rgba(150, 150, 150, 0.2) 0px 0px 2px; position: relative; float: right; margin-left: 10px; clear: right; margin-bottom: 10px; width: 300px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><a class=\"ed_image_link\" title=\"点击查看大图\" href=\"http://baike.sogou.com/PicBooklet.v?relateImageGroupIds=&amp;lemmaId=25078&amp;now=http%3A%2F%2Fpic.baike.soso.com%2Fp%2F20130626%2F20130626094215-119507979.jpg&amp;type=1\" target=\"_blank\" style=\"position: relative; display: block; color: rgb(51, 102, 204);\"><img title=\"深圳东部华侨城\" alt=\"\" src=\"http://pic.baike.soso.com/p/20130626/bki-20130626094215-119507979.jpg\" width=\"300\" height=\"191\" style=\"display: inline-block; vertical-align: top;\"></a><span style=\"display: block; line-height: 20px; padding: 6px 10px;\">深圳东部华侨城</span></span>三洲茶园：三洲茶园坐落在青山、绿水、茶田、湿地之间，区内竹林茂密，溪水潺潺，空气中蕴含着大量负离子。是游客洗礼身心、寻幽探奇、亲近自然的绝佳去处。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">茵特拉根：撷取欧洲瑞士阿尔卑斯山麓茵特拉根的建筑、赛马特的花卉、谢菲尔德的彩绘等多种题材和元素，实现了中欧山地建筑风格与优美自然景观的完美结合。茵特拉根包括温馨的主题街区、古老的森林小火车、典雅的度假酒店、矿泉SPA、东部华侨城大剧院、水上高尔夫、网球馆等项目。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">湿地花园：湿地花园是一个以花海景观为载体、以大地艺术展示为特色，集观光、科普教育、户外游乐和特色运动于一体的湿地山野体验区。包括四季花田、升空气球、深圳山地自行车项目、游乐天地、旋转花车、四季植物馆、布兰花园、湿地廊桥、信息湿地、花钟教堂十大项目。（《一起来看流星雨》楚雨荨和慕容云海的热气球之旅和浪漫告白就是选景在这里）</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">板块三：云海谷体育公园</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">云海谷体育公园是以休闲健身、生态探险、时尚运动、休闲娱乐、奥运军体运动为主线，体现户外运动旅游文化。包括两个18洞的山地球场和高尔夫练习场。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">华兴寺坐落在东部华侨城的观音座莲山，体现佛教旅游文化。包括：观音座莲宝像、华兴寺檀越酒店、妙相禅境、《天音梵乐》、大雄宝殿、众香界、香积斋、归一阁、云水堂等。佛像开光大典。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">板块四：主题酒店群落</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">主题酒店群落，由四大主题酒店组成，拥有600余间客房，是国内首个主题酒店群落。包括：瑞士文化主题的茵特拉根酒店、水文化主题的大侠谷瀑布酒店、现代艺术主题的云海谷房车酒店、佛禅文化主题的华兴寺檀越酒店等。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">茵特拉根酒店：茵特拉根酒店茵特拉根华侨城酒店将中欧山地建筑风格与南中国壮美的山海景观，完美结合，出落成一个美得不食人间烟火的童话。包括308间豪华客房、池畔别墅、连排别墅、商业街公寓以及总统堡，在优雅，尊贵，中为你一一呈现山海的浪漫。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">华兴寺檀越酒店华兴寺檀越酒店：将“禅宗”理念纳入酒店的设计构思，创造空灵、简朴的禅意空间。包括22间禅意客房，6间禅意套房，14间静谧大床房，2间典雅双床房。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">大侠谷瀑布酒店：大侠谷瀑布酒店隐藏在大瀑布和巨岩之间，将海洋、红酒、太空统统装进客房，包括啤工厂、太空主题餐厅、全日咖啡厅、宴会演绎厅等主题区。客人可将壮观的大侠谷瀑布收入眼帘，也可沉醉于海菲德红酒小镇。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">云海谷房车酒店：位于2.5平方公里郊野体育公园，收尽东部华侨城独有的湖光山色、云海奇观。她将概念客房与豪华房车，静与动完美结合，最好的诠释了“自由”或“肆意”。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">板块五：天麓大宅</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">深圳东部华侨城</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><span class=\"text_img ed_imgfloat_right\" style=\"border: 1px solid rgb(226, 226, 226); color: rgb(102, 102, 102); display: inline; font-size: 12px; padding: 2px; text-align: center; text-indent: 0px; box-shadow: rgba(150, 150, 150, 0.2) 0px 0px 2px; position: relative; float: right; margin-left: 10px; clear: right; margin-bottom: 10px; width: 220px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><a class=\"ed_image_link\" title=\"点击查看大图\" href=\"http://baike.sogou.com/PicBooklet.v?relateImageGroupIds=&amp;lemmaId=25078&amp;now=http%3A%2F%2Fpic.baike.soso.com%2Fp%2F20130902%2F20130902100224-1441222959.jpg&amp;type=1\" target=\"_blank\" style=\"position: relative; display: block; color: rgb(51, 102, 204);\"><img title=\"天麓大宅\" alt=\"\" src=\"http://pic.baike.soso.com/ugc/baikepic/12295/cut-20130902100237-1981693897.jpg/0\" width=\"220\" height=\"165\" style=\"display: inline-block; vertical-align: top;\"></a><span style=\"display: block; line-height: 20px; padding: 6px 10px;\">天麓大宅</span></span>全球发售。集合诸多非凡之美，深圳东部华侨城 天麓，在中国的别墅史上，是一部由山海写就，壮丽的“建筑史诗”。天麓，依托于东部华侨城9平方公里壮阔的山海间，依托“国家生态旅游示范区”的顶级配套，又有“华兴寺”观音坐莲宝像的佛光普照、殷殷祈福，更显得弥足珍贵；天麓，以巧夺天工的建筑表达人类对山、海的景仰，用非一般的高尚、优雅的空间审美来表现自然空间的博大丰富，席席独树一帜，富于表现力！天麓，历经几代人的酝酿、推敲，更有世界建筑大师理查德迈耶的首次中国实践，2008年，荣膺联合国“全球人居环境最佳社区”。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">天麓共分七个区，其中2008年开盘的“一区、二区、七区”三个别墅区120席别墅，目前仅余27席藏品级独栋大宅，弥足稀稀，终极揭幕。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">在天麓设计中，藉由当地环境与气候上的差异性来寻求 “场所精神”，使用空间、光线、结构的秩序来传达静谧的氛围，再融入现代西方文化，在功能上进行改良，使设计更加适合现代人的生活方式。设计的灵感全部来源于周围文化和环境的点点滴滴，并整合了自然风景和建筑空间，将室内和室外融为一体。建筑的空间感往往体现在葱翠的高原、水池和中庭上，从而营造出所有元素的完美结合。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">相关经济技术指标</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">占地面积82899.18m</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">建筑面积8269.97 m2</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">海洋之心</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">容积率：≤0.1</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">建筑覆盖率：≤15%</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">栋数：20栋山海大宅</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">户型面积：241-730 m</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">停车位：40个</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">【天麓二区】：原生山地建筑 自然本色</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">住宅组群的规划充分利用基地本身沿山登高线迂回弯曲的地形及由高而下的天然山势，以高低错落有序的几何形体组合创作出一个富有现代感的山地建筑群。同时根据地势坡度差异，合理安排户型，最大限度保护山地资源，减少水土流失及不必要的浪费。整体项目为现代风格，以简洁的大面积相同材质的“箱体”为主要元素，突出其质朴而轻盈，融于山林之间，回归自然追求本色的内涵。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><span class=\"text_img ed_imgfloat_left\" style=\"border: 1px solid rgb(226, 226, 226); color: rgb(102, 102, 102); display: inline; font-size: 12px; padding: 2px; text-align: center; text-indent: 0px; box-shadow: rgba(150, 150, 150, 0.2) 0px 0px 2px; float: left; margin-right: 10px; clear: left; margin-bottom: 10px; width: 300px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><a class=\"ed_image_link\" title=\"点击查看大图\" href=\"http://baike.sogou.com/PicBooklet.v?relateImageGroupIds=&amp;lemmaId=25078&amp;now=http%3A%2F%2Fpic.baike.soso.com%2Fp%2F20130813%2F20130813093206-1529742161.jpg&amp;type=1\" target=\"_blank\" style=\"position: relative; display: block; color: rgb(51, 102, 204);\"><img title=\"深圳东部华侨城\" alt=\"\" src=\"http://pic.baike.soso.com/p/20130813/20130813093206-1529742161.jpg\" width=\"300\" height=\"237\" style=\"display: inline-block; vertical-align: top;\"></a><span style=\"display: block; line-height: 20px; padding: 6px 10px;\">深圳东部华侨城</span></span>相关经济技术指标</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">占地面积：约90145m</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">建筑面积：约16900 m</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">容积率：0.18</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">建筑覆盖率：9.49%</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">栋数：44栋</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">户型面积：250-680 m</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">建筑层数：2-3层 　停车位：88个</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">【天麓七区】：果岭景观，纯生态氧吧</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">天麓七区位于云海谷体育公园内，依山傍水，绵延起伏的山峦、宁静的湖面、绿色的高尔夫球场构成了稀缺独特的优美自然风景。整个小区将北美山地小镇风格进行全新演绎，住宅之间、体量及造型都有严格要求，使之与山水环境融洽协调。整个园区的规划上采取回转序排布局，使建筑曲线与山体的曲线相吻合，避免了与山际线的视觉冲突，体现环境价值的最大化。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">相关经济技术指标</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">总用地面积：约139790.74m</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">总建筑面积：约17600 m</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">建筑容积率：≤0.13</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">建筑覆盖率：15%</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">绿地率：80%</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">总户数：56户</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">户型面积：188-1100 m</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">停车单位：80个</p></div></div></section><section style=\"color: rgb(51, 51, 51); font-family: arial, \'pingfang sc\', stheiti, simsun, sans-serif; font-size: 12px; line-height: 18px;\"><div class=\"section_title\" style=\"margin-top: 29px; margin-bottom: 15px; height: 38px; clear: both; background: url(&quot;http://cache.soso.com/baike/i/lemma/sgbk_lemma_shadow_sprite.png&quot;) 0px -21px no-repeat;\"><h2 style=\"margin-top: 0px; margin-right: 10px; margin-bottom: 0px; font-size: 22px; font-family: arial, \'pingfang sc\', stheiti, \'microsoft yahei\', sans-serif; float: left; line-height: 36px; color: rgb(116, 134, 198); white-space: nowrap; zoom: 1;\"><span style=\"font-size: 16px; float: left; display: block; width: 24px; height: 24px; margin-top: 6px; margin-right: 10px; line-height: 24px; color: rgb(255, 255, 255); text-align: center; background-color: rgb(130, 142, 201);\">5</span><a name=\"9\" id=\"9\" style=\"color: rgb(116, 134, 198);\"></a><a name=\"para9\" id=\"para9\" style=\"color: rgb(116, 134, 198);\">开发相关</a></h2></div><div class=\"section_content\" style=\"line-height: 25px; font-size: 14px; clear: both;\"><div class=\"rich_text_area\" style=\"margin-bottom: 10px; font-size: 14px; word-wrap: break-word; zoom: 1;\"></div></div></section><section style=\"color: rgb(51, 51, 51); font-family: arial, \'pingfang sc\', stheiti, simsun, sans-serif; font-size: 12px; line-height: 18px;\"><div class=\"section_content\" style=\"line-height: 25px; font-size: 14px; clear: both;\"><div class=\"rich_text_area\" style=\"margin-bottom: 10px; font-size: 14px; word-wrap: break-word; zoom: 1;\"><a name=\"para10\" id=\"para10\"></a><h3 style=\"margin-top: 25px; margin-bottom: 15px; font-size: 20px; font-family: arial, \'pingfang sc\', stheiti, \'microsoft yahei\', sans-serif; line-height: 24px;\">开发理念</h3><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城开业伊始就受到了八方游客的热烈追捧，取得了骄人的生态效益、经济效益和社会效益，能取得这些成果，追根究底，其核心价值就在于“生态环保大于天”的开发理念。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城在旅游产品创新的理论和实践上实现了一种战略性突破。这种创新理念和突破可概括为三大战略突破、三项基本原则和十大生态环保亮点。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">三大战略突破：</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">（一）项目性质上，从“做单个项目”向“打造旅游聚集区”的突破；</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">（二）项目空间上，从城市中心向郊野地带突破；</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">（三）项目类型上，从观光旅游向休闲度假突破。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">三项基本原则：</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">（一）在项目规划时，尽量不扰动山、水、植被;</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">（二）在开发建设时，加强对山、水、植被的保护;</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">（三）在产品选择时，充分利用无污染、可循环的产品。</p></div></div></section><section style=\"color: rgb(51, 51, 51); font-family: arial, \'pingfang sc\', stheiti, simsun, sans-serif; font-size: 12px; line-height: 18px;\"><div class=\"section_content\" style=\"line-height: 25px; font-size: 14px; clear: both;\"><div class=\"rich_text_area\" style=\"margin-bottom: 10px; font-size: 14px; word-wrap: break-word; zoom: 1;\"><a name=\"para11\" id=\"para11\"></a><h3 style=\"margin-top: 25px; margin-bottom: 15px; font-size: 20px; font-family: arial, \'pingfang sc\', stheiti, \'microsoft yahei\', sans-serif; line-height: 24px;\">开发特点</h3><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">针对市场散客-1</b></p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">作为一个繁忙的都市人，偶尔奔波的间歇，你可曾梦想过去到远离都市的某一些地方，将生活的节奏放慢下来，让忙碌的自己有舒缓的片刻来享受休闲的乐趣？在中国深圳的大梅沙，华侨城集团用35亿元的大手笔精雕细琢、精益求精的打造了一方9平方公里的梦想广场，让深处其中的你感受身的休憩、心的悠游。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">无论你想投身于蔚蓝的海洋、逶迤的山脉抑或浓密的竹林之中享受大自然的清新与惬意；还是奔向有着奇幻的山谷、神秘的境地自由地探险，尽情地撒野；或是寻找禅茶梵乐的静心福地修身养性，感悟禅宗的洗礼，体验诗话人生；甚至“越域”到另一个国度，体验异域的浪漫风情？这里的3个生态主题公园，2个山地高尔夫球场、3座主题小镇、4家主题酒店、4台主题演艺、主题地产和主题佛教园区都能让你将缤纷炫彩的种种旅游体验一揽在怀，让身为社会名流的你、都市精英的你或是新兴人类的你都能收获一次回归自然的惊喜，在这座专为都市人营造的梦想之城中欢笑、静谧、狂喜、安然、……</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">针对市场散客-2</b>（对外推介）</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">东部华侨城是由国务院国资委旗下的中央企业华侨城集团投资35亿元精心打造而成的世界级度假旅游目的地，全区占地近九平方公里，拥有二个主题公园、三座旅游小镇、四家度假酒店、两座18洞山地球场、大华兴寺和天麓住宅等精品项目。到东部华侨城，您可以到茵特拉根主题酒店群中挑选一间自己喜爱的特色酒店，到山地球场潇洒挥杆，来一次放松身心的高品质度假！或到探险乐园享受上天下地入海的全方位感观体验；到度假公园体验浪漫的瑞士风情和在百亩茶田中感受大自然的清新；或去拜谒目前世界上唯一一座集四尊不同观音于一体的户外贴金菩萨像，修身养性。无论你是钟情于一个人纯粹的安然与自得，还是渴望一家人的天伦之乐，在东部华侨城你都能找到属于自已的梦想天堂，都能感受到回归自然的欣喜。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">历经五载的规划建设，东部华侨城已于8月1日对外全面开业。自07年一期项目试业以来，先后荣获20多项社会荣誉，实现了良好的经济效益和社会效益，成为了深圳一张流光溢彩的城市名片。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><b style=\"font-size: 1em;\">针对市场散客-3</b>（度假）</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\"><span class=\"text_img ed_imgfloat_right\" style=\"border: 1px solid rgb(226, 226, 226); color: rgb(102, 102, 102); display: inline; font-size: 12px; padding: 2px; text-align: center; text-indent: 0px; box-shadow: rgba(150, 150, 150, 0.2) 0px 0px 2px; position: relative; float: right; margin-left: 10px; clear: right; margin-bottom: 10px; width: 300px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><a class=\"ed_image_link\" title=\"点击查看大图\" href=\"http://baike.sogou.com/PicBooklet.v?relateImageGroupIds=&amp;lemmaId=25078&amp;now=http%3A%2F%2Fpic.baike.soso.com%2Fp%2F20130626%2F20130626094243-1067873012.jpg&amp;type=1\" target=\"_blank\" style=\"position: relative; display: block; color: rgb(51, 102, 204);\"><img title=\"深圳东部华侨城\" alt=\"\" src=\"http://pic.baike.soso.com/p/20130626/bki-20130626094243-1067873012.jpg\" width=\"300\" height=\"159\" style=\"display: inline-block; vertical-align: top;\"></a><span style=\"display: block; line-height: 20px; padding: 6px 10px;\">深圳东部华侨城</span></span>东部华侨城位于中国最美八大海岸之一、风光旖旎的深圳大鹏湾畔，前拥大小梅沙黄金海岸，背倚青翠挺拔的梧桐山脉，占地近9平方公里，是国内首个集休闲度假、观光旅游、户外运动、祈福灵修、居住养生、生态体验、科普教育等为一体的世界级度假旅游目的地。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 24px;\">在这里可以入住适合不同消费需求的四大特色主题酒店，享受四种创意非凡的度假体验；挥杆于被誉为“深圳地标球场”的山地高尔夫球场；到中国最大的温泉度假庄园里泡SPA，寻养生之道；玩转生态探险和休闲度假两大主题乐园、朝拜世界唯一户外贴金四面观音像。在这座专为都市人营造的梦想之城中回归自然的怀抱，感受身的休憩、心的悠游！</p></div></div></section>',1495855266,92,5,'华侨城','小侨',1,1),
(122,'园区介绍','upload/articleThumb/201706/1496623368974b278d668e829.jpg',56,'<h3>过山车:</h3><p>该过山车轨道长度850米，<span style=\"font-size: 12px; line-height: 17.1429px;\">高30米，最高</span><span style=\"font-size: 12px; line-height: 17.1429px;\">时速</span><span style=\"font-size: 12px; line-height: 17.1429px;\">72.4km/h，</span><span style=\"font-size: 12px; line-height: 1.42857;\">单次乘坐时长1分32秒。</span><span style=\"font-size: 12px; line-height: 17.1429px;\">广州番禺</span><span style=\"font-size: 12px; line-height: 17.1429px;\">长隆欢乐世界可</span><span style=\"font-size: 12px; line-height: 17.1429px;\">看到在树丛后</span><span style=\"font-size: 12px; line-height: 17.1429px;\">若隐若现的鲜</span><span style=\"font-size: 12px; line-height: 17.1429px;\">艳</span><span style=\"font-size: 12px; line-height: 17.1429px;\">的</span><span style=\"font-size: 12px; line-height: 1.42857;\">橙色轨道，</span><span style=\"font-size: 12px; line-height: 17.1429px;\">高低</span><span style=\"font-size: 12px; line-height: 17.1429px;\">盘旋</span><span style=\"font-size: 12px; line-height: 17.1429px;\">，</span><span style=\"font-size: 12px; line-height: 17.1429px;\">&nbsp;</span><span style=\"font-size: 12px; line-height: 1.42857;\">从眼前一直延伸到远处，</span><span style=\"font-size: 12px; line-height: 1.42857;\">这就是园</span><span style=\"font-size: 12px; line-height: 17.1429px;\">&nbsp;</span><span style=\"font-size: 12px; line-height: 17.1429px;\">中曾经创下</span><span style=\"font-size: 12px; line-height: 1.42857;\">翻滚数最多的过山车</span><span style=\"font-size: 12px; line-height: 17.1428px;\">之一的</span><span style=\"font-size: 12px; line-height: 17.1429px;\">吉尼斯世界纪录的</span><span style=\"font-size: 12px; line-height: 1.42857;\">“十环过山车”。</span></p><p><span style=\"line-height: 1.42857;\">&nbsp;</span><span style=\"line-height: 1.42857;\">&nbsp; &nbsp;</span><img src=\"/hqc_mp/upload/articleImg/201706/1496623381895u=292499950,1012121716&amp;fm=117&amp;gp=0.jpg\" data-filename=\"img\" style=\"width: 165px;\"><span style=\"line-height: 1.42857;\">&nbsp;</span><span style=\"line-height: 1.42857;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><hr><h3>水上乐园：</h3><p>水上乐园是一处大型旅游场地，是主题公园的<span style=\"font-size: 12px; line-height: 17.1429px;\">其中一种，多数娱乐设施与水有关，属于娱乐</span><span style=\"font-size: 12px; line-height: 17.1429px;\">性的人工旅游景点。有游泳池，人工冲浪，</span><span style=\"font-size: 12px; line-height: 17.1429px;\">水上橡皮筏等。</span></p><p>&nbsp;<img src=\"/hqc_mp/upload/articleImg/201706/1496623393403thumbnails.jpg\" data-filename=\"img\" style=\"width: 525px;\"></p><hr><p><span style=\"color: inherit; font-family: inherit; font-size: 24px; line-height: 1.1;\">大摆锤：</span></p><p>超级大摆锤介绍:超级大摆锤是世界最新最<span style=\"font-size: 12px; line-height: 17.1428px;\">眩的大型机动游乐设备,由德国HUSS公司</span><span style=\"font-size: 12px; line-height: 17.1429px;\">原厂进口, 号称“全球最大摆锤” 最高时</span><span style=\"font-size: 12px; line-height: 17.1429px;\">速110公里/小时,最大摆幅240度,带您飞上</span><span style=\"font-size: 12px; line-height: 1.42857;\">&nbsp;</span></p><p><img src=\"/hqc_mp/upload/articleImg/201706/1496623404380u=292499950,1012121716&amp;fm=117&amp;gp=0.jpg\" data-filename=\"img\" style=\"width: 165px;\"><span style=\"line-height: 1.42857;\"><br></span></p><hr><p></p><p><span style=\"color: inherit; font-family: inherit; font-size: 24px; line-height: 1.1;\">飓风旋椅：</span></p><p>飓风飞椅是一项十分受大众喜欢的游乐设施。<span style=\"font-size: 12px; line-height: 17.1428px;\">飓风飞椅像是一把大伞（或能摇头的大伞）下</span><span style=\"font-size: 12px; line-height: 17.1429px;\">吊着很多把靓丽、精致、安全的吊椅，在大伞</span><span style=\"font-size: 12px; line-height: 17.1429px;\">转动（或摇头）时，吊椅在空中波浪起伏地旋转</span><span style=\"font-size: 12px; line-height: 1.42857;\">&nbsp;</span></p><p><img src=\"/hqc_mp/upload/articleImg/201706/1496623415299thumbnails.jpg\" data-filename=\"img\" style=\"width: 525px;\"><span style=\"font-size: 12px; line-height: 1.42857;\"><br></span></p><p></p>',1495865572,115,56897416,'深圳欢乐谷','深圳欢乐谷',1,1),
(125,'酒店餐饮','upload/articleThumb/201706/1497491292093u=1997117185,2355311337&amp;fm=23&amp;gp=0.jpg',57,'<div><span style=\"line-height: 13.2px;\"><b>涧春风尽东来，山海度假始相约。回忆旧的时光，那些儿时穿新衣、吃饺子、放爆竹的快乐过往成为了都市人分外渴望的回忆。眼看具有传统意味的</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>春节狂欢季近在咫尺，幸好，今年东部华侨城“年味儿”有点浓，倾力打造的五大精彩活动，助你寻回关于“年”的记忆！</b></span></div><div><img src=\"/hqc_mp/upload/articleImg/201706/1497491401654888.jpg\" data-filename=\"img\" style=\"width: 100%;\"><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>山海迎新第一式：海菲德狂欢巡游 、茵特拉根新春大巡游</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>时间：大年初一至初五（10:30大侠谷，13:30茶溪谷）</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>地点：大侠谷、茶溪谷主题景区</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>主题：挥洒激情、喜迎新年</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>&nbsp; &nbsp; 重磅炸弹，即刻引爆！备受期待的海菲德狂欢巡游、茵特拉根新春大巡游活动于春节期间强势登陆，各路“新春”元素的花车各显招数，有的载</b></span></div><div><b style=\"line-height: 13.2px;\"><br></b></div><div><b style=\"line-height: 13.2px;\">着身着炫丽舞服的表演嘉宾，有的载着大大小小的新年礼物、有的载着超高技艺的杂技演绎者，数十种表演风格齐齐加入到大巡游的队伍中。上午伴</b></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>随着悠扬的奏乐一起体验海菲德狂欢巡游与精彩演艺的乐趣，下午从东部华侨城大剧院途径充满欧式风情的茵特拉根小镇，再到咕咕钟舞台，跟随盛</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>大的巡游队伍，在人群中尽情狂欢。</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><img src=\"/hqc_mp/upload/articleImg/201706/1497491414240u=663886542,3054501110&amp;fm=23&amp;gp=0.jpg\" data-filename=\"img\" style=\"width: 100%;\"><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>山海迎新第二式：茵特拉根新春湖畔晚会</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>时间：大年初一至初五20:00</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>地点：茶溪谷-茵特拉根湖畔吧水上舞台</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>主题：动感演出、随心律动</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>&nbsp; &nbsp; 东部华侨城茶溪谷主题景区，在“浪漫、自然、健康、环保”的理念引导下，茶溪谷融合了山水、茶艺、花卉、风情等元素，让来到这里的每个</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>人都能从容体味其中“茶禅花竹”、“天人合一”的真谛。来到临水而建的茵特拉根湖畔吧水上舞台，感受倾力打造、集多重艺术表演形式于一体的</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>大型新春欢乐表演。各具风格的乐队演唱，神奇梦幻的艺术表演，时尚动感的舞团演出，惊心动魄的杂技演绎、充满悬念节目将接连精彩上演，引爆</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>全场!</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><img src=\"/hqc_mp/upload/articleImg/201706/1497491437867333.jpg\" data-filename=\"img\" style=\"width: 100%;\"><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>山海迎新第三式：第二届AIO新春庙会</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>时间：春节期间</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>地点：黑森林酒店</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>主题：美食荟萃、欢乐互动</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>&nbsp; &nbsp; 春节期间，黑森林酒店将举办第二届“AIO新春庙会”，充满着新春元素的装饰、装扮席卷整个景区。“年味儿”十足的新春庙会将集合互动游</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>戏、趣味体验、特色小吃三大传统庙会活动于一体，让游客感受来自东部华侨城的独特“年味儿”，还有感染力互动性极强的变装表演，琳琅满目的</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>特色工艺将在现场精彩呈现，展现中华传统民俗的惊艳魅力。</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><img src=\"/hqc_mp/upload/articleImg/201706/1497491468758777.jpg\" data-filename=\"img\" style=\"width: 100%;\"><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>山海迎新第四式：大华兴寺新春祈福</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>时间：元旦、春节期间</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>地点：大华兴寺</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>主题：祈福迎新、八福迎春</b></span></div><div><span style=\"line-height: 13.2px;\"><b>&nbsp;</b></span></div><div><span style=\"line-height: 13.2px;\"><b> &nbsp; 位于“观音座莲”山的大华兴寺，座落在云海谷、茶溪谷与大侠谷三谷之间，以唐宋时期古书院建筑风格为主，传播展示中国佛教传统文化。这</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>个春节期间，古色古香的大华兴寺将国人传统新春文化氛围渲染到极致，广纳八方来客，共度充满温馨暖意的除夕夜。钟鸣五洲，福泽绵长，八福迎</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>春，一路福气。除夕早上6:20开始，大华兴寺将开展早课、佛宝赞、上普供、过堂、接灶、供福灯、传福灯、敲福钟、敬福香、纳福归等活动将接连</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>开启，八福迎春，一路福气多多。元旦期间，大华兴寺还将举办地藏经法会，可以一次性将传统佛教文化和传统节庆氛围体</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><img src=\"/hqc_mp/upload/articleImg/201706/1497491492856u=3409283046,929299449&amp;fm=23&amp;gp=0.jpg\" data-filename=\"img\" style=\"width: 100%;\"><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>元旦+春节迎新接力不间断，白天+夜晚狂欢盛宴不停歇，祝福送不停！更多优惠详情请关注深圳东部华侨城官方网站、热线、微博、微信，精彩即将</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>上线、敬请期待！</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>官方网站：www.octeast.com</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>官方热线：86-755-8888-9888</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>官方微博：@深圳东部华侨城微博</b></span></div><div><span style=\"line-height: 13.2px;\"><b><br></b></span></div><div><span style=\"line-height: 13.2px;\"><b>官微信：新鲜上线服务号，订票扫</b></span></div>',1496299221,72,6,'深圳东部华侨城','深圳东部华侨城',1,1),
(131,'春风十里，不如度假去——东部华侨城“踏春度假季”奏响春日序曲','upload/articleThumb/201706/1497493292092102.jpg',84,'<p>春风初生，春林初盛，蛰伏了一冬的心在草木萌动的时节也跟着复苏躁动起来，按捺不住，雀跃迎春。乍暖还寒间，浩然山海绿意盎然，东部华侨城“踏春度假季”带着浓浓春意即将扶风而至，三重度假精彩携踏春优惠飘然席卷全城，温柔却有力量，在无限春光里欢愉相约、温拂人心。</p><div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><b><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/2017313144344863864.jpg\" style=\"width: 100%;\"></b></div><p></p><p>花漾女人节：风尚伊人展英姿，趣味游戏齐动员</p><p><br>&nbsp; &nbsp; &nbsp; &nbsp;南国三月，草长莺飞，蛰虫昭苏，不妨走进东部华侨城的春天，走进“花漾女人节”的无限春光里，且看身边的每一位伟大女性，在三月这个特别的庆贺时节，化身为令人惊艳的女神，展现温柔动人的绰约风姿。“花漾女人节”的开幕式将在风景如画的茶溪谷主题景区拉开帷幕，各位魅力女性将身着古色古香的民族服饰、亦或具有异域风情的主题装束现场走秀展示，演绎别样风采。活动当日，召集全城400名市民，在茶溪谷咕咕钟舞台广场同聚欢乐时光。“女生为王至尊体验”、“男女反串高跟鞋赛跑”、“我要长大角色互换”等精彩互动游戏即将上线，让家庭、情侣、朋友、亲子间感受角色互换与年代穿越的乐趣与感动。</p><div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><b><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/2017313144546295245.jpg\" style=\"width: 100%;\"></b></div>山地采茶节：三洲茶田春意闹，采青品茗话茶香&nbsp; &nbsp;<p></p><p>&nbsp; &nbsp; &nbsp;春色如画，春山如妆，三洲茶田雅韵悠长，茶翁古镇茶色生香。次第层峦的三洲茶田，将典型岭南茶田风光和原生态自然景观完美结合。无论是风和日丽、光影交错的暖阳天，还是雾气氤氲、和风细雨的雨雾天，都别具风貌，成为踏春访古，寻香品茗相当惬意的上佳之地。一年一度的“山地采茶节”成为东部华侨城春日度假里尤为期待的节日，丰富多彩的茶文化主题活动也即将为这个春天增姿添彩。</p><div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><b><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/2017313145202046557.jpg\" style=\"width: 100%;\"><br><br><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/2017313145306969531.jpg\" style=\"width: 100%;\"></b></div>&nbsp; &nbsp; &nbsp; &nbsp;待到茶青吐翠，充满度假雅趣的“山地采茶节”开幕式也将于3月下旬拉开帷幕，届时茶文化爱好者、茶溪谷老茶客、资深茶人、各界媒体、微信征集的幸运游客将齐聚一堂，感受古色古香的茶文化表演；共同体验在翠色欲滴的三洲茶园采撷顶尖新绿、采茶制茶的新奇乐趣；一起茶盏在握，尽享慵懒的春日好时光；或是在庄重闲雅的氛围中感受茶文化与传统孝道的礼仪的结合与传承；亦或回归对生活的无限热爱，用心寻找身边的“醉美茶仙子”。<div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><b><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/2017313145461529239.jpg\" style=\"width: 100%;\"><br><br><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/2017313145573698901.jpg\" style=\"width: 100%;\"></b></div>风筝营地：纸鸢竹马看儿嬉，始觉春光为醉人<br>&nbsp; &nbsp; &nbsp; &nbsp;纸鸢追风，摇曳古今，载着乘风而上的心愿绽放天际。就在这片醉人的“四季花田”，将有一场美如画的东部华侨城“风筝营地”活动精彩上演，届时将呈现震撼的风筝盛宴，带你一起领略中国传统风筝文化的魅力。放飞风筝的同时，放飞的的也是美好的心愿与梦想，东部华侨城将诚邀心怀梦想的群体一起执笔绘梦，在“创意纸鸢营”共享亲子涂鸦温馨一刻，共同感受巨型3D风筝展演的惊艳时光。这个春日里，邀你一起听风赏花嬉纸鸢！<div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><b><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/2017313146387998401.jpg\" style=\"width: 100%;\"><br><br><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/2017313146483287875.jpg\" style=\"width: 100%;\"></b></div>踏春多重优惠链接：<br>&nbsp; &nbsp; &nbsp; &nbsp;温暖这个春日的不止有徐徐春风，还有不同时段的春日特别温情特惠，一起在春暖花开的季节开启春日度假之旅，畅享主题景区、精彩演艺的优惠度假体验！<br><br>优惠一：门票3.8折<br>优惠时间：2月19日-2月27日<br>票种：东部华侨城双谷一日游门票（原价300元，优惠价115元）<br>&nbsp; &nbsp; &nbsp; 茶溪谷+天禅套票（原价250元，折扣价95元）<br>使用期限：2月20日-2月28日（周末不可用）<br>购买渠道：东部华侨城官方微信服务号及美团、同程、携程、驴妈妈、要出发、途牛六大渠道；<br>活动备注：每个用户每天最大购买张数为1张，仅限女性游客；每天数量有限，售完即止；此优惠票不可退改；<div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><b><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/201731314107468097.jpg\" style=\"width: 100%;\"></b></div>优惠二：门票6.8折<br>优惠时间：2月28日-3月7日<br>票种：东部华侨城双谷一日游门票（原价300元，优惠价205元）<br>&nbsp; &nbsp; &nbsp; 茶溪谷+天禅套票（原价250元，折扣价170元）<br>使用期限：3月1日-3月8日<br>购买渠道：东部华侨城官方微信服务号及美团、同程、携程、驴妈妈、要出发、途牛六大渠道（每个用户每天最大购买张数为5张；此优惠票不可退改）<div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><b><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/20173131410196397006.jpg\" style=\"width: 100%;\"></b></div>优惠三：门票8.3折<br>优惠时间：3月8日-3月15日<br>票种：东部华侨城双谷一日游门票（原价300元，优惠价245元）<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;茶溪谷+天禅套票（原价250元，折扣价205元）<br>使用期限：3月9日-3月16日<br>购买渠道：东部华侨城官方微信服务号及美团、同程、携程、驴妈妈、要出发、途牛六大网站（每个用户每天最大购买张数为5张）<div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><b><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/20173131410465744281.jpg\" style=\"width: 100%;\"></b></div>优惠四：茶溪谷门票特惠<br style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; background-color: rgb(243, 243, 243);\">活动时间：3月8日<br>活动价格：38元（原价180元）<br>使用期限：2017年3月9日至3月16日<br style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; background-color: rgb(243, 243, 243);\">购买渠道：东部华侨城官方微信服务号及美团、同程、携程、驴妈妈、要出发、途牛六大网站（每个用户最大购买张数为1张）。<div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><b><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201703/2017313141734971393.jpg\" style=\"width: 100%;\"></b></div><p></p>',1496301758,143,2,'东部华侨城','（文：吴晓旭 / 摄：杨云文）',1,1),
(134,'2017新春如何过？东部华侨城山海度假更特别！','upload/articleThumb/201706/1497492856653101.jpg',84,'<p>满涧春风尽东来，山海度假始相约。回忆旧的时光，那些儿时穿新衣、吃饺子、放爆竹的快乐过往成为了都市人分外渴望的回忆。眼看具有传统意味的春节狂欢季近在咫尺，幸好，今年东部华侨城“年味儿”有点浓，倾力打造的五大精彩活动，助你寻回关于“年”的记忆！</p><div style=\"line-height: 65px;\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/2017120154141522235.jpg\" style=\"width: 100%;\"></div><br><p></p><p>山海迎新第一式：海菲德狂欢巡游 、茵特拉根新春大巡游<br></p><p><br>时间：大年初一至初五（10:30大侠谷，13:30茶溪谷）<br></p><h6>地点：大侠谷、茶溪谷主题景区<br>主题：挥洒激情、喜迎新年</h6><p><span style=\"line-height: 1.42857;\">&nbsp; &nbsp;重磅炸弹，即刻引爆！备受期待的海菲德狂欢巡游、茵特拉根新春大巡游活动于春节期间强势登陆，各路“新春”元素的花车各显招数，有的载着身着炫丽舞服的表演嘉宾，有的载着大大小小的新年礼物、有的载着超高技艺的杂技演绎者，数十种表演风格齐齐加入到大巡游的队伍中。上午伴随着悠扬的奏乐一起体验海菲德狂欢巡游与精彩演艺的乐趣，下午从东部华侨城大剧院途径充满欧式风情的茵特拉根小镇，再到咕咕钟舞台，跟随盛大的巡游队伍，在人群中尽情狂欢。</span></p><div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/2017120152163287875.jpg\" style=\"width: 100%;\"><br><br><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/2017120152346872165.jpg\" style=\"width: 100%;\"><br><br><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/2017120152474312350.jpg\" style=\"width: 100%;\"></div><div><br><br>山海迎新第二式：茵特拉根新春湖畔晚会<br><br>时间：大年初一至初五20:00<br><br>地点：茶溪谷-茵特拉根湖畔吧水上舞台<br><br>主题：动感演出、随心律动</div><div><div><br></div><div>东部华侨城茶溪谷主题景区，在“浪漫、自然、健康、环保”的理念引导下，茶溪谷融合了山</div><div><div style=\"display: inline !important;\">水、茶艺、花卉、风情等元素，让来到这里的每个人都能从容体味其中“茶禅花竹”、“天人合</div></div><div>一”的真谛。来到临水而建的茵特拉根湖畔吧水上舞台，感受倾力打造、集多重艺术表演形式于</div><div><div style=\"display: inline !important;\">一体的大型新春欢乐表演。各具风格的乐队演唱，神奇梦幻的艺术表演，时尚动感的舞团演出，</div></div><div><div style=\"display: inline !important;\">惊心动魄的杂技演绎、充满悬念节目将接连精彩上演，引爆全场!</div></div></div><div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/2017120152593738727.jpg\" style=\"width: 100%;\"><br><br><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/201712015373015414.jpg\" style=\"width: 100%;\"></div><div><br><br>山海迎新第三式：第二届AIO新春庙会<br><br>时间：春节期间<br><br>地点：黑森林酒店<br></div><div><br>主题：美食荟萃、欢乐互动<br><br>&nbsp; &nbsp; 春节期间，黑森林酒店将举办第二届“AIO新春庙会”，充满着新春元素的装饰、装扮席卷整个景区。“年味儿”十足的新春庙会将集合互动游戏、趣味体验、特色小吃三大传统庙会活动于一体，让游客感受来自东部华侨城的独特“年味儿”，还有感染力互动性极强的变装表演，琳琅满目的特色工艺将在现场精彩呈现，展现中华传统民俗的惊艳魅力。</div><div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/2017120153201504657.jpg\" style=\"width: 100%;\"><br><br><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/2017120153303636554.jpg\" style=\"width: 100%;\"></div><div><br>山海迎新第四式：大华兴寺新春祈福<br><br>时间：元旦、春节期间<br><br>地点：大华兴寺<br><br>主题：祈福迎新、八福迎春<br><br>&nbsp; &nbsp; 位于“观音座莲”山的大华兴寺，座落在云海谷、茶溪谷与大侠谷三谷之间，以唐宋时期古书院建筑风格为主，传播展示中国佛教传统文化。这个春节期间，古色古香的大华兴寺将国人传统新春文化氛围渲染到极致，广纳八方来客，共度充满温馨暖意的除夕夜。钟鸣五洲，福泽绵长，八福迎春，一路福气。除夕早上6:20开始，大华兴寺将开展早课、佛宝赞、上普供、过堂、接灶、供福灯、传福灯、敲福钟、敬福香、纳福归等活动将接连开启，八福迎春，一路福气多多。</div><div style=\"line-height: 65px;\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/2017120153407810534.jpg\" style=\"width: 100%;\"><br><br><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/2017120153496925448.jpg\" style=\"width: 100%;\"><br><br><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/201712015403389361.jpg\" style=\"width: 100%;\"></div><div>元旦期间，大华兴寺还将举办地藏经法会，可以一次性将传统佛教文化和传统节庆氛围体验个够。<br><br>元旦+春节迎新接力不间断，白天+夜晚狂欢盛宴不停歇，祝福送不停！更多优惠详情请关注深圳东部华侨城官方网站、热线、微博、微信，精彩即将上线、敬请期待！<br><br>官方网站：www.octeast.com<br><br>官方热线：86-755-8888-9888<br><br>官方微博：@深圳东部华侨城微博<br><br>官方微信：新鲜上线服务号，订票扫这里<br><font color=\"#666666\" face=\"微软雅黑, 宋体\" style=\"line-height: 65px;\"><span style=\"font-size: 48px; background-color: rgb(243, 243, 243);\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/20171201457498574222.png\" style=\"height: 161px; width: 143px;\"></span></font><br>关注走心订阅号，精彩看这里 &nbsp; &nbsp;<br><font color=\"#666666\" face=\"微软雅黑, 宋体\" style=\"line-height: 65px;\"><span style=\"font-size: 48px; background-color: rgb(243, 243, 243);\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201701/20171201456534398318.png\" style=\"height: 157px; width: 139px;\">&nbsp;</span></font></div><p></p>',1496301893,64,0,'东部华侨城','思伟',1,1),
(135,'生态文化节-探秘大自然”亲子活动萌动全城 智慧冒险穿越之旅为秋游快乐加码！','upload/articleThumb/201706/1497491945990100.jpg',84,'远离喧嚣、亲近自然，秋游时节来一场妙趣横生的穿越探秘之旅，寻找单纯的乐趣与感动。10月16日上午9时，东部华侨城“生态文化节-探秘大自然”活动在充满生态与浪漫元素的茶溪谷主题景区登陆！350多名亲子家庭成员满怀热情参与，同挚爱的家人朋友一起并肩协力，在紧张有趣的冒险中释放动感与活力、感受温情与欢乐。<div style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; text-align: center; background-color: rgb(243, 243, 243);\"><span style=\"color: rgb(80, 98, 108); font-family: 宋体; font-size: 12px;\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201611/201611211717585727801.jpg\" style=\"width: 100%;\"></span></div><div><p style=\"padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px;\">&nbsp;三大趣味闯关环节精彩绽放<br>在秋日大好晨光里，身穿激萌可爱卡通亲子服装的亲子家庭们登场，彩绘师给现场参赛者们进行可爱俏皮、阳光俊朗的人脸彩绘，瞬间成为东部华侨城亮丽的风景，吸引了不少游人驻足围观。这次活动面向3-12岁的亲子家庭，报名的家长们也纷纷揣着一颗童心，陪伴着挚爱的宝贝在茶溪谷主题景区奔跑探秘，以绿色健康的方式迎接全新挑战。同时段还有专业摄影师全程跟拍，让精彩定格，用心纪录下每一位参与者的欢乐瞬间。抛却烦恼、放松身心，在探秘、冒险的世界里撒欢。</p><p style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px; text-align: center; background-color: rgb(243, 243, 243);\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201611/201611211718216482562.jpg\" style=\"width: 100%;\"></p><p style=\"padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px;\">在“探秘大自然”里的亲子家庭，在欢乐中感受与困难、挑战的博弈。“探秘大自然”不仅是一场专门为亲子家庭打造的欢乐趣味运动，更是一次环环相扣、充满冒险体验的闯关游戏。千条万道的“植物迷宫”、挑战智力的“绽放的花儿”、考验耐力的“滚筒种子”……多种趣味竞技轮番上演，伴随着音乐，享受亲情，在一家人的欢乐互动中找寻被遗忘许久的童趣情怀。</p><p style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px; text-align: center; background-color: rgb(243, 243, 243);\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201611/201611211718436932315.jpg\" style=\"color: rgb(80, 98, 108); font-family: 宋体; font-size: 12px; width: 100%;\"><br><br><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201611/2016112117198300143.jpg\" style=\"width: 100%;\"></p><p style=\"padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px;\">参赛家庭在进入“植物迷宫”后需要快速通过迷宫的同时躲避开迷宫中的铃铛人，一旦被铃铛人抓到就需回到迷宫起点；“滚筒种子”和“绽放的花儿”的环节则同样需要一家人齐心协力、充满勇气完成闯关。而除了活动本身的乐趣以外，享受东部华侨城自然风光也不失为一种幸福的选择，东部华侨城旅游度假区充满了梦幻风情的主题景区成为此次探秘大自然旅程之余的乐享福利。欣赏茶溪谷之秋日景色，让度假体验提升。</p><p style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px; text-align: center; background-color: rgb(243, 243, 243);\"><span style=\"color: rgb(80, 98, 108); font-family: 宋体; font-size: 12px;\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201611/201611211719236265033.jpg\" style=\"width: 100%;\"><br><br><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201611/20161121171944172702.jpg\" style=\"width: 100%;\"></span></p><p style=\"padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px;\">来自广州的张女士一家带着6岁的女儿在参与完此次“探秘大自然”活动后显得格外开心、意犹未尽。张女士表示上次未能抢到“糖果泡泡跑”的活动名额，感到可惜。这次能陪着孩子一起参与非常高兴，在游玩之余还感受到绿色生态的魅力，增进了与孩子的交流。同时，通过各种线索完成既定任务的过程中，非常考验孩子智力和耐力，自己也获益良多，张女士一家希望东部华侨城能有更多这样主题丰富、参与感十足的亲子活动！</p><p style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px; text-align: center; background-color: rgb(243, 243, 243);\"><span style=\"color: rgb(80, 98, 108); font-family: 宋体; font-size: 12px;\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201611/201611211719561632922.jpg\" style=\"width: 100%;\"></span></p><p style=\"padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px;\">&nbsp;“生态文化节”系列精彩活动即将上线</p><p style=\"padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px;\">东部华侨城与深圳儿童周末携手举办的“探秘大自然”亲子活动受到了广大亲子家庭的喜爱，而面向更多度假群体的“生态文化节”系列活动也即将陆续与游客朋友们见面，“约惠一起旅行”、“南瓜节”等主题活动将带着满满诚意让更多的人回归生态之中，重拾都市人与自然的紧密联系。东部华侨城主题酒店群倾情相邀享低碳新生活！告别“亚健康”，返身自然，为绿色生活带来更多美好与欢乐，感受生命的热情与蓬勃。</p><p style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, 宋体; font-size: 48px; line-height: 65px; padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px; text-align: center; background-color: rgb(243, 243, 243);\"><span style=\"color: rgb(80, 98, 108); font-family: 宋体; font-size: 12px;\"><img alt=\"\" src=\"http://www.octeast.com/mobile/uploadimgs/201611/20161121172018213763.jpg\" style=\"width: 100%;\"></span></p><p style=\"padding-top: 10px; padding-bottom: 10px; margin-bottom: 0px;\">详情请关注深圳东部华侨城官方网站、微博、微信。</p></div>',1496302621,60,0,'刘家坞','诗音',1,1),
(136,'满涧春风尽东来，山海度假始相约。','upload/articleThumb/201706/1497491671562103.jpg',84,'<div>满涧春风尽东来，山海度假始相约。回忆旧的时光，那些儿时穿新衣、吃饺子、放爆竹的快乐过往成为了都市人分外渴望的回忆。眼看具有传统意味</div><div><br></div><div>的春节狂欢季近在咫尺，幸好，今年东部华侨城“年味儿”有点浓，倾力打造的五大精彩活动，助你寻回关于“年”的记忆！</div><div><br></div><div><img src=\"/hqc_mp/upload/articleImg/201706/1497491820984106.jpg\" data-filename=\"img\" style=\"width: 100%;\"><br></div><div><span style=\"line-height: 1.42857;\">山海迎新第一式：海菲德狂欢巡游 、茵特拉根新春大巡游</span></div><div><br></div><div>时间：大年初一至初五（10:30大侠谷，13:30茶溪谷）</div><div><br></div><div>地点：大侠谷、茶溪谷主题景区</div><div><br></div><div>主题：挥洒激情、喜迎新年</div><div><br></div><div>&nbsp; &nbsp; 重磅炸弹，即刻引爆！备受期待的海菲德狂欢巡游、茵特拉根新春大巡游活动于春节期间强势登陆，各路“新春”元素的花车各显招数，有的载</div><div><br></div><div>着身着炫丽舞服的表演嘉宾，有的载着大大小小的新年礼物、有的载着超高技艺的杂技演绎者，数十种表演风格齐齐加入到大巡游的队伍中。上午伴</div><div><br></div><div>随着悠扬的奏乐一起体验海菲德狂欢巡游与精彩演艺的乐趣，下午从东部华侨城大剧院途径充满欧式风情的茵特拉根小镇，再到咕咕钟舞台，跟随盛</div><div><br></div><div>大的巡游队伍，在人群中尽情狂欢。</div><div><br></div><div><br></div><div><img src=\"/hqc_mp/upload/articleImg/201706/1497491836549104.jpg\" data-filename=\"img\" style=\"line-height: 1.42857; width: 100%;\"></div><div><br></div><div><span style=\"line-height: 1.42857;\">山海迎新第二式：茵特拉根新春湖畔晚会</span></div><div><br></div><div>时间：大年初一至初五20:00</div><div><br></div><div>地点：茶溪谷-茵特拉根湖畔吧水上舞台</div><div><br></div><div>主题：动感演出、随心律动</div><div><br></div><div>&nbsp; &nbsp; 东部华侨城茶溪谷主题景区，在“浪漫、自然、健康、环保”的理念引导下，茶溪谷融合了山水、茶艺、花卉、风情等元素，让来到这里的每个</div><div><br></div><div>人都能从容体味其中“茶禅花竹”、“天人合一”的真谛。来到临水而建的茵特拉根湖畔吧水上舞台，感受倾力打造、集多重艺术表演形式于一体的</div><div><br></div><div>大型新春欢乐表演。各具风格的乐队演唱，神奇梦幻的艺术表演，时尚动感的舞团演出，惊心动魄的杂技演绎、充满悬念节目将接连精彩上演，引爆</div><div><br></div><div>全场!</div><div><br></div><div><img src=\"/hqc_mp/upload/articleImg/201706/1497491869644105.jpg\" data-filename=\"img\" style=\"width: 100%;\"><br></div><div><br></div><div>山海迎新第三式：第二届AIO新春庙会</div><div><br></div><div>时间：春节期间</div><div><br></div><div>地点：黑森林酒店</div><div><br></div><div>主题：美食荟萃、欢乐互动</div><div><br></div><div>&nbsp; &nbsp; 春节期间，黑森林酒店将举办第二届“AIO新春庙会”，充满着新春元素的装饰、装扮席卷整个景区。“年味儿”十足的新春庙会将集合互动游</div><div><br></div><div>戏、趣味体验、特色小吃三大传统庙会活动于一体，让游客感受来自东部华侨城的独特“年味儿”，还有感染力互动性极强的变装表演，琳琅满目的</div><div><br></div><div>特色工艺将在现场精彩呈现，展现中华传统民俗的惊艳魅力。</div><div><br></div><div><img src=\"/hqc_mp/upload/articleImg/201706/1497491881896111.jpg\" data-filename=\"img\" style=\"width: 100%;\"><br></div><div><br></div><div>山海迎新第四式：大华兴寺新春祈福</div><div><br></div><div>时间：元旦、春节期间</div><div><br></div><div>地点：大华兴寺</div><div><br></div><div>主题：祈福迎新、八福迎春</div><div><br></div><div>&nbsp; &nbsp; 位于“观音座莲”山的大华兴寺，座落在云海谷、茶溪谷与大侠谷三谷之间，以唐宋时期古书院建筑风格为主，传播展示中国佛教传统文化。这</div><div><br></div><div>个春节期间，古色古香的大华兴寺将国人传统新春文化氛围渲染到极致，广纳八方来客，共度充满温馨暖意的除夕夜。钟鸣五洲，福泽绵长，八福迎</div><div><br></div><div>春，一路福气。除夕早上6:20开始，大华兴寺将开展早课、佛宝赞、上普供、过堂、接灶、供福灯、传福灯、敲福钟、敬福香、纳福归等活动将接连</div><div><br></div><div>开启，八福迎春，一路福气多多。</div><div><br></div><div><img src=\"/hqc_mp/upload/articleImg/201706/1497491899324101.jpg\" data-filename=\"img\" style=\"width: 100%;\"><br></div><div><span style=\"line-height: 1.42857;\">元旦期间，大华兴寺还将举办地藏经法会，可以一次性将传统佛教文化和传统节庆氛围体验个够。</span></div><div><br></div><div>元旦+春节迎新接力不间断，白天+夜晚狂欢盛宴不停歇，祝福送不停！更多优惠详情请关注深圳东部华侨城官方网站、热线、微博、微信，精彩即将</div><div><br></div><div>上线、敬请期待！</div><div><br></div><div>官方网站：www.octeast.com</div><div><br></div><div>官方热线：86-755-8888-9888</div><div><span style=\"line-height: 1.42857;\"><br></span></div><div><span style=\"line-height: 1.42857;\">官方微博：@深圳东部华侨城微博</span></div>',1496302848,75,1,'深圳东部华侨城','TOM猫',1,1),
(137,'  重磅炸弹，即刻引爆！','upload/articleThumb/201706/1497490994945999.jpg',57,'<div style=\"padding: 0px;\"><h1><b>涧春风尽东来，山海度假始相约。回忆旧的时光，那些儿时穿新衣、吃饺子、放爆竹的快乐过往成为了都市人分外渴望的回忆。眼看具有传统意味的春节狂欢季近在咫尺，幸好，今年东部华侨城“年味儿”有点浓，倾力打造的五大精彩活动，助你寻回关于“年”的记忆！</b></h1><h1><img src=\"/hqc_mp/upload/articleImg/201706/1497491017489666.jpg\" data-filename=\"img\" style=\"width: 100%;\"><b style=\"line-height: 1.42857;\"><br></b></h1><h1><b style=\"line-height: 1.42857;\">山海迎新第一式：海菲德狂欢巡游 、茵特拉根新春大巡游</b></h1><h1><b style=\"line-height: 1.42857;\">时间：大年初一至初五（10:30大侠谷，13:30茶溪谷）</b></h1><h1><b style=\"line-height: 1.42857;\">地点：大侠谷、茶溪谷主题景区</b></h1><h1><b style=\"line-height: 1.42857;\">主题：挥洒激情、喜迎新年</b></h1><h1><b style=\"line-height: 1.42857;\">重磅炸弹，即刻引爆！备受期待的海菲德狂欢巡游、茵特拉根新春大巡游活动于春节期间强势登陆，各路“新春”元素的花车各显招数，有的载着身着炫丽舞服的表演嘉宾，有的载着大大小小的新年礼物、有的载着超高技艺的杂技演绎者，数十种表演风格齐齐加入到大巡游的队伍中。上午伴随着悠扬的奏乐一起体验海菲德狂欢巡游与精彩演艺的乐趣，下午从东部华侨城大剧院途径充满欧式风情的茵特拉根小镇，再到咕咕钟舞台，跟随盛大的巡游队伍，在人群中尽情狂欢。</b></h1><h1><img src=\"/hqc_mp/upload/articleImg/201706/1497491070124u=3004229588,3392370978&amp;fm=23&amp;gp=0.jpg\" data-filename=\"img\" style=\"width: 100%;\"><b style=\"line-height: 1.42857;\"><br></b></h1><h1><b style=\"line-height: 1.42857;\">山海迎新第二式：茵特拉根新春湖畔晚会</b></h1><h1><b style=\"line-height: 1.42857;\">时间：大年初一至初五20:00</b></h1><h1><b style=\"line-height: 1.42857;\">地点：茶溪谷-茵特拉根湖畔吧水上舞台</b></h1><h1><b style=\"line-height: 1.42857;\">主题：动感演出、随心律动</b></h1><h1><b style=\"line-height: 1.42857;\">东部华侨城茶溪谷主题景区，在“浪漫、自然、健康、环保”的理念引导下，茶溪谷融合了山水、茶艺、花卉、风情等元素，让来到这里的每个人都能从容体味其中“茶禅花竹”、“天人合一”的真谛。来到临水而建的茵特拉根湖畔吧水上舞台，感受倾力打造、集多重艺术表演形式于一体的大型新春欢乐表演。各具风格的乐队演唱，神奇梦幻的艺术表演，时尚动感的舞团演出，惊心动魄的杂技演绎、充满悬念节目将接连精彩上演，引爆全场!</b></h1><h1><img src=\"/hqc_mp/upload/articleImg/201706/1497491115505u=2938390193,1002276433&amp;fm=23&amp;gp=0.jpg\" data-filename=\"img\" style=\"width: 100%;\"><b style=\"line-height: 1.42857;\"><br></b></h1><h1><b style=\"line-height: 1.42857;\">山海迎新第三式：第二届AIO新春庙会</b></h1><h1><b style=\"line-height: 1.42857;\">时间：春节期间</b></h1><h1><b style=\"line-height: 1.42857;\">地点：黑森林酒店</b></h1><h1><b style=\"line-height: 1.42857;\">主题：美食荟萃、欢乐互动</b></h1><h1><b style=\"line-height: 1.42857;\">春节期间，黑森林酒店将举办第二届“AIO新春庙会”，充满着新春元素的装饰、装扮席卷整个景区。“年味儿”十足的新春庙会将集合互动游戏、趣味体验、特色小吃三大传统庙会活动于一体，让游客感受来自东部华侨城的独特“年味儿”，还有感染力互动性极强的变装表演，琳琅满目的特色工艺将在现场精彩呈现，展现中华传统民俗的惊艳魅力。</b></h1><h1><img src=\"/hqc_mp/upload/articleImg/201706/1497491162454u=4070830353,989080827&amp;fm=23&amp;gp=0.jpg\" data-filename=\"img\" style=\"width: 100%;\"><b style=\"line-height: 1.42857;\"><br></b></h1><h1><b style=\"line-height: 1.42857;\">山海迎新第四式：大华兴寺新春祈福</b></h1><h1><b style=\"line-height: 1.42857;\">时间：元旦、春节期间</b></h1><h1><b style=\"line-height: 1.42857;\">地点：大华兴寺</b></h1><h1><b style=\"line-height: 1.42857;\">主题：祈福迎新、八福迎春</b></h1><h1><b style=\"line-height: 1.42857;\">位于“观音座莲”山的大华兴寺，座落在云海谷、茶溪谷与大侠谷三谷之间，以唐宋时期古书院建筑风格为主，传播展示中国佛教传统文化。这个春节期间，古色古香的大华兴寺将国人传统新春文化氛围渲染到极致，广纳八方来客，共度充满温馨暖意的除夕夜。钟鸣五洲，福泽绵长，八福迎春，一路福气。除夕早上6:20开始，大华兴寺将开展早课、佛宝赞、上普供、过堂、接灶、供福灯、传福灯、敲福钟、敬福香、纳福归等活动将接连开启，八福迎春，一路福气多多。<span style=\"text-align: center;\">元旦期间，大华兴寺还将举办地藏经法会，可以一次性将传统佛教文化和传统节庆氛围体</span></b></h1><h1><img src=\"/hqc_mp/upload/articleImg/201706/1497491200738ac75323d6b6de243-99bffc6c0cdd19d9-e2621592e550f965fcac4e2944274821.jpg\" data-filename=\"img\" style=\"width: 100%;\"><b style=\"line-height: 1.42857;\"><br></b></h1><h1><b style=\"line-height: 1.42857;\">元旦+春节迎新接力不间断，白天+夜晚狂欢盛宴不停歇，祝福送不停！更多优惠详情请关注深圳东部华侨城官方网站、热线、微博、微信，精彩即将上线、敬请期待！</b></h1><h1><b style=\"line-height: 1.42857;\">官方网站：www.octeast.com</b></h1><h1><b style=\"line-height: 1.42857;\">官方热线：86-755-8888-9888</b></h1><h1><b style=\"line-height: 1.42857;\">官方微博：@深圳东部华侨城微博</b></h1><h1><b style=\"line-height: 1.42857;\">官微信：新鲜上线服务号，订票扫</b></h1></div>',1496302986,59,0,'深圳东部华侨城','米其林',1,1),
(138,'新品推出，快来体验','upload/articleThumb/201706/149749070788399.jpg',57,'<div style=\"padding: 0px;\"><h1><b>涧春风尽东来，山海度假始相约。回忆旧的时光，那些儿时穿新衣、吃饺子、放爆竹的快乐过往成为了都市人分外渴望的回忆。眼看具有传统意味的春节狂欢季近在咫尺，幸好，今年东部华侨城“年味儿”有点浓，倾力打造的五大精彩活动，助你寻回关于“年”的记忆！</b></h1><h1><img src=\"/hqc_mp/upload/articleImg/201706/1497490741403u=3397742334,2022802045&amp;fm=23&amp;gp=0.jpg\" data-filename=\"img\" style=\"width: 100%;\"><b style=\"line-height: 1.42857;\"><br></b></h1><h1><b style=\"line-height: 1.42857;\">山海迎新第一式：海菲德狂欢巡游 、茵特拉根新春大巡游</b></h1><h1><b style=\"line-height: 1.42857;\">时间：大年初一至初五（10:30大侠谷，13:30茶溪谷）</b></h1><h1><b style=\"line-height: 1.42857;\">地点：大侠谷、茶溪谷主题景区</b></h1><h1><b style=\"line-height: 1.42857;\">主题：挥洒激情、喜迎新年</b></h1><h1><b style=\"line-height: 1.42857;\">重磅炸弹，即刻引爆！备受期待的海菲德狂欢巡游、茵特拉根新春大巡游活动于春节期间强势登陆，各路“新春”元素的花车各显招数，有的载着身着炫丽舞服的表演嘉宾，有的载着大大小小的新年礼物、有的载着超高技艺的杂技演绎者，数十种表演风格齐齐加入到大巡游的队伍中。上午伴随着悠扬的奏乐一起体验海菲德狂欢巡游与精彩演艺的乐趣，下午从东部华侨城大剧院途径充满欧式风情的茵特拉根小镇，再到咕咕钟舞台，跟随盛大的巡游队伍，在人群中尽情狂欢。</b></h1><h1><img src=\"/hqc_mp/upload/articleImg/201706/1497490799434u=3621206353,1387851290&amp;fm=23&amp;gp=0.jpg\" data-filename=\"img\" style=\"width: 100%;\"><b style=\"color: inherit; font-family: inherit; line-height: 1.42857;\"><br></b></h1><h1><b style=\"color: inherit; font-family: inherit; line-height: 1.42857;\">山海迎新第二式：茵特拉根新春湖畔晚会</b></h1><h1><b style=\"line-height: 1.42857;\">时间：大年初一至初五20:00</b></h1><h1><b style=\"line-height: 1.42857;\">地点：茶溪谷-茵特拉根湖畔吧水上舞台</b></h1><h1><b style=\"line-height: 1.42857;\">主题：动感演出、随心律动</b></h1><h1><b style=\"line-height: 1.42857;\">东部华侨城茶溪谷主题景区，在“浪漫、自然、健康、环保”的理念引导下，茶溪谷融合了山水、茶艺、花卉、风情等元素，让来到这里的每个人都能从容体味其中“茶禅花竹”、“天人合一”的真谛。来到临水而建的茵特拉根湖畔吧水上舞台，感受倾力打造、集多重艺术表演形式于一体的大型新春欢乐表演。各具风格的乐队演唱，神奇梦幻的艺术表演，时尚动感的舞团演出，惊心动魄的杂技演绎、充满悬念节目将接连精彩上演，引爆全场!</b></h1><h1><img src=\"/hqc_mp/upload/articleImg/201706/1497490852958u=3163461808,704686000&amp;fm=23&amp;gp=0.jpg\" data-filename=\"img\" style=\"width: 100%;\"><span style=\"line-height: 1.42857;\"><br></span></h1><h1><span style=\"line-height: 1.42857;\">山海迎新第