/*Table structure for table `mp_auto_reply` */

DROP TABLE IF EXISTS `mp_auto_reply`;

CREATE TABLE `mp_auto_reply` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `response_type` int(2) DEFAULT NULL COMMENT '响应类型(1关注公众号 2关键词回复)',
  `reply_type` int(2) DEFAULT NULL COMMENT '回复类型(1文字 2图片 3语音 4视频 5图文)',
  `keywords` varchar(255) DEFAULT NULL COMMENT '关键词(多个使用逗号隔开)',
  `reply_text` varchar(520) DEFAULT NULL COMMENT '回复内容',
  `media_id` varchar(520) DEFAULT NULL COMMENT '媒体ID',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=57 DEFAULT CHARSET=utf8 COMMENT='公众号自动回复';

/*Data for the table `mp_auto_reply` */

insert  into `mp_auto_reply`(`id`,`response_type`,`reply_type`,`keywords`,`reply_text`,`media_id`,`create_time`) values 
(5,2,1,'项目,检修,项目开放','您好~大侠谷的游乐项目具体开放时间和检修项目表每天都会在售票窗口的LED显示屏上显示，如果您想知道更多当天项目检修的信息，请拨打我们的大侠谷的咨询电话咨询。电话：25032247，感谢您的支持~祝您生活愉快~~/:showlove	   		','',1495162457),
(6,2,1,'攻略,游玩攻略','游玩攻略请在菜单栏的“玩转景区”里面自助查询，如果您现在还看不到菜单栏，请先退出此页面，再次进入时就能看到啦/:,@-D','',NULL),
(7,2,1,'景区地图,地图','您好！景区电子版地图还在升级调整中，请在检票入口处领取纸质版地图游园，感谢您的支持，祝您生活愉快~','',1495162423),
(8,2,1,'电瓶车','大侠谷电瓶车：单独购票：30元/位，可乘坐3次\r\n茶溪谷电瓶车：单独购票：30元/位，可乘坐6次\r\n有任何疑问欢迎拨打我们的咨询电话：\r\n大侠谷：25032247\r\n茶溪谷：25031868\r\n祝您新春快乐~游玩愉快~','',1495162436),
(9,2,1,'请问,时间,你好','您好！大侠谷春节期间开园时间9:00-18:00；茶溪谷春节期间开园时间：9:00-18:00.\r\n如果您还有其他疑问欢迎拨打我们的咨询电话\r\n大侠谷：25032247\r\n茶溪谷：25031868\r\n酒店群电话：4008601388\r\n祝您新年快乐~鸡年大吉~','',1495162457),
(10,2,1,'老人,长者,老年人,老人票','您好！65-69岁的长者享受景区门票半价优惠，70岁以上的长者享受免票优惠','',1495162457),
(11,2,1,'招聘,招,关于招聘','您好！非常感谢您对 东部华侨城旅游度假区 的关注与支持！关于我司招聘岗位详情，请关注前程无忧网或者猎聘网。东部华侨城 祝您生活愉快，谢谢！\r\n您好！详情可拨打咨询电话：0755 22350704，联系人：容小姐。祝您生活愉快，谢谢！','',1495162457),
(12,2,1,'寄存,寄存背包','您好!景区有提供寄存商品的服务，大侠谷请在15号售票窗口寄存，茶溪谷请在售票窗口旁的寄存窗口寄存。\r\n寄存收费标准：按件收费，10元/件（背包等独立小件）；20元/件（行李箱以上或者物品重量超出5公斤、物品长宽高三边之和超出113cm）','',1495162457),
(13,2,1,'跨年,狂欢,倒数,荧光跑','您好！元旦期间景区没有倒数活动，但是31号晚19:30——21:30茶溪谷有个跨年狂欢夜的活动，想要参加活动的游客必须在31号下午五点钟之前检票入园，五点半过后就不能再入园了。白天入园的游客可以在景区逗留到七点半晚会开始。晚会九点半结束，结束后游客可以乘坐景区的909巴士下山。909巴士的最晚一班车是晚上的11点。（元旦期间温度会比较低，请做好御寒保暖工作）感谢您的支持，祝您生活愉快！\r\n另：因为元旦跨年荧光跑活动需要提前报名，26号就截止报名了，没能报名的游客很抱歉不能参加哦~','',1495162741),
(14,2,1,'圣诞,圣诞节','您好！圣诞节景区这边暂时没有活动安排，更多景区相关活动请密切关注我们的微信服务号推文，感谢您的支持，祝您生活愉快~','',1495162457),
(15,2,1,'温泉','您好！茵特拉根温泉门票价格如下：酒店住店客人120元/位；散客价198元/位。如需预约及预订请拨打温泉咨询电话：0755 28886666，祝您生活愉快，谢谢！','',1495162786),
(16,2,1,'wifi,WIFI,联网,网络,上网,无线,Wi-Fi密码,Wi-Fi,CMCC－OCTEast,WLAN','您好！首先您要连接我们景区的免费WiFi：CMCC-OCTEast(Free)，连接成功后随便打开一个网页会跳出一个网页认证页面，点击屏幕左下角的微信图标关注公众号并上网（勾选已阅读并同意《免责声明》），如果你点击之后没有弹跳出一个连接成功的页面，请关闭这个页面然后再重复一次上面的操作就能成功上网，感谢您的支持，祝您生活愉快！','',1495162846),
(17,2,1,'在线购票,购票','您好！如需购票请点击屏幕左下方的“在线购票”，进入微商城自助下单，感谢您的支持，祝您生活愉快！如果您现在看不到菜单栏，请先退出此页面，再次进入时就能看到啦~/:,@-D','',1495162892),
(18,2,1,'酒店,房车,瀑布,黑森林,茵特拉根,咖酷,菩提宾舍','您好！酒店预订可以拨打我们的预订电话：4008601388，您也可以选择上我们的官网，或者是同程、美团、携程等电商APP上自助订房，感谢您的支持，祝您生活愉快！','',1495162943),
(19,2,1,'天禅','您好！天禅门票¥150元/位  ；茶溪谷+天禅套票：¥250元/位。天禅演艺每天下午三点在东部华侨城大剧院华丽上映。咨询电话：0755-88889888 谢谢！天禅1.2米以下的儿童免票，长者没有半价或者免票优惠！','',1495162958),
(20,2,1,'改期,改时间','您好！门票一经售出都是可退不可改的，如果跟您的时间有冲突的话您可以先退后重新买，退票一经受理，退款要在七个工作日后才能返回！您可以自己线上操作退票，也可以拨打我们的客服电话：4000691111，祝您生活愉快！谢谢！','',1495162978),
(21,2,1,'年卡,年票,续卡,续年卡,卡','您好！东部华侨城年卡种类及门市价格：平日卡（488元）、学生卡（488元）、单人卡（788元）、亲子卡（1188元）、情侣卡（1288元）、家庭卡（1488元）。\r\n东部华侨城年卡续卡及价格：平日卡（230元）、学生卡（230元）、单人卡（330元）、亲子卡（430元）、情侣卡（530元）、家庭卡（630元）\r\n详情可拨打咨询电话：86-0755-25032248或86-0755-25032247或86- 0755-88889888. 祝您生活愉快，谢谢！','',1495163021),
(22,2,1,'票价,收费,要钱吗,茶溪谷门票,大侠谷门票,两谷一日游门票,两谷两日游门票','您好！景区大侠谷门票：200元/张；茶溪谷：180元/张；两谷一日游：300元/张；两谷两日游：350元/张。微信在线购票和电商平台线上购票可享受折扣。持门票入园景区内大部分游乐设施项目都是免费的，但园内交通以及大型演艺如电瓶车、新版《天禅》演出等须另外收费（电瓶车：30元/位；天禅：150元/位），咨询电话：0755 8888 9888 ，祝您生活愉快，谢谢！','',1495163066),
(23,2,1,'身高,儿童,一米,1米,小孩,儿童票','您好！身高1.2米以下（含1.2米）的儿童免票入园；身高1.2米—1.5米（含1.5米）的儿童半价优惠；1.5米以上的儿童按成人票价购票。\r\n注：儿童票是在原价门票的基础上半价优惠；特价优惠票没有儿童价，如果您想买儿童票请在售票窗口现场购买，谢谢！','',1495162457),
(24,2,1,'导游,导游证','您好！凭导游证IC卡可以直接到景区入口处核实身份，免费入园。','',1495163186),
(25,2,1,'生日,生日优惠','您好！我们目前暂时没有生日当天优惠购票的政策，更多相关的优惠活动请密切关注我们的东部华侨城的微信公众号！咨询电话：075588889888，祝您生活愉快，谢谢！','',1495163207),
(26,2,1,'客服,人工,电话','您好！如果您需要咨询景区相关资讯可拨打咨询电话：0755 8888 9888，如果您微信购票有什么疑问或者需要退票、退订，请拨打客服电话：4000691111。祝您生活愉快！谢谢！','',1495163229),
(27,2,1,'双谷,一日游,两谷','您好！双谷是指大侠谷+茶溪谷这两个谷，游客持一日游门票可以在一天之内游玩大侠谷和茶溪谷。景区内大部分游乐设施项目都是免费的，但园内交通以及大型演艺如电瓶车、新版《天禅》演出等须另外收费，咨询电话：0755 8888 9888 ，祝您生活愉快，谢谢！','',1495163251),
(28,2,1,'怎么去茶溪谷,大侠谷去茶溪谷,去茶溪谷,大侠谷到茶溪谷','您好！从大侠谷云海索道、丛林缆车二号线、森林小火车等交通项目可抵达茶溪谷，温馨提示，游客须持两谷门票才可由大侠谷顺利抵达茶溪谷噢。另外，如果不经过大侠谷园区的话，从东部华侨城东大门附近的909总站，搭乘909专线巴士也可以到达茶溪谷景区的。 更多度假资讯以及优惠活动请持续关注深圳东部华侨城微信公众号: octeast_sz，祝您生活愉快，谢谢！','',1495163282),
(29,2,1,'退票,退款,退订,退','您好！如需退订、退款，请联系客服电话：4000691111进行受理，也可以在“在线购票“微商城的“个人中心”里面主动发起退订。\r\n退款受理3-7个工作日后才能到账。退款金额会原路返还您付款的银行卡/微信账号/支付宝，请注意消息提醒。有什么疑问的话欢迎拨打我们的客服电话咨询！\r\n如果您购买的是我们“花漾女人节”的活动票种的话，此部分票种是不退不改的，请您在购买前仔细看商品预订详情！\r\n感谢您的支持祝您生活愉快~~~','',1495163307),
(49,1,1,'','欢迎您关注深圳东部华侨城官方微信！\r\n\r\n如需购买优惠门票请点击屏幕左下方的“在线购票”按钮。\r\n\r\n更多有关景区游玩攻略及活动详情请点击下端菜单！\r\n\r\n祝您游玩愉快~~[嘴唇][嘴唇][嘴唇]','',1495420499),
(55,2,2,'','','',1495533824),
(56,2,1,'','','',1495589360);

/*Table structure for table `mp_banner` */

DROP TABLE IF EXISTS `mp_banner`;

CREATE TABLE `mp_banner` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `title` varchar(250) DEFAULT NULL COMMENT '横幅标题',
  `thumb_url` varchar(1024) DEFAULT NULL COMMENT '横幅图片缩略图',
  `url` varchar(1024) DEFAULT NULL COMMENT '横幅图片链接地址',
  `status` int(2) DEFAULT '1' COMMENT '状态(1启用 2禁用)',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COMMENT='横幅图片';

