/*Table structure for table `mp_cash_coupon` */

DROP TABLE IF EXISTS `mp_cash_coupon`;

CREATE TABLE `mp_cash_coupon` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cash_coupon_name` varchar(128) DEFAULT NULL COMMENT '代金券名称',
  `integral` int(11) DEFAULT NULL COMMENT '所需积分',
  `face_value` decimal(10,2) DEFAULT NULL COMMENT '代金券面值',
  `about` text COMMENT '代金券详细介绍',
  `max_exchange` int(11) DEFAULT NULL COMMENT '最大允许兑换数量',
  `day_exchange` int(11) DEFAULT NULL COMMENT '每日允许兑换数量',
  `status` int(11) DEFAULT NULL COMMENT '1、上架  2、下架',
  `cash_thumb` varchar(1024) DEFAULT NULL COMMENT '缩略图',
  `repertory` int(255) DEFAULT NULL COMMENT '库存',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COMMENT='代金券表';

