/*Table structure for table `mp_goods` */

DROP TABLE IF EXISTS `mp_goods`;

CREATE TABLE `mp_goods` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `goods_name` varchar(1024) DEFAULT NULL COMMENT '商品名称',
  `goods_thumb` varchar(1024) DEFAULT NULL COMMENT '商品缩略图',
  `goods_no` varchar(255) DEFAULT NULL COMMENT '商品编号，识别商品的唯一编号，暂时不用',
  `category_id` int(11) DEFAULT NULL COMMENT '商品分类，关联商品分类表',
  `days_limit` int(11) DEFAULT NULL COMMENT '最多可以订多少天之后的票，0不限制',
  `end_time` int(11) DEFAULT NULL COMMENT '门票有效日期到哪天，0代表不限制',
  `repertory` int(11) DEFAULT NULL COMMENT '当前库存',
  `market_price` decimal(10,2) DEFAULT '0.00' COMMENT '市场价',
  `price` decimal(10,2) DEFAULT '0.00' COMMENT '商品价格',
  `integral` int(11) DEFAULT '0' COMMENT '所需积分',
  `about` text COMMENT '商品介绍',
  `notice` text COMMENT '预定须知',
  `max_exchange` int(11) DEFAULT '0' COMMENT '最大允许兑换数量，0代表不限制',
  `day_exchange` int(11) DEFAULT '0' COMMENT '每日最大兑换数量，0代表不限制',
  `status` int(11) DEFAULT NULL COMMENT '1、上架  2、下架',
  `user_id` bigint(20) DEFAULT NULL COMMENT '关联系统用户表(数据操作用户)',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8 COMMENT='商品信息表';

/*Data for the table `mp_goods` */

