/*Table structure for table `mp_goods_category` */

DROP TABLE IF EXISTS `mp_goods_category`;

CREATE TABLE `mp_goods_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `category_name` varchar(255) DEFAULT NULL COMMENT '商品分类名称',
  `parent_id` int(11) DEFAULT NULL COMMENT '父ID，暂时不使用',
  `about` varchar(1024) DEFAULT NULL COMMENT '描述',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8 COMMENT='商品分类表';

