/*Table structure for table `mp_goods_exchange_record` */

DROP TABLE IF EXISTS `mp_goods_exchange_record`;

CREATE TABLE `mp_goods_exchange_record` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `order_no` varchar(64) DEFAULT NULL COMMENT '订单号',
  `goods_id` int(11) DEFAULT NULL COMMENT '商品id（关联表mp_goods字段id）',
  `member_id` bigint(20) DEFAULT NULL COMMENT '会员id（关联表mp_member字段id）',
  `integral` int(11) DEFAULT NULL COMMENT '兑换所用积分',
  `goods_nos` text COMMENT '商品编号（兑换的商品编号，多个编号逗号隔开）【暂时不使用此字段】',
  `exchange_count` int(11) DEFAULT NULL COMMENT '兑换数量',
  `exchange_code` varchar(1024) DEFAULT NULL COMMENT '兑换码(兑换成功之后生成的兑换码，用户使用的时候提供的票据）',
  `person_phone` varchar(512) DEFAULT NULL COMMENT '兑换人手机号码',
  `use_time` int(11) DEFAULT NULL COMMENT '使用日期(用于兑换门票的时候，指定的使用日期）',
  `use_status` int(11) DEFAULT NULL COMMENT '使用状态（1、已使用 0、未使用）【如果兑换的是门票，则存在使用状态】',
  `create_time` int(11) DEFAULT NULL COMMENT '兑换时间',
  `goods_type` varchar(255) DEFAULT NULL COMMENT '标记为普通商品或代金卷 1代金卷，2普通商品（门票）',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=195 DEFAULT CHARSET=utf8 COMMENT='积分商品兑换记录表';

