/*Table structure for table `mp_member` */

DROP TABLE IF EXISTS `mp_member`;

CREATE TABLE `mp_member` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `phone` varchar(20) DEFAULT NULL COMMENT '手机号码',
  `card_no` varchar(50) DEFAULT NULL COMMENT '会员卡号',
  `true_name` varchar(50) DEFAULT NULL COMMENT '会员姓名',
  `nick_name` varchar(255) DEFAULT NULL COMMENT '微信昵称',
  `open_id` varchar(64) DEFAULT NULL COMMENT '公众号标识openid',
  `unionid` varchar(64) DEFAULT NULL COMMENT '只有在用户将公众号绑定到微信开放平台帐号后，才会出现该字段',
  `avatar` varchar(255) DEFAULT NULL COMMENT '会员头像',
  `birthday` int(11) DEFAULT NULL COMMENT '会员生日',
  `password` varchar(64) DEFAULT NULL,
  `last_year_integral` int(11) DEFAULT NULL COMMENT '上一年度积分',
  `current_year_integral` int(11) DEFAULT NULL COMMENT '本年度积分',
  `integral` int(11) DEFAULT NULL COMMENT '用户当前总积分',
  `vip_level` int(11) DEFAULT NULL COMMENT '会员级别，关联会员级别表ID',
  `create_time` int(11) DEFAULT NULL COMMENT '会员注册时间',
  `update_time` int(11) DEFAULT NULL COMMENT '信息更新时间',
  `status` int(11) DEFAULT '1' COMMENT '1、启用 2、禁用',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=100 DEFAULT CHARSET=utf8 COMMENT='会员表';

