/*Table structure for table `mp_order_records` */

DROP TABLE IF EXISTS `mp_order_records`;

CREATE TABLE `mp_order_records` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `type` int(2) DEFAULT '1' COMMENT '订单类型(1购票订单 2积分商城门票订单 3积分商城代金卷订单) ',
  `ticket_id` int(11) DEFAULT NULL COMMENT '关联门票id(对应购票订单)',
  `goods_id` int(11) DEFAULT NULL COMMENT '关联商品表ID(对应积分商城门票订单)',
  `coupon_id` int(11) DEFAULT NULL COMMENT '关联代金券ID(对应积分商城代金卷订单)',
  `item_code` varchar(64) DEFAULT NULL COMMENT '取票凭证码（通过短信告诉用户）',
  `item_count` int(11) DEFAULT NULL COMMENT '兑换数量（通过短信告诉用户）',
  `member_id` bigint(20) DEFAULT NULL COMMENT '关联会员表',
  `phone` varchar(20) DEFAULT NULL COMMENT '接收短信的手机号码',
  `order_no` varchar(64) DEFAULT NULL COMMENT '订单号',
  `create_time` int(11) DEFAULT NULL COMMENT '订单创建时间',
  `update_time` int(11) DEFAULT NULL COMMENT '订单更新时间',
  `price` decimal(10,2) DEFAULT NULL COMMENT '订单消费金额',
  `integral` int(255) DEFAULT NULL COMMENT '订单消耗积分',
  `status` int(11) DEFAULT NULL COMMENT '0、已下单  1、已兑换  2、已取票',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=201 DEFAULT CHARSET=utf8 COMMENT='商品订单记录表';

