/*Table structure for table `mp_parking_charge` */

DROP TABLE IF EXISTS `mp_parking_charge`;

CREATE TABLE `mp_parking_charge` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `plate` varchar(50) DEFAULT NULL COMMENT '车牌号',
  `order_no` varchar(64) DEFAULT NULL COMMENT '订单号',
  `bill_no` varchar(64) DEFAULT NULL COMMENT '车场回馈的消费单号',
  `member_id` bigint(20) DEFAULT NULL COMMENT '会员id，关联表（mp_member）字段id',
  `total_fee` decimal(10,2) DEFAULT '0.00' COMMENT '停车消费金额',
  `start_time` int(11) DEFAULT NULL COMMENT '车场驻留开始时间',
  `end_time` int(11) DEFAULT NULL COMMENT '车场驻留结束时间',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `update_time` int(11) DEFAULT NULL COMMENT '最后修改时间',
  `status` int(11) DEFAULT '1' COMMENT '1=正常 2=未支付成功 3=未支付 6=退单',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=88 DEFAULT CHARSET=utf8 COMMENT='停车收费记录表';

