/*Table structure for table `mp_project` */

DROP TABLE IF EXISTS `mp_project`;

CREATE TABLE `mp_project` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `project_name` varchar(128) DEFAULT NULL COMMENT '项目名称',
  `about` varchar(1024) DEFAULT NULL COMMENT '项目简介',
  `scenery_id` int(11) DEFAULT NULL COMMENT '所属景点id（关联表mp_scenery_spot字段id）',
  `category_id` int(11) DEFAULT NULL COMMENT '项目类型id（关联表mp_project_category字段id）',
  `thumb_url` varchar(1024) DEFAULT NULL COMMENT '项目缩略图地址',
  `lat` varchar(64) DEFAULT NULL COMMENT '纬度',
  `lng` varchar(64) DEFAULT NULL COMMENT '经度',
  `project_content` text COMMENT '项目详细介绍',
  `status` int(11) DEFAULT NULL COMMENT '项目状态：1、启用 0、停用',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=220 DEFAULT CHARSET=utf8 COMMENT='景点项目表';

/*Data for the table `mp_project` */

insert  into `mp_project`(`id`,`project_name`,`about`,`scenery_id`,`category_id`,`thumb_url`,`lat`,`lng`,`project_content`,`status`) values 
(34,'室外水公园','现在暂未开放，它主要成为大型演出场地，中心区有110（11X10）平方米的上下升降舞台，可以将演员及道具从浪池下方升至水面以上。并可以形成高空跳水池。为配合演出结合造浪泳池设置了2900立方的高低位水池各一个，使作为主题场景的造浪池中的水可以在25秒内迅速的排空，并可在2分钟内迅速的冲回。可营造出“沧海桑田”“覆水能收”等大场面的主题演出效果。结合现场地形，设置了可同时容纳8100人的固定看台区。',39,413,'upload/scenicSpotThumb/201706/1496729293750out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<div>现在暂未开放，它主要成为大型演出场地，中心区有110（11X10）平方米的上下升降舞台，可以将演员及道具从浪池下方升至水面以上。并可以形成高空跳水池。为配合演出结合造浪泳池设置了2900立方的高低位水池各一个，使作为主题场景的造浪池中的<span style=\"line-height: 1.42857;\">水可以在25秒内迅速的排空，并可在2分钟内迅速的冲回。可营造出“沧海桑田”“覆水能收”等大场面的主题演出效果。结合现场地形，设置了可同时容纳8100人的固定看台区。</span></div>',0),
(35,'室内水公园','占地面积约5500平方米，它的外观呈半球型，主题长128米，宽度53米，高22米。水公园的外部结构采用了上千吨轻钢网壳结构，并且设置了1900平米的巨大开启扇，可开合机构使中间50米跨度可以沿扇弧形轨道平开。开启后水公园室内与水公园室外相互渗透连接，环境融为一体。在我们的水公园内设置了多种游乐设备项目，儿童戏水区、海盗船、室内漂流河、滑板冲浪、大碗、U型滑道、室外滑道。',39,412,'upload/scenicSpotThumb/201706/1496909455945QQ截图20170602092519.png','22.606404','114.305746','<div>占地面积约5500平方米，它的外观呈半球型，主题长128米，宽度53米，高22米。水公园的外部结构采用了上千吨轻钢网壳结构，并且设置了1900平米的巨大开启扇，可开合机构使中间50米跨度可以沿扇弧形轨道平开。开启后水公园室内与水公园室外相互渗<span style=\"line-height: 1.42857;\">透连接，环境融为一体。在我们的水公园内设置了多种游乐设备项目，儿童戏水区、海盗船、室内漂流河、滑板冲浪、大碗、U型滑道、室外滑道。</span></div><div><div><span style=\"line-height: 17.1428px;\">儿童戏水区：儿童游乐互动参与性项目，有3条大滑道、2个大水桶。</span></div><div><span style=\"line-height: 17.1428px;\">海盗船：木质船体，海盗模型栩栩如生，可将美好的记忆留在照片中。</span></div><div><span style=\"line-height: 17.1428px;\">室内漂流河：3-4个造浪室，多种浪型，浪最高1.2米。</span></div><div><span style=\"line-height: 17.1428px;\">滑板冲浪：直立或者趴式冲浪设备，可以举行冲浪表演或者竞赛。滑板冲浪区可以让人体验到海上冲浪的快感和刺激。</span></div><div><span style=\"line-height: 17.1428px;\">大碗：由美国引进，大碗直径8米，滑道长48米。</span></div><div style=\"\"><span style=\"line-height: 17.1428px;\">U型滑道：由美国引进，滑道宽4米，滑下速度可达到2米/秒。</span></div></div>',1),
(39,'四季花田','位于茶溪谷正门口，占地约为4.5万平方米，由168条红、黄、紫、橙、绿等色彩缤纷的植物彩带组成，其中花卉品种20余种。鸟瞰花田，首尾相连，蜿蜒起伏，绚丽多彩，给人强烈的视觉冲击，亲临花田泥土芬芳，条条彩带伸向天边，令人遐想无限。',69,412,'upload/scenicSpotThumb/201706/1497496570552四季花田01.jpg','22.631656','114.281865','<p><span style=\"line-height: 1.42857;\">位于茶溪谷正门口，占地约为4.5万平方米，由168条红、黄、紫、橙、绿等色彩缤纷的植物彩带组成，其中花卉品种20余种。鸟瞰花田，首尾相连，蜿蜒起伏，绚丽多彩，给人强烈的视觉冲击，亲临花田泥土芬芳，条条彩带伸向天边，令人遐想无限。</span></p><p><br></p>',1),
(40,'艺术牛','也叫彩绘牛，最早是由一个瑞士艺术家帕斯卡尔·克纳普首创，这种彩绘牛的设计理念是将田园引入城市。自1998年首次公开展示至今，彩绘牛以其可爱的造型、缤纷的色彩风靡一时。彩绘牛采用和真牛的一比一大小的模型，以模型为“画板”任意在上面进行彩绘。另外，在国外的许多城市每年都要举行一次彩绘牛活动。彩绘牛活动受益于Jimmy基金，该机构是英格兰最著名的大型癌症治疗中心，活动的收益将作为癌症的研究与治疗基金。彩绘牛活动目前已经发展成为世界上最大、最流行的公共艺术活动之一，被誉为公共艺术的奥林匹克。在过去的6年时间里，彩绘牛已经走遍了世界的大部分城市。彩绘牛一直不断的壮大，不仅仅是扩大规模，在整体质量和创造性上也不断的追求着进步。这项活动最明显的特点就是把艺术从博物馆里搬到街上来，让不同年龄的人们都可以欣赏得到，并参与其中。',69,412,'upload/scenicSpotThumb/201706/1496714871323out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">也叫彩绘牛，最早是由一个瑞士艺术家帕斯卡尔·克纳普首创，这种彩绘牛的设计理念是将田园引入城市。自1998年首次公开展示至今，彩绘牛以其可爱的造型、缤纷的色彩风靡一时。彩绘牛采用和真牛的一比一大小的模型，以模型为“画板”任意在上面进行彩绘。另外，在国外的许多城市每年都要举行一次彩绘牛活动。彩绘牛活动受益于Jimmy基金，该机构是英格兰最著名的大型癌症治疗中心，活动的收益将作为癌症的研究与治疗基金。彩绘牛活动目前已经发展成为世界上最大、最流行的公共艺术活动之一，被誉为公共艺术的奥林匹克。在过去的6年时间里，彩绘牛已经走遍了世界的大部分城市。彩绘牛一直不断的壮大，不仅仅是扩大规模，在整体质量和创造性上也不断的追求着进步。这项活动最明显的特点就是把艺术从博物馆里搬到街上来，让不同年龄的人们都可以欣赏得到，并参与其中。</span></p>',1),
(41,'峡湾栈道','峡湾栈道全长600米，共247级台阶，步行10分钟左右，栈道中有108级台阶是由进口的芬兰木材建成，具有防滑、防潮、防腐的作用。',40,412,'upload/scenicSpotThumb/201706/1496653137735峡湾栈道01.jpg','22.606662','114.305176','峡湾栈道全长600米，共247级台阶，步行10分钟左右，栈道中有108级台阶是由进口的芬兰木材建成，具有防滑、防潮、防腐的作用。',1),
(42,'信息湿地','信息湿地占地约11万平方米，是一种介于陆地和水体之间过渡得生态系统。具有生态净化功能，维护生态安全、保护动植物多样性的作用，信息湿地采用国内较先进的三级水质处理技术，（一、石坝：在一定长宽高的洼地中以沙石作为填料形成填料床，让污水仔里面曲折的流动从而达到净化水质的效果。二、生物浮导技术：人工构建生物床，在床体上种植成活率高处理性能好的植物，利用植物的生态系统对水质进行过滤。三、水生植物种植术：直接在靠水岸边的水中种植成活率高处理性能好的水生植物，利用植物庞大的根系对水质进行过滤。）为三洲田水库提供了天然生态过滤网。',69,412,'upload/scenicSpotThumb/201706/1496714898093out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<div><span style=\"line-height: 1.42857;\">信息湿地占地约11万平方米，是一种介于陆地和水体之间过渡得生态系统。具有生态净化功能，维护生态安全、保护动植物多样性的作用，信息湿地采用国内较先进的三级水质处理技术，（一、石坝：在一定长宽高的洼地中以沙石作为填料形成填料床，让污水仔里面曲折的流动从而达到净化水质的效果。二、生物浮导技术：人工构建生物床，在床体上种植成活率高处理性能好的植物，利用植物的生态系统对水质进行过滤。三、水生植物种植术：直接在靠水岸边的水中种植成活率高处理性能好的水生植物，利用植物庞大的根系对水质进行过滤。）为三洲田水库提供了天然生态过滤网。</span></div>',1),
(43,'湿地栈桥','在信息湿地和山洲田水库的水面上铺有一条木制栈桥，桥临水面、蜿蜒曲折，行走在栈桥上，给人一种恬静的气氛。',69,412,'upload/scenicSpotThumb/201706/1496714990715湿地栈桥.jpg','22.6315','114.282441','<p><span style=\"line-height: 1.42857;\">在信息湿地和山洲田水库的水面上铺有一条木制栈桥，桥临水面、蜿蜒曲折，行走在栈桥上，给人一种恬静的气氛。</span></p><p><br></p>',1),
(44,'花钟教堂','矗立在四季花田和茵特拉根小镇之间的小山坡上，是湿地花园的标志性建筑物。教堂由芬兰原木原装顶级木材组合而成，与茵特拉根小镇交相辉映。教堂外的花钟将花卉与钟表相结合，钟面直径达15米（时针长度：5.28米、分针长度：6.5米、秒针长度：6.7米），12个数字和指针都是雕花铝板制成，机电一体的装置保证了指针的准确移动，让时间的流逝也花香四溢。教堂外的观景平台是最佳观景点之一，可以俯瞰整个茵特拉根小镇及湿地花园绚丽的花海景观。\n',69,412,'upload/scenicSpotThumb/201706/1496715016062花钟教堂.jpg','22.631894','114.280538','<p><span style=\"line-height: 1.42857;\">矗立在四季花田和茵特拉根小镇之间的小山坡上，是湿地花园的标志性建筑物。教堂由芬兰原木原装顶级木材组合而成，与茵特拉根小镇交相辉映。教堂外的花钟将花卉与钟表相结合，钟面直径达15米（时针长度：5.28米、分针长度：6.5米、秒针长度：6.7米），12个数字和指针都是雕花铝板制成，机电一体的装置保证了指针的准确移动，让时间的流逝也花香四溢。教堂外的观景平台是最佳观景点之一，可以俯瞰整个茵特拉根小镇及湿地花园绚丽的花海景观。</span></p><p><br></p>',1),
(45,'生命之源','在信息湿地和三洲田水库之间的红色拱形建筑被称为“生命之源”，是抽象艺术，旨意是告诉我们生命源于水，所以我们要节约用水，保护水源。',69,412,'upload/scenicSpotThumb/201706/1496715048205out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">在信息湿地和三洲田水库之间的红色拱形建筑被称为“生命之源”，是抽象艺术，旨意是告诉我们生命源于水，所以我们要节约用水，保护水源。</span></p>',1),
(46,'红色方舟','是一个双层半下沉式的廊桥建筑，它的建造寓意源于《圣经》中的“诺亚方舟”的故事，诺亚方舟是世风日下的人类生存的希望，而红色方舟是在警示人们热爱生态，保护环境是人类生存的基本条件。在红色方舟的一层有展示茶溪谷特色的生态景观图片、湿地信息图片，以及从南美亚马逊引进的大型淡水鱼海象鱼。',69,412,'upload/scenicSpotThumb/201706/1496715072787out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">是一个双层半下沉式的廊桥建筑，它的建造寓意源于《圣经》中的“诺亚方舟”的故事，诺亚方舟是世风日下的人类生存的希望，而红色方舟是在警示人们热爱生态，保护环境是人类生存的基本条件。在红色方舟的一层有展示茶溪谷特色的生态景观图片、湿地信息图片，以及从南美亚马逊引进的大型淡水鱼海象鱼。</span></p>',1),
(47,'地球血管','这组红色钢管式构架被称为“地球血管”，在这里它主要是寓意着地球肌体和血脉，我们生存的地球是一个庞大的生命有机体系，它为我们提供了很多生存元素，如森林、大地、水、空气等等。我们创意地球血管，其旨意是提醒人类要保护地球，同时也体现了地球生命的生生不息。地球血管上方还安装了太阳能电板，直接跟景区形成了并网，供应景区的用电。\n',69,412,'upload/scenicSpotThumb/201706/1496715094162out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">这组红色钢管式构架被称为“地球血管”，在这里它主要是寓意着地球肌体和血脉，我们生存的地球是一个庞大的生命有机体系，它为我们提供了很多生存元素，如森林、大地、水、空气等等。我们创意地球血管，其旨意是提醒人类要保护地球，同时也体现了地球生命的生生不息。地球血管上方还安装了太阳能电板，直接跟景区形成了并网，供应景区的用电。</span></p><p><br></p>',1),
(48,'台地花园','由七块花坛三角区与三个商铺组成，花坛三角区每个区域随着季节的变化种植不同的花朵。台地花园全程配备6组扶梯，为我们的游览提供便利。',40,412,'upload/scenicSpotThumb/201706/1496653877085台地花园 (2).JPG','22.606661','114.305675','由七块花坛三角区与三个商铺组成，花坛三角区每个区域随着季节的变化种植不同的花朵。台地花园全程配备6组扶梯，为我们的游览提供便利。',1),
(49,'花桥广场','花桥广场是整个湿地花园有的主轴线，贯穿“生态之门”至“地球血管”的整个空间。广场的构造向我们展示了艺术与生态魅力，使整个园区最具想象力的部分，整个广场从高空俯瞰形似一把撑开的花伞。',69,412,'upload/scenicSpotThumb/201706/1496715149618out2014.10.17景区示意图（中文）-正面(1).jpg','22.538809','113.984715','<p><span style=\"line-height: 1.42857;\">花桥广场是整个湿地花园有的主轴线，贯穿“生态之门”至“地球血管”的整个空间。广场的构造向我们展示了艺术与生态魅力，使整个园区最具想象力的部分，整个广场从高空俯瞰形似一把撑开的花伞。</span></p>',1),
(50,'旋转花车','欧式造型的旋转花车营造出一个温馨、浪漫的空间。花车轨道长230米，圆型花车的直径2米。通过轨道上的接触器控制花车左右转动，又小弧度的转向。车外布满五颜六色的花卉，花随车动，影随人仪，迎面扑来阵阵花香，让游客在不同的视觉欣赏色彩缤纷的奇花异卉，在花海中体验蜿蜒穿梭的乐趣。',69,412,'upload/scenicSpotThumb/201706/1496715172817旋转花车01.JPG','22.632631','114.280477','<p>欧式造型的旋转花车营造出一个温馨、浪漫的空间。花车轨道长230米，圆型花车的直径2米。通过轨道上的接触器控制花车左右转动，又小弧度的转向。车外布满五颜六色的花卉，花随车动，影随人仪，迎面扑来阵阵花香，让游客在不同的视觉欣赏色彩缤纷的奇花异卉，在花海中体验蜿蜒穿梭的乐趣。</p><p><br></p>',1),
(51,'大侠谷瀑布','瀑布宽300米，地形高差70米，水流落差42米，蔚然壮观。它是我们东部华侨城的守护神，也是目前世界最长、落差最大的滨海人造瀑布。更为称奇的是在我们的瀑布里面有家以“水”为主题元素的瀑布酒店。',40,412,'upload/scenicSpotThumb/201706/1496654388125大侠谷瀑布.jpg','22.606513','114.303614','瀑布宽300米，地形高差70米，水流落差42米，蔚然壮观。它是我们东部华侨城的守护神，也是目前世界最长、落差最大的滨海人造瀑布。更为称奇的是在我们的瀑布里面有家以“水”为主题元素的瀑布酒店。',1),
(52,'天幕','小镇上空覆盖着一条天幕，长120米，宽18米，投影达12米，天幕技术非常先进，不但可以分辨播出普通影视节目，还可以作为整理播出特制的节目信号，为小镇增添浪漫的风情。',41,412,'upload/scenicSpotThumb/201706/1496654655109海布伦宫01.jpg','22.609302','114.304427','小镇上空覆盖着一条天幕，长120米，宽18米，投影达12米，天幕技术非常先进，不但可以分辨播出普通影视节目，还可以作为整理播出特制的节目信号，为小镇增添浪漫的风情。',1),
(53,'树体彩绘','在四季花桥广场，可以看到许多大树上都画有动物头像，这种大自然装置艺术叫做“树体彩绘”，在树干上利用其自然纹理，如树枝、树瘤、树洞等原有条件来创作。用颜料绘制成各种创意图案，用其它材料制作出附属的装饰。这样可增加树的趣味及艺术性供人们欣赏。',69,412,'upload/scenicSpotThumb/201706/1496715194876out2014.10.17景区示意图（中文）-正面(1).jpg','22.538809','113.984715','<p><span style=\"line-height: 1.42857;\">在四季花桥广场，可以看到许多大树上都画有动物头像，这种大自然装置艺术叫做“树体彩绘”，在树干上利用其自然纹理，如树枝、树瘤、树洞等原有条件来创作。用颜料绘制成各种创意图案，用其它材料制作出附属的装饰。这样可增加树的趣味及艺术性供人们欣赏。</span></p>',1),
(54,'四季植物馆','是湿地花园里最大的建筑物，占地面积近6000平方米。植物馆的造型设计非常独特，从上空俯瞰，整个四季植物馆就像一只巨大的甲壳虫，其两边的肢体上都安装有太阳能点板，为植物馆内的用电补充电能。另外，植物馆的屋顶是自动开合式的，可以调节管内的温度，使馆内的植物能在恒温的条件下生长繁殖。四季植物馆主要由太空瓜果区、珍奇植物观赏区、彩蝶区、科普互动空间等四个区域组成。植物馆入口大堂内有纪念品和小盆景销售。在四季植物馆内，游客可以了解许许多多的科普生态知识。',69,412,'upload/scenicSpotThumb/201706/1496715222341四季植物馆02.JPG','22.633359','114.280126','<p><span style=\"line-height: 1.42857;\">是湿地花园里最大的建筑物，占地面积近6000平方米。植物馆的造型设计非常独特，从上空俯瞰，整个四季植物馆就像一只巨大的甲壳虫，其两边的肢体上都安装有太阳能点板，为植物馆内的用电补充电能。另外，植物馆的屋顶是自动开合式的，可以调节管内的温度，使馆内的植物能在恒温的条件下生长繁殖。四季植物馆主要由太空瓜果区、珍奇植物观赏区、彩蝶区、科普互动空间等四个区域组成。植物馆入口大堂内有纪念品和小盆景销售。在四季植物馆内，游客可以了解许许多多的科普生态知识。</span></p><p><br></p>',1),
(55,'商业街','各大店铺装饰的琳琅满目。星巴克咖啡店、必胜客餐厅等为小镇增添了美妙的气氛。',41,414,'upload/scenicSpotThumb/201706/1496654890533激情海菲德.jpg','22.608802','114.304315','各大店铺装饰的琳琅满目。星巴克咖啡店、必胜客餐厅等为小镇增添了美妙的气氛。',1),
(56,'科普互动空间','  进入四季植物馆，首先映入眼帘的就是悬挂的地球仪，也是目前国内最大的地球仪。地球仪转动的方向就是地球自转的方向，所以视觉效果有“太空看地球”的感觉。',69,412,'upload/scenicSpotThumb/201706/1496715251003out2014.10.17景区示意图（中文）-正面(1).jpg','22.633359','114.280126','<p><span style=\"line-height: 1.42857;\">进入四季植物馆，首先映入眼帘的就是悬挂的地球仪，也是目前国内最大的地球仪。地球仪转动的方向就是地球自转的方向，所以视觉效果有“太空看地球”的感觉。</span></p>',1),
(57,'海布伦宫','占地2000平方米、层高12米的超大宴会演绎厅海布伦宫，拥有两个长方型主舞台和可升降圆形舞台，二楼的独立包厢可将整个大厅一览无余。紫色琉璃灯，熔岩的屋顶与墙壁造型，设计艺术在这里也发挥得淋漓尽致。',41,413,'upload/scenicSpotThumb/201706/1496654987348海布伦宫02.jpg','22.609866','114.304937','占地2000平方米、层高12米的超大宴会演绎厅海布伦宫，拥有两个长方型主舞台和可升降圆形舞台，二楼的独立包厢可将整个大厅一览无余。紫色琉璃灯，熔岩的屋顶与墙壁造型，设计艺术在这里也发挥得淋漓尽致。',1),
(58,'太空瓜果区','这个区域面积约有2000平方米，主要种植有10余种太空瓜果和20余种观赏南瓜。',69,412,'upload/scenicSpotThumb/201706/1496715272665out2014.10.17景区示意图（中文）-正面(1).jpg','22.633359','114.280126','<p>这个区域面积约有2000平方米，主要种植有10余种太空瓜果和20余种观赏南瓜。</p><p><br></p>',1),
(59,'珍奇植物观赏区','百余种珍奇植物令人耳目一新。如：闻声起舞的跳舞草，舒展大方的鹿角蕨，素雅清新的紫竹，附生植物的兜兰、万代兰、文心兰等等，令人目不暇接。',69,412,'upload/scenicSpotThumb/201706/1496715312237out2014.10.17景区示意图（中文）-正面(1).jpg','22.633359','114.280126','<p><span style=\"line-height: 1.42857;\">百余种珍奇植物令人耳目一新。如：闻声起舞的跳舞草，舒展大方的鹿角蕨，素雅清新的紫竹，附生植物的兜兰、万代兰、文心兰等等，令人目不暇接。</span></p><p><br></p>',1),
(60,'彩蝶区','这个区域占地面积约650平方米，主要展出了我国12个科及国外闪碟可得珍贵蝴蝶标本，还展出了美国、日本、马来西亚、印度、巴西等国的国蝶，共有150多种2000多只，以及国内高水平的蝶翅工艺品多幅。\n茵特拉根兰花园：兰花园占地8000平方米，其中园内兰花40余种，近14万株，背景植物80余种，真个兰花园主要利用角度化设计和多样化设计揭开兰花的神秘面纱，角度新颖“以人无则有，人有则精，人精则换”的理念指导设计，通过巧妙构思，合理布局，弥补了国内建设兰花园稀少的状态，通过兰花与常见植物、热带兰和亚热带兰、地生兰与附生兰的有效结合，营造出小型热带雨林的景观效果。',69,412,'upload/scenicSpotThumb/201706/1496715333316out2014.10.17景区示意图（中文）-正面(1).jpg','22.633359','114.280126','<div><span style=\"line-height: 1.42857;\">这个区域占地面积约650平方米，主要展出了我国12个科及国外闪碟可得珍贵蝴蝶标本，还展出了美国、日本、马来西亚、印度、巴西等国的国蝶，共有150多种2000多只，以及国内高水平的蝶翅工艺品多幅。</span></div><div>茵特拉根兰花园：兰花园占地8000平方米，其中园内兰花40余种，近14万株，背景植物80余种，真个兰花园主要利用角度化设计和多样化设计揭开兰花的神秘面纱，角度新颖“以人无则有，人有则精，人精则换”的理念指导设计，通过巧妙构思，合理布局，弥补了国内建设兰花园稀少的状态，通过兰花与常见植物、热带兰和亚热带兰、地生兰与附生兰的有效结合，营造出小型热带雨林的景观效果。</div><div><br></div>',1),
(61,'湖畔美食廊','集聚的各种风味的美食，使游客在游玩的过程中有更多的餐饮选择。',41,414,'upload/scenicSpotThumb/201706/1496655446202湖畔美食长廊 .JPG','22.60928','114.303002','集聚的各种风味的美食，使游客在游玩的过程中有更多的餐饮选择。',1),
(62,'茶田迷宫','隐藏于茶田之中，以茶丛间隔排列出的迷宫通道。不要小看低矮的灌木丛，曲折分叉的通道让人难以捉摸，有趣又好玩。',70,412,'upload/scenicSpotThumb/201706/1496715896698out2014.10.17景区示意图（中文）-正面(1).jpg','111111','1111111','<p><span style=\"line-height: 1.42857;\">隐藏于茶田之中，以茶丛间隔排列出的迷宫通道。不要小看低矮的灌木丛，曲折分叉的通道让人难以捉摸，有趣又好玩。</span></p>',1),
(63,'风光互补灯','风光互补路灯体现了节能环保的理念，它是使用自然风和太阳光作为能源。风力发电机和太阳能电池分别将太阳能和风能转化为电能，为照明系统提供电力。风力发电机和太阳能电池可以各自独立发电，也可以共同联合发电。阴雨天和夜间可以利用风能，无风时可以利用太阳能，风能和太阳能互补发电。系统同时配有蓄电池，可以保证在既无风又无阳光的情况下，照明系统能正常工作。风光路灯使用自然风和太阳光等天然绿色能源，不需要市电等外部电力，因此无需埋设或架设供电电缆，不会给环境带来污染，特别适合缺点地区和远离电网地区的照明设备。另外，每个风力发电机的小风扇可以根据风向自动调节小风扇的方向，在蓄电池蓄够电能时，风力发电机和太阳能发电板就自动停止工作，以延长各自的使用寿命。风光路灯不仅可以作为景区的照明系统，同时也是景区的一道美丽的风景线。',70,412,'upload/scenicSpotThumb/201706/1496715911372out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">风光互补路灯体现了节能环保的理念，它是使用自然风和太阳光作为能源。风力发电机和太阳能电池分别将太阳能和风能转化为电能，为照明系统提供电力。风力发电机和太阳能电池可以各自独立发电，也可以共同联合发电。阴雨天和夜间可以利用风能，无风时可以利用太阳能，风能和太阳能互补发电。系统同时配有蓄电池，可以保证在既无风又无阳光的情况下，照明系统能正常工作。风光路灯使用自然风和太阳光等天然绿色能源，不需要市电等外部电力，因此无需埋设或架设供电电缆，不会给环境带来污染，特别适合缺点地区和远离电网地区的照明设备。另外，每个风力发电机的小风扇可以根据风向自动调节小风扇的方向，在蓄电池蓄够电能时，风力发电机和太阳能发电板就自动停止工作，以延长各自的使用寿命。风光路灯不仅可以作为景区的照明系统，同时也是景区的一道美丽的风景线。</span></p>',1),
(64,'激流勇进','激流勇进运行高度为26米，最大运行速度80km/h，每条小船可乘坐20人，运行一周约3.5分钟。船体在下坡段俯冲而下，撞击冲浪池水面，顿时浪花冲天，景象蔚为壮观。',68,412,'upload/scenicSpotThumb/201706/1496655714425激流勇进_9604.JPG','22.608677','114.302846','激流勇进运行高度为26米，最大运行速度80km/h，每条小船可乘坐20人，运行一周约3.5分钟。船体在下坡段俯冲而下，撞击冲浪池水面，顿时浪花冲天，景象蔚为壮观。',1),
(65,'三洲田水库','始建于1958年，库容808万立方米，正常库容是720万立方米，集雨面积9.6平方公里。水库里的水不仅是我们景区里面的景观水，同时也是我们景区和部分深圳居民的生活饮用水。因水库海拔达330米，因此三洲田水库被誉为深圳“天湖”。\n三洲田水库的上库——大水坑水库，位于森林小火车轨道的下方。原为三洲田水库的一部分，修建盐三公路时此水库与三洲田水库隔开，通过泄洪道连通。大水坑水库集雨面积约2平方公里，库容约50万立方米，每年产水量约为140万立方米。',70,412,'upload/scenicSpotThumb/201706/1496715973322out2014.10.17景区示意图（中文）-正面(1).jpg','22.642601','114.281464','<div><span style=\"line-height: 1.42857;\">始建于1958年，库容808万立方米，正常库容是720万立方米，集雨面积9.6平方公里。水库里的水不仅是我们景区里面的景观水，同时也是我们景区和部分深圳居民的生活饮用水。因水库海拔达330米，因此三洲田水库被誉为深圳“天湖”。</span></div><div>三洲田水库的上库——大水坑水库，位于森林小火车轨道的下方。原为三洲田水库的一部分，修建盐三公路时此水库与三洲田水库隔开，通过泄洪道连通。大水坑水库集雨面积约2平方公里，库容约50万立方米，每年产水量约为140万立方米。</div><div><br></div>',1),
(66,'爱情湿地','占地面积近3万m2的人工湿地，湿地内种植了处理性能好，成活率高的水生植被，在改善周围生态环境的同时也体现出东部华侨城对生态环保的重视，同时爱情也像湿地一样要经过悉心栽培，层层过滤，感情才能得到升华。',70,412,'upload/scenicSpotThumb/201706/1496728471170爱情湿地.JPG','22.636109','114.276091','<p><span style=\"line-height: 1.42857;\">占地面积近3万m2的人工湿地，湿地内种植了处理性能好，成活率高的水生植被，在改善周围生态环境的同时也体现出东部华侨城对生态环保的重视，同时爱情也像湿地一样要经过悉心栽培，层层过滤，感情才能得到升华。</span></p>',1),
(67,'红羽栈道','环爱情湿地而建，全长800米。沿途可欣赏到蜿蜒的山林、溪水、湿地景色。路旁湿地蛙鸣阵阵，生机勃勃，行走其上，感觉人与自然分外融合。',70,412,'upload/scenicSpotThumb/201706/1496716025576out2014.10.17景区示意图（中文）-正面(1).jpg','22.634803','114.276071','<p><span style=\"line-height: 1.42857;\">环爱情湿地而建，全长800米。沿途可欣赏到蜿蜒的山林、溪水、湿地景色。路旁湿地蛙鸣阵阵，生机勃勃，行走其上，感觉人与自然分外融合。</span></p>',1),
(68,'茶岭长廊','依山势而建，建于茶田之间，行走之上可深入茶田，享受山风涤荡。',70,412,'upload/scenicSpotThumb/201706/1496716059497out2014.10.17景区示意图（中文）-正面(1).jpg','22.635112','114.271716','<p><span style=\"line-height: 1.42857;\">依山势而建，建于茶田之间，行走之上可深入茶田，享受山风涤荡。</span></p>',1),
(69,'空中漫步','空中攀爬项目作为大型互动探险体验综合体，结合茶溪谷童趣溪周围环境精心设计。游客可在48种不同的游乐项目里平衡穿越，自由上下，尽享茶园探险。气势恢宏，占地200多平米，二层总高度9.2米，项目的设计由趣至险，即可浅尝辄止，更可勇敢探索。',70,412,'upload/scenicSpotThumb/201706/1496716076172out2014.10.17景区示意图（中文）-正面(1).jpg','22.637765','114.272673','<p><span style=\"line-height: 1.42857;\">空中攀爬项目作为大型互动探险体验综合体，结合茶溪谷童趣溪周围环境精心设计。游客可在48种不同的游乐项目里平衡穿越，自由上下，尽享茶园探险。气势恢宏，占地200多平米，二层总高度9.2米，项目的设计由趣至险，即可浅尝辄止，更可勇敢探索。</span></p>',1),
(70,'童趣乐园','童趣乐园是专为儿童设计的全卡通式游乐区，它分机械游乐区与4D影院，游乐区有转转杯、碰碰车、（梦幻乐园）游戏厅。一个充满着奇幻浪漫的4D影院，它以3D的立体动画经过特效处理给大家带来一次最奇幻特别的浪漫旅程，影院每天轮流播放两场4D电影：《好人、坏人和马》、《小王子》。',68,412,'upload/scenicSpotThumb/201706/1496656113568童趣乐园_9652.JPG','22.607293','114.302442','<div>童趣乐园是专为儿童设计的全卡通式游乐区，它分机械游乐区与4D影院，游乐区有转转杯、碰碰车、（梦幻乐园）游戏厅。一个充满着奇幻浪漫的4D影院，它以3D的立体动画经过特效处理给大家带来一次最奇幻特别的浪漫旅程，影院每天轮流播放两场4D电影<span style=\"line-height: 1.42857;\">：《好人、坏人和马》、《小王子》。</span></div>',1),
(71,'竹乐谷','占地面积约4000m2，谷内山风吹过，能听到像音乐般的响声，因此得名竹乐谷。谷内凉亭、摇椅、秋千还有风铃、排箫等全是仿竹制品，躺在竹椅上听竹铃“嗒嗒”，呼吸大量负离子，仿佛世外之人。',70,412,'upload/scenicSpotThumb/201706/1496716128377茶溪谷－竹乐谷2.jpg','22.636494','114.27238','<p><span style=\"line-height: 1.42857;\">占地面积约4000m2，谷内山风吹过，能听到像音乐般的响声，因此得名竹乐谷。谷内凉亭、摇椅、秋千还有风铃、排箫等全是仿竹制品，躺在竹椅上听竹铃“嗒嗒”，呼吸大量负离子，仿佛世外之人。</span></p><p><br></p>',1),
(72,'热带雨林','借助了亚马逊热带雨林风情，走进林中，雨雾缭绕，植物与湿土混合出自然的香气，硕大的“蚂蚁”爬上高高的树干，小溪边卧着大大的“蜗牛”，各种仿真类昆虫呼和有声，充满雨林特色。',70,412,'upload/scenicSpotThumb/201706/1496716159718热带雨林 (3).JPG','22.637231','114.273501','<p><span style=\"line-height: 1.42857;\">借助了亚马逊热带雨林风情，走进林中，雨雾缭绕，植物与湿土混合出自然的香气，硕大的“蚂蚁”爬上高高的树干，小溪边卧着大大的“蜗牛”，各种仿真类昆虫呼和有声，充满雨林特色。</span></p><p><br></p>',1),
(74,'问茶泉','位于无忧湖的尽头。东部华侨城内水源资源丰富，除开大面积的水库，还有很多处山泉水泉眼，东部华侨城内泉眼所出的山泉水为国家一级偏硅酸型矿泉水，偏硅酸含量为37.8mg/l。经检测，问茶泉的泉水的PH值呈弱碱性，用这里的山泉水冲泡这里的自产茶，滋味格外甘醇。',70,412,'upload/scenicSpotThumb/201706/1496716179410问茶泉 (3).JPG','22.636266','114.270617','<p><span style=\"line-height: 1.42857;\">位于无忧湖的尽头。东部华侨城内水源资源丰富，除开大面积的水库，还有很多处山泉水泉眼，东部华侨城内泉眼所出的山泉水为国家一级偏硅酸型矿泉水，偏硅酸含量为37.8mg/l。经检测，问茶泉的泉水的PH值呈弱碱性，用这里的山泉水冲泡这里的自产茶，滋味格外甘醇。</span></p><p><br></p>',1),
(75,'真人CS','真人CS是一种集运动与游戏于一体，非常紧张刺激的激光枪战游戏，是一种高科技的娱乐活动，每场游戏需要12位游客，每场约20分钟，入场后需分成2支队伍进行激光对抗赛。',68,412,'upload/scenicSpotThumb/201706/1496717550727怒火冲锋真人CS_9654.JPG','22.607822','114.303007','真人CS是一种集运动与游戏于一体，非常紧张刺激的激光枪战游戏，是一种高科技的娱乐活动，每场游戏需要12位游客，每场约20分钟，入场后需分成2支队伍进行激光对抗赛。',1),
(76,'风语亭','海拔336.3米，总占地200平米，站在亭中，从不同方向可远眺云海谷、茵特拉根小镇、云中风车等景点，轻风拂过宛如耳边细语。因此而得名“风语亭”，风语亭的周围风景如画，软水温山，妙不可言，在此一坐，时光流缓。',70,412,'upload/scenicSpotThumb/201706/1496716197505风语亭 .JPG','22.63724','114.279973','<div><span style=\"line-height: 1.42857;\">海拔336.3米，总占地200平米，站在亭中，从不同方向可远眺云海谷、茵特拉根小镇、云中风车等景点，轻风拂过宛如耳边细语。因此而得名“风语亭”，风语亭的周围风景如画，软水温山，妙不可言，在此一坐，时光流缓。</span></div><div><br></div>',1),
(77,'望湖亭','海拔325.4米，其外形依照景点所在位置的地形而设置。可供游客休息、观景。',70,412,'upload/scenicSpotThumb/201706/1496716227501out2014.10.17景区示意图（中文）-正面(1).jpg','22.639771','114.277409','<p><span style=\"line-height: 1.42857;\">海拔325.4米，其外形依照景点所在位置的地形而设置。可供游客休息、观景。</span></p>',1),
(78,'问茶亭','海拔320余米，亭子矗立在蓊郁的茶田之中，亭中有一些茶文化小知识，供游人在此休息时学习。问茶亭下方的水库中有着旧时三洲田村的遗址。',70,412,'upload/scenicSpotThumb/201706/1496716264360out2014.10.17景区示意图（中文）-正面(1).jpg','22.640605','114.275521','<p><span style=\"line-height: 1.42857;\">海拔320余米，亭子矗立在蓊郁的茶田之中，亭中有一些茶文化小知识，供游人在此休息时学习。问茶亭下方的水库中有着旧时三洲田村的遗址。</span></p><p><br></p>',1),
(79,'闻香茶岭区','是茶田比较集中的一个区域，典型的岭南茶田风光，空气中弥漫着茶香，以茶田、湿地、竹林、书库为主要景观。',70,412,'upload/scenicSpotThumb/201706/1496716292602out2014.10.17景区示意图（中文）-正面(1).jpg','22.638621','114.277561','<p><span style=\"line-height: 1.42857;\">是茶田比较集中的一个区域，典型的岭南茶田风光，空气中弥漫着茶香，以茶田、湿地、竹林、书库为主要景观。</span></p><p><br></p>',1),
(80,'奇趣坡','占地2000多平米,利用生态材料组成各种奇趣古怪的小动物，如木蚱蜢、木马等，是儿童的游乐场。',70,412,'upload/scenicSpotThumb/201706/1496716313027out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">占地2000多平米,利用生态材料组成各种奇趣古怪的小动物，如木蚱蜢、木马等，是儿童的游乐场。</span></p><p><br></p>',1),
(81,'魔棋阵','占地250平方米，有 18颗巨石棋子，每个石雕的棋子重约75公斤。在魔棋阵，对弈山顶，湖水泛波，是静心休憩的好去处。',70,412,'upload/scenicSpotThumb/201706/1496716330720out2014.10.17景区示意图（中文）-正面(1).jpg','22.639019','114.279584','<p><span style=\"line-height: 1.42857;\">占地250平方米，有 18颗巨石棋子，每个石雕的棋子重约75公斤。在魔棋阵，对弈山顶，湖水泛波，是静心休憩的好去处。</span></p>',1),
(82,'茶溪栈桥','全长247米，高30米，桥宽2米，是茶溪谷最壮观的钢结构悬索景观吊桥。横跨于爱情湿地上空，近赏茶田和古镇、远眺大侠谷和云海谷，同时也是茶翁古镇通往茵特拉根小镇的捷径。',70,412,'upload/scenicSpotThumb/201706/1496716374764out2014.10.17景区示意图（中文）-正面(1).jpg','22.635311','114.275172','<p><span style=\"line-height: 1.42857;\">全长247米，高30米，桥宽2米，是茶溪谷最壮观的钢结构悬索景观吊桥。横跨于爱情湿地上空，近赏茶田和古镇、远眺大侠谷和云海谷，同时也是茶翁古镇通往茵特拉根小镇的捷径。</span></p><p><br></p>',1),
(83,'海上风暴','它的轨道高1.32米，运转直径12.3米，运转速度每分钟9.5转，连圆弧轨道均共有20个平底船，每个平底船可坐2名乘客，圆形轨道有4个波峰和波谷，运行时平底船可以正反自由旋转，能让我们体验在大海波浪中的感觉。',68,412,'upload/scenicSpotThumb/201706/1496657055905out2014.10.17景区示意图（中文）-正面(1).jpg','22.607779','114.302136','它的轨道高1.32米，运转直径12.3米，运转速度每分钟9.5转，连圆弧轨道均共有20个平底船，每个平底船可坐2名乘客，圆形轨道有4个波峰和波谷，运行时平底船可以正反自由旋转，能让我们体验在大海波浪中的感觉。',1),
(84,'竹溪索桥','桥长60米，宽1.5米，高18米，横跨竹乐谷上空。',70,412,'upload/scenicSpotThumb/201706/1496716417659竹溪索桥.JPG','22.636813','114.272519','<div><span style=\"line-height: 1.42857;\">桥长60米，宽1.5米，高18米，横跨竹乐谷上空。</span></div><div><br></div>',1),
(85,'丹枫拱桥','桥身总长36米，宽2米，位于无忧湖上。周围种植了许多山乌桕、红枫，秋冬季节，山林呈现出绛红色，俨如丹枫，因此得名“丹枫拱桥”。',70,412,'upload/scenicSpotThumb/201706/1496716437085丹凤拱桥.jpg','22.63824','114.272447','<p><span style=\"line-height: 1.42857;\">桥身总长36米，宽2米，位于无忧湖上。周围种植了许多山乌桕、红枫，秋冬季节，山林呈现出绛红色，俨如丹枫，因此得名“丹枫拱桥”。</span></p><p><br></p>',1),
(86,'迪斯科','斯科转盘取自UFO形状，高度14米，每次乘坐24人，设备启动后转盘在巨大的半圆形轨道上上下摆动25次，同时转盘进行正反自由旋转20圈。',68,412,'upload/scenicSpotThumb/201706/1496657250715out2014.10.17景区示意图（中文）-正面(1).jpg','22.607828','114.301762','斯科转盘取自UFO形状，高度14米，每次乘坐24人，设备启动后转盘在巨大的半圆形轨道上上下摆动25次，同时转盘进行正反自由旋转20圈。',1),
(87,'枫溪腾桥','桥长123米，宽1.8米，高25米，桥身以藤制成，以绳索悬挂于山谷之中。',70,412,'upload/scenicSpotThumb/201706/1496716458903枫溪藤桥.jpg','22.637819','114.2713','<p><span style=\"line-height: 1.42857;\">桥长123米，宽1.8米，高25米，桥身以藤制成，以绳索悬挂于山谷之中。</span></p><p><br></p>',1),
(88,'卧虹桥','桥身约110米，犹如一道彩虹卧在三洲田水库之上。',70,412,'upload/scenicSpotThumb/201706/1496716486276卧虹桥01.jpg','22.638998','114.275913','<p><span style=\"line-height: 1.42857;\">桥身约110米，犹如一道彩虹卧在三洲田水库之上。</span></p><p><br></p>',1),
(89,'火鸟山谷','火鸟山谷现有动物60余种，3000余只。已形成鸟类为主、两栖爬行动物为辅的观赏体系。分为火鸟湿地、天鹅湖、松鼠园、动物长廊、飞禽表演区。火鸟山谷融火烈鸟展示、鸟艺表演、百鸟争鸣、小动物展览等多项动物景观于一体，让人流连忘返。',68,412,'upload/scenicSpotThumb/201706/1496657429751out2014.10.17景区示意图（中文）-正面(1).jpg','22.608348','114.300395','火鸟山谷现有动物60余种，3000余只。已形成鸟类为主、两栖爬行动物为辅的观赏体系。分为火鸟湿地、天鹅湖、松鼠园、动物长廊、飞禽表演区。火鸟山谷融火烈鸟展示、鸟艺表演、百鸟争鸣、小动物展览等多项动物景观于一体，让人流连忘返。',1),
(90,'无忧廊桥','桥长32米，廊顶种植植物，并设计了水帘，位于响水溪之上。',70,412,'upload/scenicSpotThumb/201706/1496716504528无忧廊桥 (1).JPG','22.637345','114.271224','<p><span style=\"line-height: 1.42857;\">桥长32米，廊顶种植植物，并设计了水帘，位于响水溪之上。</span></p><p><br></p>',1),
(91,'栖霞回廊','建于丹枫拱桥左侧山坡上的观景回廊。栖霞回廊全长103米，宽1.5米，最高点距离地面5米左右。顺阶梯盘旋上升至回廊顶端的亭台——飞霞台，客观梧桐山余脉及三洲茶园景观，傍晚时观落霞铺满湖面，风采迷人。',70,412,'upload/scenicSpotThumb/201706/1496716523532栖霞回廊.jpg','22.639305','114.272494','<p><span style=\"line-height: 1.42857;\">建于丹枫拱桥左侧山坡上的观景回廊。栖霞回廊全长103米，宽1.5米，最高点距离地面5米左右。顺阶梯盘旋上升至回廊顶端的亭台——飞霞台，客观梧桐山余脉及三洲茶园景观，傍晚时观落霞铺满湖面，风采迷人。</span></p><p><br></p>',1),
(92,'火烈鸟','主要栖息于温热带盐湖水滨，涉行浅滩，以小虾、蛤蜊、昆虫、藻类等为食。觅食时头往下浸，嘴倒转，将食物吮入口中，把多余的水和不能吃的渣滓排出，然后徐徐吞下。性怯懦，喜群栖，常万余只结群。',68,412,'upload/scenicSpotThumb/201706/1496657608616out2014.10.17景区示意图（中文）-正面(1).jpg','22.608348','114.300395','主要栖息于温热带盐湖水滨，涉行浅滩，以小虾、蛤蜊、昆虫、藻类等为食。觅食时头往下浸，嘴倒转，将食物吮入口中，把多余的水和不能吃的渣滓排出，然后徐徐吞下。性怯懦，喜群栖，常万余只结群。',1),
(93,'落羽桥','弯曲的桥梁横跨在水库上游的人工湿地上方，红色的桥身格外的亮眼，是连接三洲茶园和湿地花园的通道。因桥的两侧种满了落羽杉而取名“落羽桥”。\n落羽杉又名称落羽松。落叶乔木。原产地高达50米，现江苏大丰杉木基地大量人工培育，胸径可达2米；树干尖削度大，干基膨大，地面通常有屈膝状的呼吸根；树皮为长条片状脱落，棕色；枝水平开展，树冠幼树圆锥形，老树为宽圆锥状；嫩枝开始绿色，秋季变为棕色，生叶侧生小枝为2列。叶线形，扁平，基部扭曲在小枝上为2列羽状，长1-1.5厘米，宽约1毫米，先端尖，上面中脉下凹，淡绿色，下面黄绿色，中脉隆起，每边有4-8条气孔线，落前变成红褐色。落羽杉原产美洲，我国已成功引种了80多年。其枝叶茂盛，秋季落叶较迟，冠形雄伟秀丽，是优美的庭园、道路绿化树种。在我国大部分地区都可做工业用树林和生态保护林。其种子是鸟雀、松鼠等野生动物喜食的饲料，因此对加强森林公园、维护自然保护区生物链，水土保持，涵养水源等均起到很好的作用。',70,412,'upload/scenicSpotThumb/201706/1496716539798落羽桥.jpg','22.634938','114.278162','<div><span style=\"line-height: 1.42857;\">弯曲的桥梁横跨在水库上游的人工湿地上方，红色的桥身格外的亮眼，是连接三洲茶园和湿地花园的通道。因桥的两侧种满了落羽杉而取名“落羽桥”。</span></div><div>落羽杉又名称落羽松。落叶乔木。原产地高达50米，现江苏大丰杉木基地大量人工培育，胸径可达2米；树干尖削度大，干基膨大，地面通常有屈膝状的呼吸根；树皮为长条片状脱落，棕色；枝水平开展，树冠幼树圆锥形，老树为宽圆锥状；嫩枝开始绿色，秋季变为棕色，生叶侧生小枝为2列。叶线形，扁平，基部扭曲在小枝上为2列羽状，长1-1.5厘米，宽约1毫米，先端尖，上面中脉下凹，淡绿色，下面黄绿色，中脉隆起，每边有4-8条气孔线，落前变成红褐色。落羽杉原产美洲，我国已成功引种了80多年。其枝叶茂盛，秋季落叶较迟，冠形雄伟秀丽，是优美的庭园、道路绿化树种。在我国大部分地区都可做工业用树林和生态保护林。其种子是鸟雀、松鼠等野生动物喜食的饲料，因此对加强森林公园、维护自然保护区生物链，水土保持，涵养水源等均起到很好的作用。</div><div><br></div>',1),
(94,'黑天鹅','原产于澳洲，是天鹅家族中的重要一员，为世界著名观赏珍禽。其体貌特征为全身除初级飞羽小部分为白色外，其余通体羽色漆黑，背覆花絮状灰羽。火鸟山谷除了这些动物还有许多好看、有趣的动物等着我们去参观噢。',68,412,'upload/scenicSpotThumb/201706/1496657648514out2014.10.17景区示意图（中文）-正面(1).jpg','22.608348','114.300395','原产于澳洲，是天鹅家族中的重要一员，为世界著名观赏珍禽。其体貌特征为全身除初级飞羽小部分为白色外，其余通体羽色漆黑，背覆花絮状灰羽。火鸟山谷除了这些动物还有许多好看、有趣的动物等着我们去参观噢。',1),
(95,'庚子首义纪念馆 ','  庚子首义馆占地700平方米，是为了纪念1900年在这里打响的由孙中山先生引领的庚子首义。因1900年为庚子年因此这场战役名为庚子首义，同时因这里地处三洲田片区最早由惠州管辖因此这场战役历史上也称之为三洲田起义和惠州起义。起义的内容主要以推翻当时清政府腐朽的统治为主。整个馆由四个展厅组成，这四个厅分别是：危难厅，警醒厅，首义厅和共和厅。每个厅以历史图片、视频、文字和展品等形式描述了庚子首义发生的历史背景、起义经过和深远影响，再现了中国近代史中国人民反帝反封建和追求民主与共和的艰苦历程。庚子首义馆设计精巧，每快砖石和布景都具有深刻的含义。',70,412,'upload/scenicSpotThumb/201706/1496716558403out2014.10.17景区示意图（中文）-正面(1).jpg','22.637162','114.273929','<p><span style=\"line-height: 1.42857;\">庚子首义馆占地700平方米，是为了纪念1900年在这里打响的由孙中山先生引领的庚子首义。因1900年为庚子年因此这场战役名为庚子首义，同时因这里地处三洲田片区最早由惠州管辖因此这场战役历史上也称之为三洲田起义和惠州起义。起义的内容主要以推翻当时清政府腐朽的统治为主。整个馆由四个展厅组成，这四个厅分别是：危难厅，警醒厅，首义厅和共和厅。每个厅以历史图片、视频、文字和展品等形式描述了庚子首义发生的历史背景、起义经过和深远影响，再现了中国近代史中国人民反帝反封建和追求民主与共和的艰苦历程。庚子首义馆设计精巧，每快砖石和布景都具有深刻的含义。</span></p>',1),
(96,'丛林穿梭','丛林穿梭U型滑道全长880米，其中有4个大的弯道，5个小的弯道，全程行驶只需1.35分钟左右，极具动感刺激。一个小时的运载量约为700人左右。',68,412,'upload/scenicSpotThumb/201706/1496657779092out2014.10.17景区示意图（中文）-正面(1).jpg','22.608499','114.301929','丛林穿梭U型滑道全长880米，其中有4个大的弯道，5个小的弯道，全程行驶只需1.35分钟左右，极具动感刺激。一个小时的运载量约为700人左右。',1),
(97,'半坡街','是一条通往古镇的上坡街道，街边铸了几尊罗汉的雕像，街道右边是白墙青瓦的各类茶馆、茶叶铺子和纪念品店，店中的牌匾、楹联自有一番意境别具古镇气氛，街道上镶嵌的铜框反应的是五十六个民族所饮用的不同的茶类。',71,412,'upload/scenicSpotThumb/201706/1496716580295out2014.10.17景区示意图（中文）-正面(1).jpg','22.633947','114.274437','<p><span style=\"line-height: 1.42857;\">是一条通往古镇的上坡街道，街边铸了几尊罗汉的雕像，街道右边是白墙青瓦的各类茶馆、茶叶铺子和纪念品店，店中的牌匾、楹联自有一番意境别具古镇气氛，街道上镶嵌的铜框反应的是五十六个民族所饮用的不同的茶类。</span></p>',1),
(98,'幻影旋风-6D影院','幻影旋风是模拟室内过山车探险的一部影片，视觉及情景相结合给人一种亦真亦幻的感觉。',68,412,'upload/scenicSpotThumb/201706/1496718198144XD 幻影旋风 2.jpg','22.610623','114.302777','幻影旋风是模拟室内过山车探险的一部影片，视觉及情景相结合给人一种亦真亦幻的感觉。',1),
(99,'罗汉雕像','在半坡街的入口处可以看到几尊罗汉的铜像,他们是十八罗汉的前三位。在我国的茶文化中历来就有“罗汉与茶素有渊源”的说法,传说西王母设宴款待五百罗汉,管茶的罗汉喝得酩酊大醉,不小心折断了手中的茶树枝,因此被贬,下凡到人间,后来有人发现这种茶可以医治百病,就起名为“铁罗汉”。罗汉与禅也同样关系密切,“罗汉禅”就是中国传统气功中的一个分支,在这里罗汉与茶、禅构成了一个茶香袅袅的空间,在这里选择了十八罗汉的前三位作为点缀,主要就是烘托茶禅文化的氛围。',71,412,'upload/scenicSpotThumb/201706/1496716596804out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<div><span style=\"line-height: 1.42857;\">在半坡街的入口处可以看到几尊罗汉的铜像,他们是十八罗汉的前三位。在我国的茶文化中历来就有“罗汉与茶素有渊源”的说法,传说西王母设宴款待五百罗汉,管茶的罗汉喝得酩酊大醉,不小心折断了手中的茶树枝,因此被贬,下凡到人间,后来有人发现这种茶可以医治百病,就起名为“铁罗汉”。罗汉与禅也同样关系密切,“罗汉禅”就是中国传统气功中的一个分支,在这里罗汉与茶、禅构成了一个茶香袅袅的空间,在这里选择了十八罗汉的前三位作为点缀,主要就是烘托茶禅文化的氛围。</span></div><div><br></div>',1),
(100,'宾头颅','也叫长眉罗汉，是十八罗汉的第一位，释迦牟尼的四大声闻之一。传说佛祖因为他在未受戒的人面前妄自显弄神通,所以不准他进入涅般,并命令他率领一千阿罗汉常住人门，造福世人。',71,412,'upload/scenicSpotThumb/201706/1496716624644out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">也叫长眉罗汉，是十八罗汉的第一位，释迦牟尼的四大声闻之一。传说佛祖因为他在未受戒的人面前妄自显弄神通,所以不准他进入涅般,并命令他率领一千阿罗汉常住人门，造福世人。</span></p>',1),
(101,'迦诺迦伐蹉','又称欢喜罗汉,是十八罗汉的第二位,也是释迦牟尼的四大声闻之一。传说有一次佛陀看到他虔诚的礼拜,就给他讲了“四种结业”、“四处恶行”、“六种应该做的事”,他听后茅塞顿开,成为佛陀的弟子,并证得阿罗汉果。',71,412,'upload/scenicSpotThumb/201706/1496716638598out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">又称欢喜罗汉,是十八罗汉的第二位,也是释迦牟尼的四大声闻之一。传说有一次佛陀看到他虔诚的礼拜,就给他讲了“四种结业”、“四处恶行”、“六种应该做的事”,他听后茅塞顿开,成为佛陀的弟子,并证得阿罗汉果。</span></p>',1),
(102,'迦诺迦伐厘惰阁','又称举钵罗汉,是十八罗汉的第三位,同样是释迦牟尼的四大声闻之一。传说因为他容易冲动,佛陀就特意为他讲了长生童子的故事,使他不再和其他人争吵,由于他化缘的时候每次都是举起铁钵向人家化缘,修成罗汉后被世人称为举钵罗汉。',71,412,'upload/scenicSpotThumb/201706/1496716654375out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">又称举钵罗汉,是十八罗汉的第三位,同样是释迦牟尼的四大声闻之一。传说因为他容易冲动,佛陀就特意为他讲了长生童子的故事,使他不再和其他人争吵,由于他化缘的时候每次都是举起铁钵向人家化缘,修成罗汉后被世人称为举钵罗汉。</span></p>',1),
(103,'飓风营救','飓风营救是一个轨道游玩项目，占地面积2690平方米，建筑面积1200平方米，包装成战船的小车通过轨道在水池上行走，水池内通过包装布置成飓风过后的小镇，游客通过旋转水枪的转臂从湖中吸水，然后通过旋转转臂射水，射程达6米，水站船轨道长200米，共有小车10辆，小车行使速度为0.28米—1.5/秒，每辆小车可载4人，载客量为800人/小时。',68,412,'upload/scenicSpotThumb/201706/1496658321845out2014.10.17景区示意图（中文）-正面(1).jpg','22.611541','114.302961','<div>飓风营救是一个轨道游玩项目，占地面积2690平方米，建筑面积1200平方米，包装成战船的小车通过轨道在水池上行走，水池内通过包装布置成飓风过后的小镇，游客通过旋转水枪的转臂从湖中吸水，然后通过旋转转臂射水，射程达6米，水站船轨道长200米<span style=\"line-height: 1.42857;\">，共有小车10辆，小车行使速度为0.28米—1.5/秒，每辆小车可载4人，载客量为800人/小时。</span></div>',1),
(104,'百茶屏','百茶屏近200m2，屏上雕刻的是百余种不同时代、不同字体的“茗”、“茶”的写法，以及有关于“茶”的短句、回文和故事画面，如：“国不可一日无君，君不可一日无茶”、“茶圣陆羽图”、“飞马贡茶图”等，俗话说好茶来自云雾山中，在百茶屏的四周也雕有祥云图案，这样的艺术处理既是构图的需要也是深含意境的一种内涵，因为茶与云雾有种不解之缘。',71,412,'upload/scenicSpotThumb/201706/1496716675260out2014.10.17景区示意图（中文）-正面(1).jpg','22.634422','114.273883','<p><span style=\"line-height: 1.42857;\">百茶屏近200m2，屏上雕刻的是百余种不同时代、不同字体的“茗”、“茶”</span><span style=\"line-height: 1.42857;\">的写法，以及有关于“茶”的短句、回文和故事画面，如：“国不可一日无君，君不可一日无茶”、“茶圣陆羽图”、“飞马贡茶图”等，俗话说好茶来自云雾山中，在百茶屏的四周也雕有祥云图案，这样的艺术处理既是构图的需要也是深含意境的一种内涵，因为茶与云雾有种不解之缘。</span></p><div><br></div>',1),
(106,'茶艺广场','茶艺广场是茶翁古镇的中心服务区，主要提供茶餐、土菜、大腕面、快餐及小吃等餐饮服务，另外在茶翁古镇还可以欣赏到粤剧、古典乐曲等表演。茶艺广场的主建筑包括聚茗楼、品茗阁、古镇面馆、茶源堂等，总建筑面积约4500m2，是游客聚餐集中点。这几栋建筑为茶翁古镇营造了古朴的气氛，许多建筑细节突出了中国古代建筑的风格，建筑多以祥禽瑞兽、仙花芝草为题材，寄寓了吉祥喜庆之意，寄托了人们对吉祥生活的向往，在聚茗楼的楼顶还有一座紫铜大钟，钟身铸有“波若波罗密多”心经，大钟与对面茶山的大鼓构成了晨钟暮鼓之意，让人在观景之余深刻体会到禅茶一味的意境。',71,414,'upload/scenicSpotThumb/201706/1496716711512out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">茶艺广场是茶翁古镇的中心服务区，主要提供茶餐、土菜、大腕面、快餐及小吃等餐饮服务，另外在茶翁古镇还可以欣赏到粤剧、古典乐曲等表演。茶艺广场的主建筑包括聚茗楼、品茗阁、古镇面馆、茶源堂等，总建筑面积约4500m2，是游客聚餐集中点。这几栋建筑为茶翁古镇营造了古朴的气氛，许多建筑细节突出了中国古代建筑的风格，建筑多以祥禽瑞兽、仙花芝草为题材，寄寓了吉祥喜庆之意，寄托了人们对吉祥生活的向往，在聚茗楼的楼顶还有一座紫铜大钟，钟身铸有“波若波罗密多”心经，大钟与对面茶山的大鼓构成了晨钟暮鼓之意，让人在观景之余深刻体会到禅茶一味的意境。</span></p>',1),
(107,'醍醐灌顶','茶艺广场正中心位置有一座茶壶构造的“壶神”，叫做“醍醐灌顶”。“醍醐灌顶”源自佛教的一个典故，是描述人的顿悟状态，在佛语里是指给人类灌输智慧的意思。“壶神”高3米，直径达4米，由紫铜锻造而成。壶嘴可以喷出水和水雾。“醍醐”是从牛乳中反复提炼而得到的甘美食品，在印度，不但视为“世间第一味”，而且认为它有较高药用价值。佛教常用“醍醐”比喻“无上法味”，“大涅槃”、“佛性”等。至于“灌顶”，原来古印度新王登基时的意识：取四海之水装于宝瓶内，流注新王之顶，象征新王以有四海的统治权力。',71,412,'upload/scenicSpotThumb/201706/1496716727303out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<div><span style=\"line-height: 1.42857;\">茶艺广场正中心位置有一座茶壶构造的“壶神”，叫做“醍醐灌顶”。“醍醐灌顶”源自佛教的一个典故，是描述人的顿悟状态，在佛语里是指给人类灌输智慧的意思。“壶神”高3米，直径达4米，由紫铜锻造而成。壶嘴可以喷出水和水雾。“醍醐”是从牛乳中反复提炼而得到的甘美食品，在印度，不但视为“世间第一味”，而且认为它有较高药用价值。佛教常用“醍醐”比喻“无上法味”，“大涅槃”、“佛性”等。至于“灌顶”，原来古印度新王登基时的意识：取四海之水装于宝瓶内，流注新王之顶，象征新王以有四海的统治权力。</span></div>',1),
(108,'地心四千里','它是一套互动式旅程体验设备，利用立体感的三维电脑成像技术将图像投影于无缝接合的银幕上。是一部模拟探险的三维电影。',68,412,'upload/scenicSpotThumb/201706/1496658597174地心四千里_9719.JPG','22.611244','114.302401','<div>它是一套互动式旅程体验设备，利用立体感的三维电脑成像技术将图像投影于无缝接合的银幕上。是一部模拟探险的三维电影。此设备凭360度的旋转系统技术使游客可以从正面、侧面、背面或四面环绕等各个方位体验影片中的故事情节。不仅如此， 银幕<span style=\"line-height: 1.42857;\">画面的变化与设备小车的运动规律是经过整合实现同步进行。参与者将完全溶入到设备主题当中。 它是高科技特效技术与电影结合后的产物。另外配上物理特效的使用如风、火、气味以及烟雾， 这些特效亦增强了设备的体验性。</span></div>',1),
(109,'古镇戏台','在地面积约60平方米，戏台上每天都定时上演独具特色的茶戏、粤剧以及古典器乐等表演。古镇戏台的设计为茶翁古镇更添一份“古意”。同时，戏台也是观赏茶溪栈桥的最佳位置。',71,412,'upload/scenicSpotThumb/201706/1496716743686out2014.10.17景区示意图（中文）-正面(1).jpg','22.635466','114.273191','<div><span style=\"line-height: 1.42857;\">在地面积约60平方米，戏台上每天都定时上演独具特色的茶戏、粤剧以及古典器乐等表演。古镇戏台的设计为茶翁古镇更添一份“古意”。同时，戏台也是观赏茶溪栈桥的最佳位置。</span></div>',1),
(110,'豆花坊','主营小吃类。位于茶艺广场的入口处，背靠茶山，四周溪水潺潺，茶香袅袅。在这里用餐，美食与美景并收，别有一番意境。在豆花坊，您可以品尝到各种美味小吃，最具特色的是“茶园三宝（茶叶蛋、玉米、红薯）”和字“自制泉水豆花”',71,414,'upload/scenicSpotThumb/201706/1496716760979out2014.10.17景区示意图（中文）-正面(1).jpg','22.634886','114.27299','<div><span style=\"line-height: 1.42857;\">主营小吃类。位于茶艺广场的入口处，背靠茶山，四周溪水潺潺，茶香袅袅。在这里用餐，美食与美景并收，别有一番意境。在豆花坊，您可以品尝到各种美味小吃，最具特色的是“茶园三宝（茶叶蛋、玉米、红薯）”和字“自制泉水豆花”。</span></div>',1),
(111,'茶艺坊','占地面积约1500m2，建筑面积约800m2，包括茶叶加工区和制茶体验区，在茶艺坊可以品尝、购买三洲茶园的自产茶。',71,414,'upload/scenicSpotThumb/201706/1496716784249out2014.10.17景区示意图（中文）-正面(1).jpg','22.634669','114.273538','<p><span style=\"line-height: 1.42857;\">占地面积约1500m2，建筑面积约800m2，包括茶叶加工区和制茶体验区，在茶艺坊可以品尝、购买三洲茶园的自产茶。</span></p>',1),
(112,'茶叶加工区','加工三洲茶园的自产茶，主要制作绿茶和乌龙茶。在这里，游客可以参观和学习茶叶现代化的制作流程。所有的茶叶加工生产设备都是相当先进的，使整个茶叶加工流程更方便、快捷，并且保证了卫生标准。另外，所有需要热源的机器都是采用天然气为热源，从而体现了生态环保的生态理念',71,412,'upload/scenicSpotThumb/201706/1497496665910out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<div><span style=\"line-height: 1.42857;\"><b>茶叶加工区</b></span></div><div><span style=\"line-height: 1.42857;\">加工三洲茶园的自产茶，主要制作绿茶和乌龙茶。在这里，游客可以参观和学习茶叶现代化的制作流程。所有的茶叶加工生产设备都是相当先进的，使整个茶叶加工流程更方便、快捷，并且保证了卫生标准。另外，所有需要热源的机器都是采用天然气为热源，从而体现了生态环保的生态理念。</span></div>',1),
(113,'飞跃巅峰—木质过山车','木质过山车是过山车的鼻祖，东部华侨城的木质过山车是继上海欢乐谷之后亚洲第二座，这种木质的过山车在运行中会发出碰撞声，让人感觉更惊险更刺激。但其轨道长度、行进速度、高低落差以及俯冲倾斜度均超过前者。',68,412,'upload/scenicSpotThumb/201706/1496659008447out2014.10.17景区示意图（中文）-正面(1).jpg','22.611903','114.302015','<div>木质过山车是过山车的鼻祖，东部华侨城的木质过山车是继上海欢乐谷之后亚洲第二座，这种木质的过山车在运行中会发出碰撞声，让人感觉更惊险更刺激。但其轨道长度、行进速度、高低落差以及俯冲倾斜度均超过前者。木质过山车是外方根<span style=\"line-height: 1.42857;\">据东部华侨城的要求结合现有山体进行设计的，轨道平面总长1470米，提升高度42m。最大落差40米，最高时速97公里。列车运行穿梭于实体山坡与山谷之间，可以在高速刺激的的列车上欣赏大梅沙、大侠谷美丽的自然风光；每列列车12节车厢，每车厢可以坐2人，每列</span><span style=\"line-height: 1.42857;\">列车可以坐24人。整个设备的材质全部采用美国南方一级黄松原木加工切割而成，达到美国最高的ＣＣＡ040－ＫＤＡＴ标准，在出厂之前已经完成了木材的切割以及防腐防潮处理，能防各种虫害，使用寿命长。项目建设将与生态地形完美结合，能够实现90度倾斜旋转效</span><span style=\"line-height: 1.42857;\">果的木质过山车。</span></div>',1),
(114,'浣茶亭','位于古镇一角，清流环绕，四位分别持有古筝、笙箫、笛子、琵琶的仕女分居亭之四隅(yu)。在浣茶亭中央刻有四种书法形式的中国文化史上著名的“宝塔诗”。这首“宝塔诗”是元微之与白居易在兴华亭为离别而聚会时，共同以“茶”为题而填的。前五句为第一层次，概括介绍了茶叶的来源，特性和受人喜爱；中间四句为第二层次，写烹茶和饮茶，不仅细腻刻画了煮茶的方法，而且描绘了经过煎煮后茶的颜色和芬芳；最后四句为第三层次，用拟人的手法刻画了茶的功效并予以赞扬。所谓“夜后邀陪明月，晨前命对朝霞”道尽了雅人国士与茶文化之间的浓厚情缘。\n茶\n香叶  嫩芽\n慕诗客  爱僧家\n碾雕白玉  罗织红纱\n铫煎黄蕊色  碗转曲尘花\n夜后邀陪明月  晨前命对朝霞\n洗尽古今人不倦  将至醉乱岂堪夸\n另外，在茶艺广场随处可见石雕篆刻的茶诗和短句,这些短句都是历来文人为茶所写,都有一定的历史和文化背景。',71,412,'upload/scenicSpotThumb/201706/1496716809625out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<div><span style=\"line-height: 1.42857;\">位于古镇一角，清流环绕，四位分别持有古筝、笙箫、笛子、琵琶的仕女分居亭之四隅(yu)。在浣茶亭中央刻有四种书法形式的中国文化史上著名的“宝塔诗”。这首“宝塔诗”是元微之与白居易在兴华亭为离别而聚会时，共同以“茶”为题而填的。前五句为第一层次，概括介绍了茶叶的来源，特性和受人喜爱；中间四句为第二层次，写烹茶和饮茶，不仅细腻刻画了煮茶的方法，而且描绘了经过煎煮后茶的颜色和芬芳；最后四句为第三层次，用拟人的手法刻画了茶的功效并予以赞扬。所谓“夜后邀陪明月，晨前命对朝霞”道尽了雅人国士与茶文化之间的浓厚情缘。</span></div><div>茶</div><div>香叶 &nbsp;嫩芽</div><div>慕诗客 &nbsp;爱僧家</div><div>碾雕白玉 &nbsp;罗织红纱</div><div>铫煎黄蕊色 &nbsp;碗转曲尘花</div><div>夜后邀陪明月 &nbsp;晨前命对朝霞</div><div>洗尽古今人不倦 &nbsp;将至醉乱岂堪夸</div><div>另外，在茶艺广场随处可见石雕篆刻的茶诗和短句,这些短句都是历来文人为茶所写,都有一定的历史和文化背景。</div>',1),
(115,'八咏后人','是采用清朝著名篆刻家、印章家沈皋的作品。“八咏后人”出自李清照的诗《题八咏楼》。',71,412,'upload/scenicSpotThumb/201706/1496716826713out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">是采用清朝著名篆刻家、印章家沈皋的作品。“八咏后人”出自李清照的诗《题八咏楼》。</span></p>',1),
(116,'年高身键不肯做神仙','是中国近代著名画家、书法家齐白石先生在将近九十岁高龄时篆刻的一枚著名印信。',71,412,'upload/scenicSpotThumb/201706/1496716843697out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">是中国近代著名画家、书法家齐白石先生在将近九十岁高龄时篆刻的一枚著名印信。</span></p>',1),
(117,'咆哮山洪','它是利用山和蓄水池来模拟山洪暴发的一个景象，1500立方米的洪水倾泻而下，刹那间大船会倾斜，摩托艇腾空而出，融合了爆破、枪战、烟火、声光、滑水、高空特技等多种特效，我们可尽情的观赏这部好莱坞大片《咆哮山洪》。',68,412,'upload/scenicSpotThumb/201706/1496659280112咆哮山洪 (3).jpg','22.611263','114.30374','它是利用山和蓄水池来模拟山洪暴发的一个景象，1500立方米的洪水倾泻而下，刹那间大船会倾斜，摩托艇腾空而出，融合了爆破、枪战、烟火、声光、滑水、高空特技等多种特效，我们可尽情的观赏这部好莱坞大片《咆哮山洪》。',1),
(118,'有花有酒且开眉','这句诗选自《庆金枝令》,是采用清代著名篆刻家周芬的作品。',71,412,'upload/scenicSpotThumb/201706/1496716863812out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<div><span style=\"line-height: 1.42857;\">这句诗选自《庆金枝令》,是采用清代著名篆刻家周芬的作品。</span></div>',1),
(119,'写其大略','是被誉为“江南三铁”的现代著名书画家、篆刻家钱瘦铁先生的著名作品。',71,412,'upload/scenicSpotThumb/201706/1496716881738out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<div><span style=\"line-height: 1.42857;\">是被誉为“江南三铁”的现代著名书画家、篆刻家钱瘦铁先生的著名作品。</span></div>',1),
(120,'不可一日无此君','是清朝的第六位皇帝乾隆皇帝说过的话。在这里有一个小插曲,在乾隆八十五岁的时候想要让位给嘉庆，一位老臣便挽留说:“国不可一日无君”，嗜茶如命的乾隆便说:“君不可一日无茶”。',71,412,'upload/scenicSpotThumb/201706/1496716916586out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">是清朝的第六位皇帝乾隆皇帝说过的话。在这里有一个小插曲,在乾隆八十五岁的时候想要让位给嘉庆，一位老臣便挽留说:“国不可一日无君”，嗜茶如命的乾隆便说:“君不可一日无茶”。</span></p>',1),
(121,'乾隆御览之宝','显而易见,这几个字一定也与乾隆皇帝有关了,这里也有一个小趣闻。据说乾隆皇帝对于自己所钟爱的书画都喜欢印上刻有这样的几个字的朱文椭圆玺，表示珍藏。',71,412,'upload/scenicSpotThumb/201706/1496716935676out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">显而易见,这几个字一定也与乾隆皇帝有关了,这里也有一个小趣闻。据说乾隆皇帝对于自己所钟爱的书画都喜欢印上刻有这样的几个字的朱文椭圆玺，表示珍藏。</span></p>',1),
(122,'陶然自乐','出自陶渊明的文章《时运》，这里采用的是与徐悲鸿、邓散木并称\"艺坛三杰\"的近代著名书法家、画家白蕉的书法作品。',71,412,'upload/scenicSpotThumb/201706/1496716948928out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<div><span style=\"line-height: 1.42857;\">出自陶渊明的文章《时运》，这里采用的是与徐悲鸿、邓散木并称\"艺坛三杰\"的近代著名书法家、画家白蕉的书法作品。</span></div>',1),
(123,'深海探奇','深海中的恐龙级物种--水母！这里有两种满足你的好奇心。',68,412,'upload/scenicSpotThumb/201706/1496659408788深海探险_9692.JPG','22.61209','114.304148','<div>水母是海洋中一种大型浮游生物，是一种低等肠腔动物，水母的出现比恐龙还早，可追溯到6.5亿年前，水母的种类很多，全世界大约有250种左右，直径从10厘米到100厘米之间，分布在全球各地海水里。他们的天敌是一种海龟。水母没有心脏，没有大脑，没<span style=\"line-height: 1.42857;\">有骨骼，也没有眼睛，它的身体95﹪以上都是由水组成。水母可区分三个主要部分；一是圆圆的伞体，其以一缩一放来进行游泳运动;二是触手，上面布满刺细胞用来抓捕及麻痹猎物；三是口腕，是水母的嘴巴，吃食猎物。我们这里目前饲养的有两种，一种是海月水母，</span><span style=\"line-height: 1.42857;\">另外一种是倒立水母。这两种也是我国常见的品种。</span></div>',1),
(124,'不可居无竹','这首诗出自苏轼的著名诗句“宁可食无肉，不可居无竹；无肉使人瘦，无竹使人俗。”这里采用的齐白石先生的书法作品。',71,412,'upload/scenicSpotThumb/201706/1496716966540out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','<p><span style=\"line-height: 1.42857;\">这首诗出自苏轼的著名诗句“宁可食无肉，不可居无竹；无肉使人瘦，无竹使人俗。”这里采用的齐白石先生的书法作品。</span></p>',1),
(125,'云海索道','设备是由法国POMA公司引进的单循环脱挂索道，于2008年5月1日投入营运。索道全长1089米，共有34个吊厢，每个吊箱可乘坐8人，每小时载客量可达2000人。索道与丛林缆车形成两条绿色环保交通干线。',68,412,'upload/scenicSpotThumb/201706/1496659672017out2014.10.17景区示意图（中文）-正面(1).jpg','22.617996','114.295639','设备是由法国POMA公司引进的单循环脱挂索道，于2008年5月1日投入营运。索道全长1089米，共有34个吊厢，每个吊箱可乘坐8人，每小时载客量可达2000人。索道与丛林缆车形成两条绿色环保交通干线。',1),
(126,'丛林缆车','亚洲首创的双坡山地式观光缆车，1号线全长约1360米，由两辆缆车交错运行，每辆可乘坐150人，平均每小时载客量可达1500人，高差400米的丛林缆车将给我们带来壮观的观景视角。\n',68,412,'upload/scenicSpotThumb/201706/1496659765173out2014.10.17景区示意图（中文）-正面(1).jpg','22.608919','114.301333','<div>亚洲首创的双坡山地式观光缆车，1号线全长约1360米，由两辆缆车交错运行，每辆可乘坐150人，平均每小时载客量可达1500人，高差400米的丛林缆车将给我们带来壮观的观景视角。</div><div><br></div>',1),
(127,'茵特拉根火车站','是森林小火车的停靠站，距离大拐弯站3公里，小火车轨道全长约6公里，由中铁一局规划建设。森林小火车融观光性、参与性和交通运输功能三位一体，每班可乘坐120人，全程运行45分钟，先后经过峡谷、山林、湖泊、隧道，可远观原生态自然景观、黑森林酒店、天麓街区、大华兴寺等景点，为了保护水源，在大水坑水库上方的高架桥最高处达40米。',72,412,'upload/scenicSpotThumb/201706/1496716995992茵特拉根小火车站.jpg','22.631594','114.27579','<p><span style=\"line-height: 1.42857;\">是森林小火车的停靠站，距离大拐弯站3公里，小火车轨道全长约6公里，由中铁一局规划建设。森林小火车融观光性、参与性和交通运输功能三位一体，每班可乘坐120人，全程运行45分钟，先后经过峡谷、山林、湖泊、隧道，可远观原生态自然景观、黑森林酒店、天麓街区、大华兴寺等景点，为了保护水源，在大水坑水库上方的高架桥最高处达40米。</span></p>',1),
(128,'云中漫步','在这长约1公里的观景带里可以看到云海谷高尔夫球场、华兴寺和大侠谷全景及大梅沙的海景景观，在雾蒙蒙的天气时在此行走犹如在云中穿梭。',43,412,'upload/scenicSpotThumb/201706/1496659886964云中漫步02.jpg','22.617478','114.295714','在这长约1公里的观景带里可以看到云海谷高尔夫球场、华兴寺和大侠谷全景及大梅沙的海景景观，在雾蒙蒙的天气时在此行走犹如在云中穿梭。',1),
(129,'旋转木马','设计源自法国，木马直径8米，两层高度计5米，容量18人。娱乐单元有木马、飞机、老爷车、热气球、潜水艇，花车等8种造型，体现了1900年代欧洲科技文化进步的成果，反映了一个变革时代的科技创新和传统经典和谐相处的社会缩影。',72,412,'upload/scenicSpotThumb/201706/1496717061211out2014.10.17景区示意图（中文）-正面(1).jpg','22.632497','114.277523','<p><span style=\"line-height: 1.42857;\">设计源自法国，木马直径8米，两层高度计5米，容量18人。娱乐单元有木马、飞机、老爷车、热气球、潜水艇，花车等8种造型，体现了1900年代欧洲科技文化进步的成果，反映了一个变革时代的科技创新和传统经典和谐相处的社会缩影。</span></p>',1),
(130,'茵特拉根商业街','茵特拉根商业街占地面积近1万m2，全长200余米，纵贯整个茵特拉根小镇。包括5栋商业店铺，商业街一楼是主题纪念品店和特色健康的餐饮，商业街的二楼是温馨的茵特拉根公寓客房，共设有43间客房，是茵特拉根酒店的配套设施。',72,414,'upload/scenicSpotThumb/201706/1496717084355步行街01.jpg','22.632317','114.277281','<p><span style=\"line-height: 1.42857;\">茵特拉根商业街占地面积近1万m2，全长200余米，纵贯整个茵特拉根小镇。包括5栋商业店铺，商业街一楼是主题纪念品店和特色健康的餐饮，商业街的二楼是温馨的茵特拉根公寓客房，共设有43间客房，是茵特拉根酒店的配套设施。</span></p>',1),
(131,'茵特拉根廊桥','位于在茵特拉根商业街的中心位置，横跨于硫森湖之上。走上廊桥，可以感受到欧式廊桥不同于中式廊桥的建筑风格，在廊桥的顶部画有欧洲经典的壁画。茵特拉根廊桥不但是一道美丽的风景线，同时也是一条美丽的观景走廊，廊桥的中间位置是欣赏茵特拉根华侨城酒店全景的最佳位置。',72,412,'upload/scenicSpotThumb/201706/1496717107278茵特拉根廊桥.JPG','22.631594','114.27579','<p><span style=\"line-height: 1.42857;\">位于在茵特拉根商业街的中心位置，横跨于硫森湖之上。走上廊桥，可以感受到欧式廊桥不同于中式廊桥的建筑风格，在廊桥的顶部画有欧洲经典的壁画。茵特拉根廊桥不但是一道美丽的风景线，同时也是一条美丽的观景走廊，廊桥的中间位置是欣赏茵特拉根华侨城酒店全景的最佳位置。</span></p>',1),
(132,'云中风车','由八台风力机组组成的风车电站，每年可发电200万度，电量全部输入景区电网，是国内第一座近万平方米的旅游景观风力发电站群，也是东部华侨城的独特景观和地理标识。',43,412,'upload/scenicSpotThumb/201706/1496660121998out2014.10.17景区示意图（中文）-正面(1).jpg','111111','111111','由八台风力机组组成的风车电站，每年可发电200万度，电量全部输入景区电网，是国内第一座近万平方米的旅游景观风力发电站群，也是东部华侨城的独特景观和地理标识。',1),
(133,'茵特拉根酒店','茵特拉根酒店是东部华侨城第一间主题酒店，内部设施五星级标准，设有300余间客房，其中包括山景房、湖景房、连排别墅、小木屋泳池别墅等各种房型，酒店的大堂是目前国内较罕见的花园式大堂，大堂吧类似于中世纪的大教堂，充分体现了人与自然和谐共处的理念，酒店内的餐饮设施也十分齐全，中式、日式、意大利式应有尽有，另在酒店内部还设有小型水上码头，入住酒店的住客可直接乘游艇到达酒店对面的温泉馆泡温泉。',72,414,'upload/scenicSpotThumb/201706/14968144528897茵特拉根酒店意大利餐厅.jpg','22.634188','114.287146','<p><span style=\"line-height: 1.42857;\">茵特拉根酒店是东部华侨城第一间主题酒店，内部设施五星级标准，设有300余间客房，其中包括山景房、湖景房、连排别墅、小木屋泳池别墅等各种房型，酒店的大堂是目前国内较罕见的花园式大堂，大堂吧类似于中世纪的大教堂，充分体现了人与自然和谐共处的理念，酒店内的餐饮设施也十分齐全，中式、日式、意大利式应有尽有，另在酒店内部还设有小型水上码头，入住酒店的住客可直接乘游艇到达酒店对面的温泉馆泡温泉。</span></p>',1),
(134,'东部华侨城大剧院','东部华侨城大剧院是投资近2个亿打造的多功能剧院，建筑面积约1000m2可同时容纳1600余人，集晚会表演、大型宴会、商务会议和展览等多种能于一体。每天下午3：00会上演东部华侨城的主题晚会之一，——《天禅》，《天禅》是由中外艺术家历经三年精心打造的一场以禅、茶文化为主题的大型多媒体交响音画晚会，并荣获了首届亚洲青年艺术节金奖。晚会融合了多种艺术手段，高科技的声、光、电技术给《天禅》晚会带来强烈的视觉震撼。',72,413,'upload/scenicSpotThumb/201706/1496717197498大剧院_9873.JPG','22.633501','114.275813','<p><span style=\"line-height: 1.42857;\">东部华侨城大剧院是投资近2个亿打造的多功能剧院，建筑面积约1000m2可同时容纳1600余人，集晚会表演、大型宴会、商务会议和展览等多种能于一体。每天下午3：00会上演东部华侨城的主题晚会之一，——《天禅》，《天禅》是由中外艺术家历经三年精心打造的一场以禅、茶文化为主题的大型多媒体交响音画晚会，并荣获了首届亚洲青年艺术节金奖。晚会融合了多种艺术手段，高科技的声、光、电技术给《天禅》晚会带来强烈的视觉震撼。</span></p>',1),
(135,'登峰造极','它采用的是高强度夹胶钢化玻璃制成，建造在这海拔490米的山顶上，悬挑长达17米，走道宽2.5米，扶手高1米，玻璃栏杆高1.25米，四周为悬崖峭壁，在上面自由漫步，体验世界在脚下的感觉。',43,412,'upload/scenicSpotThumb/201706/1496660288483out2014.10.17景区示意图（中文）-正面(1).jpg','22.616291','114.294782','它采用的是高强度夹胶钢化玻璃制成，建造在这海拔490米的山顶上，悬挑长达17米，走道宽2.5米，扶手高1米，玻璃栏杆高1.25米，四周为悬崖峭壁，在上面自由漫步，体验世界在脚下的感觉。',1),
(136,'《天禅》','新《天禅》以茶圣陆羽和一代宗师皎然法师的“人生三饮、三问”，巧妙融入陆羽的生命曲线中“幼年、青年、老年”等三大品格各异、色彩对比强烈的艺术造型这一逻辑线，引出“天、水、火、风、地、山、海、人、禅”九个单元，向观众诉说禅道和生命的故事。整场表演将中国古老的茶文化与佛教文化相结合，运用国内顶尖的声、光、电、多媒体等现代科技手段，穿插中国凤凰涅槃等的古老传说、糅合芭蕾、武术、杂技、水袖、现代舞等传统与现代结合的艺术表演形式来演绎东方茶禅文化。\n',72,413,'upload/scenicSpotThumb/201706/1496814327965天禅 (1).jpg','22.63319','114.276217','<div><span style=\"line-height: 1.42857;\">新《天禅》以茶圣陆羽和一代宗师皎然法师的“人生三饮、三问”，巧妙融入陆羽的生命曲线中“幼年、青年、老年”等三大品格各异、色彩对比强烈的艺术造型这一逻辑线，引出“天、水、火、风、地、山、海、人、禅”九个单元，向观众诉说禅道和生命的故事。整场表演将中国古老的茶文化与佛教文化相结合，运用国内顶尖的声、光、电、多媒体等现代科技手段，穿插中国凤凰涅槃等的古老传说、糅合芭蕾、武术、杂技、水袖、现代舞等传统与现代结合的艺术表演形式来演绎东方茶禅文化。</span></div><div><br></div><div><br></div>',1),
(137,'室内网球馆','网球馆占地约1800m2，设有两个标准的球场，网球馆的屋顶采用自动开合式设计，无论天气变幻与昼夜更替，都能让人尽兴挥洒。室内网球馆是茵特拉根酒店的配套设施。',72,412,'upload/scenicSpotThumb/201706/1496717242802水上高尔夫  01.jpg','22.600485','114.30361','<p><span style=\"line-height: 1.42857;\">网球馆占地约1800m2，设有两个标准的球场，网球馆的屋顶采用自动开合式设计，无论天气变幻与昼夜更替，都能让人尽兴挥洒。室内网球馆是茵特拉根酒店的配套设施。</span></p>',1),
(138,'云霄飞轮','设备产自意大利，高40米，每台2个座舱，每舱可坐4人，每台每小时载客120人 。最大旋转速度10转/分钟，旋转半径达20米,重力加速度3.6g 。两台奇迹般的旋转臂与支撑杆紧密固定，每个转臂一头都在悬崖之边，这是来到我们大侠谷不可错过的极具刺激的娱乐项目之一。',43,412,'upload/scenicSpotThumb/201706/1496660493439out2014.10.17景区示意图（中文）-正面(1).jpg','22.616325','114.294957','<div>设备产自意大利，高40米，每台2个座舱，每舱可坐4人，每台每小时载客120人 。最大旋转速度10转/分钟，旋转半径达20米,重力加速度3.6g 。两台奇迹般的旋转臂与支撑杆紧密固定，每个转臂一头都在悬崖之边，这是来到我们大侠谷不可错过的极具刺激的<span style=\"line-height: 1.42857;\">娱乐项目之一。</span></div>',1),
(139,'自然之眼观光塔','塔高110米，行程约69.2米,观光塔直径2.5米,舱室直径12米,升降速度1m/s,旋转速度2分钟/转,座舱容量72人, 每小时运量约1500人。上升速度可根据需要进行调节，登高远眺，云海索道、云中漫步、登峰造极、云中部落尽收眼底，天气晴好时可欣赏到壮观的360度全景景观及香港周边岛屿。',43,412,'upload/scenicSpotThumb/201706/1496660601817out2014.10.17景区示意图（中文）-正面(1).jpg','22.615725','114.294668','<div>塔高110米，行程约69.2米,观光塔直径2.5米,舱室直径12米,升降速度1m/s,旋转速度2分钟/转,座舱容量72人, 每小时运量约1500人。上升速度可根据需要进行调节，登高远眺，云海索道、云中漫步、登峰造极、云中部落尽收眼底，天气晴好时可欣赏到<span style=\"line-height: 1.42857;\">壮观的360度全景景观及香港周边岛屿。</span></div>',1),
(140,'云中部落','广场为游客集散区，同时也是游客游览必经的中转站，广场上体现的是玛雅族的图腾文化，游客可在此休息用餐。',43,414,'upload/scenicSpotThumb/201706/1496660922638云中餐厅.jpg','22.615418','114.293813','广场为游客集散区，同时也是游客游览必经的中转站，广场上体现的是玛雅族的图腾文化，游客可在此休息用餐。',1),
