/*Table structure for table `mp_store` */

DROP TABLE IF EXISTS `mp_store`;

CREATE TABLE `mp_store` (
  `poi_id` varchar(255) DEFAULT NULL COMMENT '门店ID（适用于微信卡券、微信门店业务），具体定义参考微信门店，与shop_id一一对应',
  `sid` varchar(255) DEFAULT NULL COMMENT '商户自己的id，用于后续审核通过收到poi_id 的通知时，做对应关系。请商户自己保证唯一识别性',
  `business_name` varchar(255) DEFAULT NULL COMMENT '门店名称',
  `branch_name` varchar(255) DEFAULT NULL COMMENT '分店名称',
  `categories` varchar(255) DEFAULT NULL COMMENT '类目',
  `telephone` varchar(255) DEFAULT NULL COMMENT '联系电话',
  `province` varchar(100) DEFAULT NULL COMMENT '省份',
  `city` varchar(100) DEFAULT NULL COMMENT '市',
  `district` varchar(100) DEFAULT NULL COMMENT '县/区',
  `address` varchar(255) DEFAULT NULL COMMENT '街道',
  `location` varchar(255) DEFAULT NULL COMMENT '经纬度 ,分隔'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='微信门店';

/*Data for the table `mp_store` */

/*Table structure for table `mp_ticket_order` */

DROP TABLE IF EXISTS `mp_ticket_order`;

CREATE TABLE `mp_ticket_order` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `order_no` varchar(255) DEFAULT NULL COMMENT '订单编号',
  `order_title` varchar(512) DEFAULT NULL COMMENT '订单标题',
  `ticket_id` int(11) DEFAULT NULL COMMENT '门票id',
  `ticket_count` int(11) DEFAULT '0' COMMENT '门票数量',
  `start_time` int(11) DEFAULT NULL COMMENT '有效期开始时间',
  `end_time` int(11) DEFAULT NULL COMMENT '有效期结束时间',
  `order_phone` varchar(64) DEFAULT NULL COMMENT '下单手机号码',
  `unionid` varchar(255) DEFAULT NULL COMMENT '只有在用户将公众号绑定到微信开放平台帐号后，才会出现该字段',
  `member_id` bigint(20) DEFAULT NULL COMMENT '会员id（关联表mp_member字段id）',
  `total_fee` decimal(10,2) DEFAULT '0.00' COMMENT '订单总金额',
  `status` int(11) DEFAULT '3' COMMENT '订单状态：1、已支付  2、支付失败  3、待支付 4、已关闭(用户取消订单或超时未支付)',
  `del` tinyint(1) DEFAULT NULL COMMENT '删除状态(0正常1已删除)',
  `update_time` int(11) DEFAULT NULL COMMENT '最后修改时间',
  `create_time` int(11) DEFAULT NULL COMMENT '下单时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=75 DEFAULT CHARSET=utf8 COMMENT='购票订单记录表';

/*Data for the table `mp_ticket_order` */

