/*Table structure for table `mp_tickets` */

DROP TABLE IF EXISTS `mp_tickets`;

CREATE TABLE `mp_tickets` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `ticket_name` varchar(128) DEFAULT NULL COMMENT '门票名称',
  `thumb_url` varchar(1024) DEFAULT NULL COMMENT '缩略图',
  `ticket_no` varchar(128) DEFAULT NULL COMMENT '门票批次编号(扩展字段，暂不启用)',
  `price` decimal(10,2) DEFAULT NULL COMMENT '价格',
  `market_price` decimal(10,2) DEFAULT NULL COMMENT '市场价',
  `advance` tinyint(1) DEFAULT NULL COMMENT '是否需要提前购票(0否1是)',
  `sale_date` int(11) DEFAULT NULL COMMENT '开售时间',
  `start_buy_date` int(11) DEFAULT NULL COMMENT '开始购票日期',
  `end_buy_date` int(11) DEFAULT NULL COMMENT '结束购票日期',
  `weekend_type` tinyint(1) DEFAULT NULL COMMENT '周末票价是否另算(0否1是)',
  `weekend_price` decimal(10,2) DEFAULT NULL COMMENT '周末票价',
  `discount` decimal(2,2) DEFAULT NULL COMMENT '折扣',
  `about` text COMMENT '预定须知',
  `ticket_content` text COMMENT '门票详情',
  `sale_count` int(11) DEFAULT NULL COMMENT '已售数量',
  `status` int(11) DEFAULT NULL COMMENT '1、上架  2、下架',
  `update_time` int(11) DEFAULT NULL COMMENT '最后修改时间',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=102 DEFAULT CHARSET=utf8 COMMENT='景区门票表';

