/*Table structure for table `mp_vip_level` */

DROP TABLE IF EXISTS `mp_vip_level`;

CREATE TABLE `mp_vip_level` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `vip_name` varchar(255) DEFAULT NULL COMMENT '等级名称',
  `min_integral` int(11) DEFAULT NULL COMMENT '最小积分',
  `max_integral` int(11) DEFAULT '0' COMMENT '最大积分',
  `normal_integral_rule` decimal(10,2) DEFAULT NULL COMMENT '常规日积分（例如：1元=1分）',
  `special_integral_rule` decimal(10,2) DEFAULT NULL COMMENT '特别日积分（例如：1元=2分）',
  `integral_coefficient` decimal(10,2) DEFAULT NULL COMMENT '积分系数',
  `about` varchar(1024) DEFAULT NULL COMMENT '描述',
  `icon_url` varchar(1024) DEFAULT NULL COMMENT '图标url',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=87 DEFAULT CHARSET=utf8 COMMENT='会员等级表';

/*Data for the table `mp_vip_level` */

