/* Procedure structure for procedure `autoUpdateMemberIntegral` */

/*!50003 DROP PROCEDURE IF EXISTS  `autoUpdateMemberIntegral` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` PROCEDURE `autoUpdateMemberIntegral`()
BEGIN
	 DECLARE done INT DEFAULT 1;
   DECLARE telephone varchar(200);
   DECLARE lastIntegral int DEFAULT 0;
   DECLARE currentIntegral int DEFAULT 0;
   DECLARE integral1 int DEFAULT 0;
   DECLARE nowIntegral1 int DEFAULT 0;
   declare levelId int ;
   declare vipName varchar(200) ;
   /*. 会员积分游标..*/
   DECLARE intregralCur CURSOR FOR SELECT  a.phone,IFNULL(a.last_year_integral,'0')last_year_integral,
                                           IFNULL(a.current_year_integral,'0')current_year_integral,
                                           a.integral from mp_member a where a.integral !=0;
   DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 2;
   /*开启临时表 为方便信息发送*/
    
    CREATE TEMPORARY TABLE IF NOT EXISTS tempTab
     (sid int primary key auto_increment,tet VARCHAR(100), lasterIntegralTemp int,IntegralTemp int ,vipName varchar(100));
     DELETE FROM tempTab;

   OPEN intregralCur;

   FETCH intregralCur INTO telephone,lastIntegral,currentIntegral,integral1;
   WHILE done=1 DO
    /*会员积分 */
    set nowIntegral1=integral1-lastIntegral;
    /*会员等级*/
    select  id into levelId  from mp_vip_level where nowIntegral1>=min_integral and max_integral>=nowIntegral1;
    select  vip_name into vipName  from mp_vip_level where nowIntegral1>=min_integral and max_integral>=nowIntegral1;
    /*修改会员积分和等级*/
    update mp_member mp set mp.last_year_integral=currentIntegral,mp.current_year_integral=0,mp.integral=nowIntegral1,mp.vip_level=levelId where mp.phone=telephone;
    /*兑换内容插入临时表*/
    insert into tempTab values(null,telephone ,lastIntegral,nowIntegral1,vipName);
    
   FETCH intregralCur INTO telephone,lastIntegral,currentIntegral,integral1;
    
   END WHILE;

   CLOSE intregralCur;
    select * from tempTab;   

END */$$
DELIMITER ;

